-- chunkname: @/modules/client_locale/locales/pt-BR/dynamic_events.lua

translation = {
	["Kill 20 Troll Fledgling."] = "Elimine 20 Troll Fledgling.",
	["With his eyes trained towards the stars, Zalzibar has seen red streaks in the sky... a sure omen of things to come."] = "Com seus olhos voltados para as estrelas, Zalzibar viu riscos vermelhos no c\xE9u... um claro press\xE1gio do que est\xE1 por vir.",
	["Trolls are about to attack Riverend!"] = "Os trolls est\xE3o prestes a atacar Riverend!",
	["Collect as many meteorites as possible!"] = "Colete o m\xE1ximo de meteoritos poss\xEDvel!",
	["The starving denizens of Tarnag Mountain walked down to Riverend in search for a meal!"] = "Os habitantes famintos de Tarnag Mountain desceram at\xE9 Riverend em busca de uma refei\xE7\xE3o!",
	["Bring the meteorites to Zalzibar."] = "Leve os meteoritos para Zalzibar.",
	["Leave the den before it collapses!"] = "Saia do esconderijo antes que ele desabe!",
	["Defeat Djinns or Jackals in the Riftsand Trenches."] = "Derrote Djinns ou Jackals nas Trincheiras de Riftsand.",
	["Survive the third wave of Sharks!"] = "Sobreviva \xE0 terceira ofensiva de tubar\xF5es!",
	["Attack the supplies either from the Djinns or the Jackals."] = "Ataque os suprimentos dos Djinns ou dos Jackals.",
	["Survive the second wave of Sharks!"] = "Sobreviva \xE0 segunda ofensiva de tubar\xF5es!",
	["Taking refuge in a cavern deep below Gilead, these extremist naturalists are figuring out a way to help nature reclaim the land."] = "Refugiados em uma caverna profunda abaixo de Gilead, naturalistas extremistas est\xE3o tentando encontrar uma maneira de ajudar a natureza a reconquistar seu espa\xE7o.",
	["Kill the Shark Goldenmaw!"] = "Elimine o Shark Goldenmaw!",
	["Defeat the aggressive naturalist."] = "Derrote o naturalista agressivo.",
	["Survive the wave of Sharks attacking from the water!"] = "Sobreviva \xE0 ofensiva de tubar\xF5es que atacam da \xE1gua!",
	["The brew has became unstable and creatures started crawling from the cauldron! Clear the cavern before it gets out of control."] = "A mistura se tornou inst\xE1vel e criaturas come\xE7aram a sair do caldeir\xE3o! Acabe com elas antes que a situa\xE7\xE3o saia do controle.",
	["Investigate the den"] = "Investigue o esconderijo.",
	["Tranter is preparing to transport his goods to the town of Truce, on the Northern frontier of Zephyr Vale, and is seeking protection on the route."] = "Tranter est\xE1 se preparando para transportar suas mercadorias para a cidade de Truce, na fronteira norte de Zephyr Vale, e est\xE1 procurando prote\xE7\xE3o durante a rota.",
	["Sharks found their way into the tortoises' den through a complex of underwater caves, and they must be sent away immediately!"] = "Tubar\xF5es encontraram o caminho at\xE9 o esconderijo das tartarugas atrav\xE9s de um complexo de cavernas subaqu\xE1ticas, e eles devem ser afastados imediatamente!",
	["Speak with Tranter and stay close to him while he packs up."] = "Fale com Tranter e fique perto dele enquanto ele faz as malas.",
	["Go forth and claim the spoils of battle!"] = "Avance e reivindique os esp\xF3lios da batalha!",
	["Stay close to Tranter and escort him to the stronghold of Bastion."] = "Fique perto de Tranter e o escolte at\xE9 a fortaleza de Bastion.",
	["Defend yourself against the aggressive Hoarder and his underlings!"] = "Defenda-se contra o acumulador agressivo e seus subordinados!",
	["Spiders are swarming out of the forest and crawling towards Tranter's wagon!"] = "Aranhas est\xE3o vindo da floresta em dire\xE7\xE3o \xE0 carro\xE7a de Tranter!",
	["Kaiman ambushers have appeared out of nowhere - kill them!"] = "Kaiman Ambushers apareceram do nada. Elimine-os!",
	["Spiders are coming down the trees and mountain walls, crawling towards Tranter's wagon!"] = "Aranhas est\xE3o descendo das \xE1rvores e das montanhas, se aproximando da carro\xE7a de Tranter!",
	["The kaimans jump out from their hiding spots to defend their stolen goods!"] = "Os kaimans saltam de seus esconderijos para defender seus bens roubados!",
	["From the darkness of the forest, a flock of bats come swaying against the wagon!"] = "Uma revoada de morcegos, vinda da escurid\xE3o da floresta, est\xE1 em dire\xE7\xE3o \xE0 carro\xE7a!",
	["Kill 5 Toad Enforcers, 5 Toad Praetorians, and the Toad Farseer."] = "Elimine 5 Toad Enforcers, 5 Toad Praetorians e o Toad Farseer.",
	["Hookmasks are charging against the caravan. Protect it before it's looted!"] = "Hookmasks est\xE3o avan\xE7ando em dire\xE7\xE3o \xE0 caravana. Proteja-a antes que seja saqueada!",
	["Kill 5 Toad Executioners."] = "Elimine 5 Toad Executioners.",
	["Starving bears came rallying from the forest, longing for an easy meal!"] = "Ursos famintos sa\xEDram da floresta em busca de uma refei\xE7\xE3o f\xE1cil!",
	["Kill 10 Toad Assassins."] = "Elimine 10 Toad Assassins.",
	["A swarm of bees got allured by the wagon's noise. Drive them away!"] = "Um enxame de abelhas foi atra\xEDdo pelo barulho da carro\xE7a. Afaste-as!",
	["The toads have launched a sneak attack on Dras Narda!"] = "Os sapos lan\xE7aram um ataque surpresa em Dras Narda!",
	["Spiders are crawling out of cracks on the wall and ground, attracted by the convoy!"] = "Aranhas est\xE3o saindo das rachaduras nas paredes e do ch\xE3o, atra\xEDdas pelo comboio!",
	["Too many toads have been killed above Dras Narda, and the toads have launched a sneak attack!"] = "Muitos sapos foram eliminados acima de Dras Narda, e os sapos lan\xE7aram um ataque surpresa!",
	["It's an ambush! Protect the convoy from the spiders crawling out of the shadows!"] = "\xC9 uma emboscada! Proteja a caravana do ataque das aranhas!",
	["There will be a fishing competition on the shores of Gilead Isle soon!"] = "Em breve haver\xE1 uma competi\xE7\xE3o de pesca nas margens de Gilead Island!",
	["From the depths of the woods, a howling pack of wolves rally against Tranter and his wagon!"] = "Uma alcateia uivante, vinda das profundezas da floresta, corre em dire\xE7\xE3o a Tranter e sua carro\xE7a!",
	["Fish are swarming around Gilead's southwest fishing dock. Catch as many as possible before they scatter!"] = "Os peixes est\xE3o se aglomerando em torno do cais de pesca do sudoeste de Gilead. Pegue o m\xE1ximo que puder antes que se dispersem!",
	["Aberrant Hookmask soldiers charge from the cover of the woods. Defend the caravan before it's looted!"] = "Soldados Hookmask avan\xE7am das sombras da floresta. Defenda a caravana antes que seja saqueada!",
	["The shallow waters of Gilead are teeming with fish, and some fishing enthusiasts have decided to sponsor a fishing competition."] = "As \xE1guas rasas de Gilead est\xE3o cheias de peixes, e alguns entusiastas decidiram patrocinar uma competi\xE7\xE3o de pesca.",
	["Howling and growling beasts emerge from a cave at the nearby hills and charge against Tranter!"] = "Feras uivantes e rosnantes emergem de uma caverna nas colinas pr\xF3ximas e avan\xE7am contra Tranter!",
	["Toxic clouds are coming from within the rat poison factory. Something's not right."] = "Nuvens t\xF3xicas est\xE3o saindo da f\xE1brica de veneno dos ratos. Algo n\xE3o est\xE1 certo.",
	["A pack of growling wolves surrounds the caravan and are not willing to run away."] = "Um bando de lobos rosnadores cerca a caravana e n\xE3o est\xE1 disposto a recuar. ",
	["Destroy the 4 tanks to avoid the ratoxin being deployed."] = "Destrua os 4 tanques para evitar o uso da ratoxina.",
	["Defend Tranter's wagon from the ravenous beasts!"] = "Defenda a carro\xE7a de Tranter das feras famintas!",
	["When horrifying witchcraft meets the final resting place of those undeservedly vanquished, it's the finest invitation for abominations from beyond the grave."] = "Quando a horrenda bruxaria encontra o \xFAltimo lugar de descanso daqueles derrotados injustamente, surge o convite perfeito para abomina\xE7\xF5es que emergem de al\xE9m-t\xFAmulo.",
	["A local merchant, Tranter, is interested in hiring help to safely move his trade cargo to the fortress of Defiance, in the center of Zephyr Vale."] = "Tranter, um comerciante local, est\xE1 interessado em contratar ajuda para transportar com seguran\xE7a sua carga comercial at\xE9 a fortaleza de Defiance, no centro de Zephyr Vale.",
	["From time to time, enormous quantities of poison are crafted by the rats of Crohollow Bog. If left unchecked, this could mean a massacre of the people who still stand their ground in the region."] = "De tempos em tempos, enormes quantidades de veneno s\xE3o fabricadas pelos ratos de Crowhollow Bog. Se n\xE3o forem controlados, isso poderia significar um massacre das pessoas que ainda resistem na regi\xE3o.",
	["From the nearby woods, a flock of bats come swaying against the wagon!"] = "Uma revoada de morcegos, vinda dos bosques pr\xF3ximos, est\xE1 em dire\xE7\xE3o \xE0 carro\xE7a.",
	["Defeat as many Firerock Foxes as you can before they're driven away!"] = "Elimine o maior n\xFAmero poss\xEDvel de raposas Firerock antes que fujam!",
	["Howling and growling beasts emerge from the nearby hills and charge against Tranter!"] = "Feras uivantes e rosnantes emergem de uma caverna nas colinas pr\xF3ximas e avan\xE7am contra Tranter!",
	["The foxes at the edge of The Spine are howling furiously today"] = "As raposas em Windy Canyon est\xE3o uivando furiosamente hoje.",
	["Tired of getting its defenses destroyed by raiders, Far'oro leaves his position at the camp to punish whomever raises a weapon inside djinn territory."] = "Cansado de ter suas defesas destru\xEDdas por saqueadores, Far'oro deixa sua posi\xE7\xE3o no acampamento para punir quem quer que erga uma arma dentro do territ\xF3rio dos djinns.",
	["When the Morningfoxes are weak due to constant hunting, the vile Firerock Foxes take this opportunity to gain territory."] = "Quando as Morningfoxes est\xE3o fracas devido \xE0 ca\xE7a constante, as mal\xE9ficas Firerock aproveitam a oportunidade para ganhar territ\xF3rio.",
	["A strong wind current announces Far'oro is making his way into the battlefield!"] = "Uma corrente de vento forte anuncia que Far'oro est\xE1 se aproximando do campo de batalha!",
	["Kill the Hookmask Bandits sacking the Lumber Yard."] = "Derrote os bandidos Hookmask que est\xE3o saqueando Lumber Yard.",
	["Defeat the Shieldback Croc."] = "Derrote o Shieldback Croc.",
	["Wild packs of chewacabras have appeared from the hills!"] = "Chewacabras est\xE3o surgindo das colinas!",
	["Captain Klaus arrived at the camp to collect the bandits' loot. Defeat him before he escapes with the spoils!"] = "Captain Klaus chegou ao acampamento para coletar o saque dos bandidos. Derrote-o antes que ele escape com os esp\xF3lios!",
	["Join the brawl and defeat 20 pirates!"] = "Junte-se \xE0 briga e derrote 20 piratas!",
	["Female blizzard beasts only choose the fittest and most fertile of mates, and cannot resist the smell of a potent male."] = "Bestas da nevasca f\xEAmeas escolhem apenas os parceiros mais aptos e f\xE9rteis, e n\xE3o podem resistir ao cheiro de um macho dominante.",
	["A giant brawl has broken out on the streets of Seabreeze!"] = "Uma grande briga eclodiu nas ruas de Seabreeze!",
	["The artifial pheromones have attracted a number of beasts - fight your way out!"] = "Os ferom\xF4nios artificiais atra\xEDram v\xE1rias bestas. Lute para escapar!",
	["When the residents of Seabreeze get drunk, their inner pirates come out... restore order to the streets!"] = "Quando os moradores de Seabreeze ficam b\xEAbados, acabam se comportando como piratas. Restaure a ordem nas ruas!",
	["The weird smell has also attracted some yetis... who's to judge? Better clear them out."] = "O cheiro estranho tamb\xE9m atraiu alguns yetis. \xC9 melhor elimin\xE1-los.",
	["Kill 10 Hobgoblins and 30 Hoghound Lancers."] = "Elimine 10 Hobgoblins e 30 Hoghound Lancers.",
	["What could the frolls be plotting so far from their lairs? Sticking around to find out doesn't seem like a great idea..."] = "O que os frolls poderiam estar tramando t\xE3o longe de suas tocas? Ficar por perto para descobrir n\xE3o parece ser uma \xF3tima ideia...",
	["Kill 30 Hoghound Raiders and 30 Hoghound Archers."] = "Elimine 30 Hoghound Raiders e 30 Hoghound Archers.",
	["Slay the frolls before you get killed... or worse!"] = "Acabe com os frolls antes que eles acabem com voc\xEA ou fa\xE7am algo pior!",
	["A zorian wearing coral armor has appeared on the shore. Defend yourself!"] = "Um zorian vestindo uma armadura de coral apareceu na costa. Defenda-se!",
	["Zorians have risen from the water to defend their lair! Fight for your life!"] = "Zorians emergiram da \xE1gua para defender seu covil! Lute pela sua vida!",
	["When a fungi's cut in half, a new one can be born from each part. Being continuously killed by adventurers, sometimes the Ravenguard needs help putting them down."] = "Quando um fungo \xE9 cortado ao meio, um novo pode nascer de cada parte. Sendo constantemente eliminados por aventureiros, \xE0s vezes a Ravenguard precisa de ajuda para control\xE1-los.",
	["Casualties are piling up in the Riftsand trenches, and neither side seems to gain an advantage. However, there is a way to make a difference. Join the fray and help turn the tide of the war."] = "As baixas est\xE3o se acumulando nas trincheiras de Riftsand, e nenhum dos lados parece estar em vantagem. No entanto, h\xE1 uma maneira de fazer a diferen\xE7a. Junte-se \xE0 luta e ajude a mudar o rumo da guerra.",
	["Get rid of the fungi spreading through the Undermarsh Mines."] = "Elimine os fungos que est\xE3o se espalhando por Undermash Mines.",
	["Collect meteorites and defend yourself against Ghaz Raiders!"] = "Colete meteoritos e defenda-se dos Ghaz Raiders!",
	["Lure a Pirate."] = "Atraia um Pirata.",
	["Talk to Zalzibar."] = "Fale com Zalzibar.",
	["A constant cry echoes through the graveyard. Something still hides amonsgt the shadows of the sacred lights..."] = "Um choro constante ecoa pelo cemit\xE9rio. Algo ainda se esconde nas sombras das luzes sagradas...",
	["Destroy 20 Pirate Ships."] = "Destrua 20 Navios Piratas.",
	["Return to Machak with the blue caps."] = "Volte at\xE9 Machak com os cogumelos azuis.",
	["Talk to Machak"] = "Fale com Machak.",
	["Kill the remaining Toadstool Shamans in the nearby swamps"] = "Elimine os Toadstool Shamans restantes nos p\xE2ntanos pr\xF3ximos.",
	["The coal basin at the top of the lighthouse is out. Gather oil from Zorians to re-light the basin."] = "O braseiro no topo do farol se apagou. Colete \xF3leo dos zorians para reacender a bacia.",
	["Kill Zorian Sandfish or Zorian Shorehunter to obtain Zorian Oil, and use it on the Coal Basin to re-light the fire."] = "Elimine Zorian Sandfish ou Zorian Shorehunter para obter Zorian Oil e use-o no braseiro para reacender o fogo.",
	["Open the chest"] = "Abra o ba\xFA.",
	["A gravestone glows in the fading daylight, revealing a faint inscription carved into the moss-covered stone."] = "Uma l\xE1pide brilha na luz crepuscular que se esvai, revelando uma inscri\xE7\xE3o fraca esculpida na pedra coberta de musgo.",
	["Defeat 30 Skeletons underground to descend to the next floor"] = "Elimine 30 Skeletons no subsolo para descer para o pr\xF3ximo andar.",
	["Defeat 40 Skeleton Soldiers to descend to the next floor"] = "Elimine 40 Skeleton Soldiers para descer para o pr\xF3ximo andar.",
	["Kill Rargum the Dread."] = "Derrote Rargum the Dread.",
	["As they're running out of food, spiders usually travel back to the surface and hide amidst the white blanket of snow, waiting for the right time to ambush unaware preys."] = "\xC0 medida que est\xE3o ficando sem comida, as aranhas geralmente voltam \xE0 superf\xEDcie e se escondem no meio da neve, esperando o momento certo para emboscar presas desavisadas.",
	["Spiders are crawling up from their dens and hiding amidst the snow fields, hoping to find enough victims to feed the whole colony."] = "Aranhas est\xE3o saindo de suas tocas e se escondendo no meio dos campos de neve, esperando encontrar v\xEDtimas suficientes para alimentar toda a col\xF4nia.",
	["Be careful! Starving spiders are ambushing unaware victims in the area."] = "Cuidado! Aranhas famintas est\xE3o emboscando v\xEDtimas desavisadas na \xE1rea.",
	["As the snow spiders fall dead on the snow, their queens raise from their dens to take care of the problem."] = "\xC0 medida que as aranhas perecem na neve, suas rainhas saem das tocas para lidar com o problema.",
	["An aspiring Hookmask alchemist is looking to outsource a special mission to anyone willing to accept."] = "Um alquimista aspirante da Hookmask quer terceirizar uma miss\xE3o especial para qualquer um disposto a aceitar.",
	["Speak with the suspiciously friendly man looking for Zorian Poison at the cave's entrance"] = "Converse com o homem suspeitosamente amig\xE1vel que procura por veneno de zorian na entrada da caverna.",
	["It's mating season! Explore the caves beneath Fort Mercury and loot as many Zorian Poison Concentrates as you can."] = "\xC9 \xE9poca de acasalamento! Explore as cavernas sob Fort Mercury e saqueie o m\xE1ximo de veneno de zorian que puder.",
	["The muddy waters of Crowhollow Bog are said to shelter countless oddities - from rare fish schools to mutated abominations. In some places of the world, fishing isn't a sport."] = "As \xE1guas lamacentas de Crowhollow Bog s\xE3o conhecidas por abrigar in\xFAmeras anomalias, desde cardumes de peixes raros at\xE9 abomina\xE7\xF5es mutantes. Em alguns lugares do mundo, a pesca n\xE3o \xE9 um esporte.",
	["Defend yourself against the mutated crocodile!"] = "Defenda-se contra o crocodilo mutante!",
	["The knowledge of blood magic is kept secret amongst the elder vampires, and playing with it irresponsibly may lead a soul to their darkest secrets."] = "O conhecimento da magia de sangue \xE9 mantido em segredo entre os vampiros mais antigos, e brincar com ela de forma irrespons\xE1vel pode levar uma alma aos seus segredos mais sombrios.",
	["A threatening energy flows throughout Kinslayer's Crypt. Find the origin of this power before it ceases."] = "Uma energia amea\xE7adora flui por toda Kinslayers Crypt. Encontre a origem desse poder antes que ele cesse.",
	["Lost souls desperately crawl out of the shadows on the hidden halls of Kinslayers Crypt."] = "Almas perdidas rastejam desesperadamente para fora das sombras nos corredores escondidos de Kinslayers Crypt.",
	["Amidst the vanishing spirits, an entity driven by anger and pain is manifested."] = "Em meio aos esp\xEDritos que desaparecem, uma entidade impulsionada pela raiva e pela dor se manifesta.",
	["There is only one way to meet with the Red Council - invading their lair and summoning them for battle. They always accept the challenge."] = "H\xE1 apenas uma maneira de se encontrar com o Conselho Vermelho: invadindo seu covil e convocando-os para a batalha. Eles sempre aceitam o desafio.",
	["Activate each of the six obelisks to summon the Red Count."] = "Ative cada um dos seis obeliscos para convocar o Red Count.",
	["Kill the Red Count and put an end to the Council's machinations!"] = "Derrote o Red Count e ponha fim \xE0s maquina\xE7\xF5es do Conselho!",
	["Talking animals are said to hide within the C\xE2nion Ventoso... they appear to lone hunters during certain times of the day."] = "Animais que falam s\xE3o conhecidos por se esconder em Windy Canyon... eles aparecem para ca\xE7adores solit\xE1rios durante certas horas do dia.",
	["Kill 20 Golden Cubs."] = "Elimine 20 Golden Cubs.",
	["Kill 15 Golden Kits."] = "Elimine 15 Golden Kits.",
	["Defeat the terrifying Smiling Sister!"] = "Derrote a aterrorizante Smiling Sister!",
	["Zorians emerge from the depths, attacking anything that dares come near the fishing spot."] = "Zorians emergem das profundezas, atacando qualquer coisa que ouse se aproximar do ponto de pesca.",
	["Zorians pour from the water, defending the fishing spot nearby!"] = "Zorians saltam da \xE1gua, defendendo o ponto de pesca nas proximidades!",
	["A frost zorian champion has emerged, trying to buy time so its brethren can scare away the sturgeons."] = "Um campe\xE3o zorian de gelo surgiu, tentando ganhar tempo para que seus irm\xE3os possam afugentar os esturj\xF5es.",
	["Saurians love the early hours of warm sunlight and don't appreciate when their sunbathing gets disturbed..."] = "Os saurians adoram as primeiras horas de luz solar e n\xE3o gostam quando seu banho de sol \xE9 interrompido...",
	["Clear the location of saurians. Wait, it looks like someone is trapped on the mountain..."] = "Liberte o local dos saurians. Espere, parece que algu\xE9m est\xE1 preso na montanha...",
	["Clear the location of saurians"] = "Liberte o local dos saurians.",
	["The Mutant Wolves are getting out of hand. Speak to Jurgen von Thuring to participate and play the role of the reaper. Cull the wolves and make the roads safe once more!"] = "Mutant Wolves est\xE3o ficando fora de controle. Fale com Jurgen von Thuring para participar e desempenhar o papel de ceifador. Elimine os lobos e torne as estradas seguras mais uma vez!",
	["Kill all Mutant Wolves in the area and collect their fur."] = "Elimine todos os Mutant Wolves na \xE1rea e colete suas peles.",
	["Return to Jurgen and let him know of the result."] = "Informe Jurgen sobre o resultado.",
	["Kill all Mutant Wolves and Bears in the area and collect their fur."] = "Elimine todos os Mutant Wolves na \xE1rea e colete suas peles.",
	["The tropics of Sajecho are home to many types of flowers, but be wary when choosing which ones to pick..."] = "Os tr\xF3picos de Sajecho s\xE3o o lar de muitos tipos de flores, mas tenha cuidado ao escolher quais delas colher...",
	["Survive the ambush by Poisonpetal Lillies."] = "Sobreviva \xE0 emboscada das Poisonpetal Lillies.",
	["Descend down into the Poisonpetal burrow."] = "Des\xE7a at\xE9 a toca das Poisonpetals.",
	["Defeat the Poisonpetal Overgrowth in the north burrow."] = "Detenha o crescimento de Poisonpetals na toca do norte.",
	["Find out how far the burrow goes."] = "Descubra at\xE9 onde a toca se estende.",
	["In Windy Canyon, there's a note pinned to a palm tree advertising a bone carver who's willing to pay for Warhog Tusks."] = "Em Windy Canyon, h\xE1 um bilhete pregado em uma palmeira anunciando um entalhador de ossos disposto a pagar por presas de Warhogs.",
	["Kill Warhogs and collect as many Tusks as possible before time runs out."] = "Elimine os Warhogs e colete o m\xE1ximo de presas poss\xEDvel antes que o tempo acabe.",
	["A myth to some and a reality to the ones unlucky enough to come across her, the oldest of the Crowmaidens is an unimaginably powerful hag, capable of controlling the bog as if it was one her insidious children."] = "Um mito para alguns e uma realidade para aqueles que tiveram a infelicidade de encontr\xE1-la, a mais antiga das Crowmaidens \xE9 uma bruxa inimaginavelmente poderosa, capaz de controlar o p\xE2ntano como se fosse um de seus insidiosos filhos.",
	["Bring an end to the Corvine Bride's reign of terror!"] = "Ponha um fim ao reinado de terror da Noiva Corvina!",
	["From time to time, illnesses from the mainland affect Vikkar Bay and its residents. To prevent them from spreading, Lewett needs help brewing a cure."] = "De tempos em tempos, doen\xE7as do continente afetam Vikkar Bay e seus moradores. Para evitar que se espalhem, Lewett precisa de ajuda para preparar uma cura.",
	["Wait until Lewett is finished brewing the initial solution."] = "Espere at\xE9 que Lewett termine de preparar a solu\xE7\xE3o inicial.",
	["There are mystical ice spikes growing all around Vikkar Bay. Find them and bring samples to the cauldron."] = "H\xE1 estalagmites de gelo m\xEDstico crescendo por toda Vikkar Bay. Encontre-os e leve amostras para o caldeir\xE3o.",
	["The medicine is cooling down much too fast! Gather lumber from around the village and drop it into the fire before it's too late."] = "O rem\xE9dio est\xE1 esfriando muito r\xE1pido! Colete lenha ao redor da vila e coloque-a na fogueira antes que seja tarde demais.",
	["Defeat the Orc Spellcasters and Witchdoctors to stop their ritual. "] = "Elimine os Orcs Spellcasters e Witchdoctors para impedir o ritual.",
	["After Gilbert crafted a bone-made totem, Orcs have been gathering in the underground of Orkrev to perform their traditional rituals. Better stop the ritual before we know what it is for."] = "Depois que Gilbert criou um totem feito de ossos, os orcs t\xEAm se reunido no subterr\xE2neo de Orkrev para realizar seus rituais tradicionais. \xC9 melhor interromper o ritual antes de sabermos para o que ele serve. ",
	["The corrupted drake's eggs must be purged while there's still time!"] = "Os ovos dos drakes corrompidos devem ser expurgados enquanto ainda h\xE1 tempo!",
	["The normal venomfang eggs are undefended! Time to grab some!"] = "Os ovos normais dos venomfang est\xE3o indefesos! Hora de pegar alguns!",
	["The Slithering Hills, home to a big nest of Venomfang Drakes, was one of the least corrupted landscapes of the Blotch. Something has changed, though..."] = "Slithering Hills, lar de um grande ninho de Venomfang Drakes, era uma das paisagens menos corrompidas em The Blotch. No entanto, algo mudou...",
	["Defeat 50 Ghaz Raiders."] = "Derrote 50 Ghaz Raiders.",
	["Defeat 30 Ghaz Raiders."] = "Derrote 30 Ghaz Raiders.",
	["Ghaz Raiders are attacking merchants travelling through Hadarak!"] = "Ghaz Raiders est\xE3o atacando os comerciantes que viajam por Hadarak!",
	["When the western trade routes through Hadarak are busy, Ghaz Raiders appear from the wastes to pillage and kill."] = "Quando as rotas comerciais que passam por Hadarak est\xE3o movimentadas, os Ghaz aparecem para saquear e matar.",
	["Take the supplies scattered around the hideout."] = "Pegue os suprimentos espalhados ao redor do esconderijo.",
	["The damage caused by the Winterborn Alliance is already done... might as well take what's left behind."] = "O estrago causado pela Winterborn Alliance j\xE1 est\xE1 feito... \xC9 melhor pegar o que sobrou.",
	["Kill 20 Goblin Pyros."] = "Elimine 20 Goblin Pyros.",
	["Kill 10 Hobgoblins and 10 Goblin Pyros."] = "Elimine 10 Hobgoblins e 10 Goblin Pyros.",
	["Kill 20 Hobgoblins, 20 Goblin Pyros, and one Goblin Berserker."] = "Elimine 20 Hobgoblins, 20 Goblin Pyros e 1 Goblin Berserker.",
	["Kill 15 Hobgoblins and 2 Goblin Pyros."] = "Elimine 15 Hobgoblins e 2 Goblin Pyros.",
	["Kill 10 Hobgoblins."] = "Elimine 10 Hobgoblins.",
	["A horde of Greenshriek Goblins are attacking Barnsley Hamlet!"] = "Uma horda de Greenshriek Goblins est\xE1 atacando Barnsley Hamlet!",
	["Furious at the loss of their brethren, a horde of Greenshriek Goblins has gathered to exact vengeance upon Barnsley Hamlet."] = "Furiosos pela perda de seus irm\xE3os, uma horda de Greenshriek Goblins se reuniu para se vingar de Barnsley Hamlet.",
	["Take down the barricades so you can escape!"] = "Derrube as barricadas para que voc\xEA possa escapar!",
	["Get out of the hillock by fighting through the raging Kaiman!"] = "Saia do mont\xEDculo enfrentando o Kaiman enfurecido!",
	["Kill the Kaiman Seer before he can sacrifice even more victims!"] = "Elimine o Kaiman Seer antes que ele possa sacrificar ainda mais v\xEDtimas!",
	["The Kaiman Seer has performed a twisted ritual to enhance his powers - put a stop to the madness!"] = "O Kaiman Seer realizou um ritual distorcido para aumentar seus poderes. Ponha um fim \xE0 loucura!",
	["Every night, a kaiman seer emerges from hiding to sacrifice victims captured in the jungles of Gilead."] = "Todas as noites, um Kaiman Seer emerge do esconderijo para sacrificar v\xEDtimas capturadas nas selvas de Gilead.",
	["Use the crane under Lewett's lab to lure a skeleton up from the water pit. Defeat it and bring its ground bones to the cauldron."] = "Use a grua sob o laborat\xF3rio de Lewett para atrair um esqueleto do po\xE7o de \xE1gua. Derrote-o e leve seus ossos para o caldeir\xE3o.",
	["Talk with Lewett at his lab in Vikkar Bay."] = "Converse com Lewett em seu laborat\xF3rio em Vikkar Bay.",
	["Grab an empty vial, blend a frozen lily and alcohol in it, then drop the solution into Lewett's cauldron."] = "Pegue um frasco vazio, misture um l\xEDrio congelado e \xE1lcool nele, depois coloque a solu\xE7\xE3o no caldeir\xE3o de Lewett.",
	["Lewett needs a flask of fish oil for the mixture. Find it somewhere around Vikkar Bay and drop it into the cauldron."] = "Lewett precisa de um frasco de \xF3leo de peixe para a mistura. Encontre-o em algum lugar de Vikkar Bay e coloque-o no caldeir\xE3o.",
	["Wait until Lewett finishes the brew."] = "Espere at\xE9 que Lewett termine a prepara\xE7\xE3o.",
	["The solution has a nauseating smell, attracting unnatural creatures made rabid by the repugnant scent."] = "A solu\xE7\xE3o atrai criaturas sobrenaturais enlouquecidas pelo seu odor repugnante e nauseante.",
	["Lewett's cannot control the magic within the solution! Grab a Conduit Crystal from his trunk and use it to clear Vikkar Bay of the wild energy"] = "Lewett n\xE3o consegue controlar a magia dentro da solu\xE7\xE3o! Pegue um Conduit Crystal em seu ba\xFA e use-o para limpar a energia descontrolada de Vikkar Bay.",
	["Find where Lewett keeps his stock of sparkling dust, grab some and drop it into the medicine."] = "Encontre onde Lewett guarda seu estoque de p\xF3 brilhante, pegue um pouco e coloque-o no rem\xE9dio.",
	["Chaos' spreading in Orkwatch! The orcs are vulnerable as a Warchief has yet to rise!"] = "O caos est\xE1 se espalhando em Orkwatch! Os orcs est\xE3o vulner\xE1veis, pois ainda n\xE3o h\xE1 um novo Chefe de Guerra!",
	["Orkwatch is in chaos! The orcs are fighting amongst themselves to determine a new Warchief!"] = "O caos est\xE1 se espalhando em Orkwatch! Os orcs est\xE3o lutando entre si para determinar um novo Chefe de Guerra!",
	["Beware! There have been sightings of Hookmask Bandits in the area."] = "Cuidado! Bandidos Hookmask foram avistados na \xE1rea.",
	["Kill the Hookmask Bandits stealing from the animal pen."] = "Derrote os bandidos Hookmask que est\xE3o roubando do cercado de animais.",
	["Kill the Hookmask Bandits on the road."] = "Derrote os bandidos Hookmask na estrada.",
	["Kill the Hookmask Bandits attacking the house."] = "Derrote os bandidos Hookmask que est\xE3o atacando a casa.",
	["Put down the corrupted drake known as Slitherfang!"] = "Elimine o drake corrompido conhecido como Slitherfang!",
	["Defend yourself against the swamp drakes!"] = "Defenda-se contra os drakes do p\xE2ntano!",
	["Hookmasks are gathering at Silvercliff to pay their taxes to the camp's chief. Defeat them and take the plunder for you"] = "Hookmasks est\xE3o se reunindo em Silvercliff para pagar seus impostos ao chefe do acampamento. Derrote-os e pegue o saque.",
	["Discover what lies beyond gooey wall!"] = "Descubra o que est\xE1 al\xE9m da parede pegajosa!",
	["Hookmasks are gathering at Silvercliff to pay their taxes to the camp's chief. Defeat them and take their plunder"] = "Hookmasks est\xE3o se reunindo em Silvercliff para pagar seus impostos ao chefe do acampamento. Derrote-os e pegue o saque.",
	["Deep down the muddy and forsaken Slithering Hills, a deadly and completely corrupted creature prowls in the darkness while trying to replicate itself."] = "L\xE1 no fundo da lamacenta e esquecida Slithering Hills, uma criatura mortal e completamente corrompida perambula na escurid\xE3o enquanto tenta se replicar.",
	["The captain of Silvercliff hookmask camp is a hard one to meet. However, whenever the bandits stack up enough sacks loot, he makes sure to come claim them himself."] = "O capit\xE3o do acampamento Hookmask em Silvercliff \xE9 dif\xEDcil de encontrar. No entanto, sempre que os bandidos acumulam saques suficientes, ele se certifica de vir reivindic\xE1-los pessoalmente.",
	["A warcry is uttered in the distance. The rebel leader Thinfang is rushing to the battlefield!"] = "Um grito de guerra \xE9 proferido ao longe. O l\xEDder rebelde Thinfang est\xE1 em dire\xE7\xE3o ao campo de batalha!",
	["Defeat 15 Skorn Acolytes, 20 Skorn Deathblades, and 5 Skorn Warlocks."] = "Elimine 15 Skorn Acolytes, 20 Skorn Deathblades e 5 Skorn Warlocks.",
	["Thinfang joined the fray! Defeat the jackal leader at their war camp."] = "Thinfang se juntou ao combate! Derrote o l\xEDder dos chacais em seu acampamento de guerra.",
	["Defeat 20 Skorn Warlocks and the Skorn Grandmaster."] = "Elimine 20 Skorn Warlocks e o Skorn Grandmaster.",
	["After a series of attacks to their supplies, Thinfang goes into the battlefield to defend their kind."] = "Ap\xF3s uma s\xE9rie de ataques aos seus suprimentos, Thinfang entra no campo de batalha para defender sua esp\xE9cie.",
	["Defeat 25 Skorn Acolytes and 10 Skorn Deathblades."] = "Elimine 25 Skorn Acolytes e 10 Skorn Deathblades.",
	["Kill the insane elementalist and take the ring from his cold, dead body!"] = "Derrote o elementalista insano e pegue o anel de seu corpo!",
	["Defeat 20 Skorn Acolytes."] = "Elimine 20 Skorn Acolytes.",
	["The flames are clearly melting the ice, yet the air only seems to be growing colder..."] = "As chamas claramente est\xE3o derretendo o gelo, mas o ar parece estar ficando apenas mais frio...",
	["Skorn Cultists are attacking Glademire Fort to kill and kidnap sacrificial victims!"] = "Cultistas Skorn est\xE3o atacando Glademire Fort para eliminar e sequestrar v\xEDtimas para sacrif\xEDcios!",
	["At the top of Castle Thergard, a man holding the power of the White Blight rests within his tomb of stone and ice..."] = "No topo de Thergard Castle, um homem que det\xE9m o poder da Praga Branca repousa em seu t\xFAmulo de pedra e gelo...",
	["Skorn Cultists have descended upon Glademire Fort, bringing death and destruction in the name of Pandemonium."] = "Cultistas Skorn invadiram Glademire Fort, trazendo morte e destrui\xE7\xE3o em nome de Pandemonium.",
	["Find and kill the Brotherhood Captain."] = "Encontre e derrote o Brotherhood Captain.",
	["Wait until Anette prepares herself to make her way back to the village"] = "Aguarde at\xE9 que Anette se prepare para voltar \xE0 aldeia.",
	["Clear the hideout of all Brotherhood Bandits."] = "Limpe o esconderijo de todos os bandidos da Brotherhood.",
	["Protect Anette along the way"] = "Proteja Anette durante o trajeto.",
	["Varner Barsavi's research has uncovered an old and well-hidden hunting ground, but who knows if the dungeon is still infested with spiders?"] = "A pesquisa de Varner Barsavi descobriu um antigo e bem-escondido campo de ca\xE7a, mas quem sabe se a masmorra ainda est\xE1 infestada de aranhas?",
	["Protect Anette from the monsters!"] = "Proteja Anette dos monstros!",
	["25 Mire Wurms and 20 Vile Wurms."] = "Elimine 25 Mire Wurms e 20 Vile Wurms.",
	["As time passed, many have lost their lives to the nightmarish events that took place at The Blotch: and many have stayed there. Angry spirits will always be on the prowl to feast on the souls of the good."] = "Com o tempo, muitos perderam suas vidas nos eventos horripilantes que ocorreram em The Blotch, e muitos permaneceram l\xE1. Esp\xEDritos raivosos sempre estar\xE3o \xE0 espreita para se alimentar das almas dos bons.",
	["20 Mire Wurms and 10 Vile Wurms."] = "Elimine 20 Mire Wurms e Vile Wurms.",
	["Talk to Malgrar about his mission and wait for him to get ready."] = "Converse com Malgrar sobre sua miss\xE3o e espere que ele se prepare.",
	["15 Mire Wurms."] = "Elimine 15 Mire Wurms.",
	["Use the cranes to hook up a frostrisen skeleton, lure them into a leghold trap and extract their ice using Malgrar's Device."] = "Use as gruas para fisgar um Frostrisen Skeleton. Atraia-os para uma armadilha e extraia o gelo usando o Malgar's Device.",
	["Wurms are leaving the Sugarcane Plantation and spreading towards [place name]."] = "Os vermes est\xE3o deixando a planta\xE7\xE3o de cana-de-a\xE7\xFAcar e se espalhando em dire\xE7\xE3o a [place name].",
	["After waiting for enough frostrisen to fall into his trap, Malgrar's willing to pay good coin for any brave adventurers to harvest Igneous Ice from their undead bodies."] = "Depois de esperar tempo suficiente para que os Frostrisen caiam em sua armadilha, Malgrar est\xE1 disposto a pagar uma boa quantia para aventureiros corajosos colherem gelo \xEDgneo de seus corpos reanimados.",
	["As the wurms at Sugarcane Plantation are repelled over and over, they spread through the isle's landscapes, searching for a new lair."] = "\xC0 medida que os vermes na planta\xE7\xE3o de cana-de-a\xE7\xFAcar s\xE3o repelidos repetidamente, eles se espalham pela ilha em busca de uma nova toca.",
	["Use a fishing rod to catch the rare species"] = "Use a vara de pesca para fisgar a esp\xE9cie rara.",
	["Defeat the Poisonpetal Goliath."] = "Elimine o Poisonpetal Goliath.",
	["Fishermen bold enough to venture into Crowhollow Bog speaks of a very rare fish capable of living in the swamp's muddy and corrupt waters."] = "Pescadores corajosos o suficiente para se aventurarem em Crowhollow Bog falam de um peixe muito raro capaz de viver nas \xE1guas lamacentas e corruptas do p\xE2ntano.",
	["Defeat 7 Poisonpetal Lillies and 3 Poisonpeal Lotuses."] = "Elimine 7 Poisonpetal Lillies e 3 Poisonpeal Lotuses.",
	["The lord of the tower showed his face at the top, and is now waiting for those brave enough to face him."] = "O senhor da torre se revelou no topo, e agora espera por aqueles que tiverem coragem de enfrent\xE1-lo.",
	["Driven into a frenzy by the sickly sweet scent of dead and decaying flowers, Poisonpetals have sprouted to defend their territory."] = "Levados \xE0 loucura pelo doce e inebriante aroma de flores mortas e em decomposi\xE7\xE3o, Poisonpetals brotaram para defender seu territ\xF3rio.",
	["The top of the tower is sealed away, but there is a strange necromantic force flowing throughout the area."] = "O topo da torre est\xE1 selado, mas h\xE1 uma estranha for\xE7a necrom\xE2ntica fluindo por toda a \xE1rea.",
	["Thin the number of Ghaz that appeared in the ruins of Hadarak."] = "Reduza o n\xFAmero de Ghaz que apareceram nas ru\xEDnas de Hadarak.",
	["You feel a weird tingle as a gentle breeze touches your skin, as if this tower is filled with magic."] = "Voc\xEA sente uma sensa\xE7\xE3o estranha quando uma brisa suave toca sua pele, como se esta torre estivesse repleta de magia.",
	["To trade and share the spoils of their raiding, the Ghaz gather at at an ancient city, abandoned by their mysterious previous inhabitants and forgotten by time."] = "Para negociar e compartilhar os esp\xF3lios de seus saques, os Ghaz se re\xFAnem em uma cidade antiga, abandonada por seus habitantes misteriosos anteriores e esquecida pelo tempo.",
	["It seems like the mechanisms run through the tower's entrails. Maybe they can help opening a passage to the top?"] = "Parece que os mecanismos passam pelas entranhas da torre. Talvez eles possam ajudar a abrir uma passagem para o topo?",
	["Defeat the wave of Zorian Raiders attacking Seafare Post."] = "Derrote a ofensiva de Zorian Raiders que est\xE1 atacando Seafare Post.",
	["Strange energies flow through this tower, what could have happened here?"] = "Energias estranhas fluem por esta torre. O que poderia ter acontecido aqui?",
	["Zorian Raiders are emerging from the ocean to attack Seafare Post!"] = "Zorian Raiders est\xE3o emergindo do oceano para atacar Seafare Post!",
	["Kill 10 Frenzy Trunks and 10 Ghastly Trunks."] = "Elimine 10 Frenzy Trunks e 10 Ghastly Trunks.",
	["Angered at their waters being overfished, Zorians have risen from the deep ocean to attack Seafare Post."] = "Irritados com a pesca excessiva em suas \xE1guas, os zorians emergiram do fundo do oceano para atacar Seafare Post.",
	["Haunted Trunks have appeared to drive out all intruders!"] = "Haunted Trunks apareceram para expulsar todos os intrusos!",
	["Join the slaughter! Defeat as many enemies as possible in 5 minutes."] = "Junte-se \xE0 batalha! Derrote o maior n\xFAmero poss\xEDvel de inimigos em 5 minutos.",
	["Sacred Rohna Undergrove, and Haunted Trunks have risen to drive out all intruders."] = "Rohna Undergrove e os Haunted Trunks se ergueram para expulsar todos os intrusos.",
	["All of the Rohna War Beacons are lit! A battle royale for territory is about to begin!"] = "Todos os far\xF3is de guerra de Rohna est\xE3o acesos! Uma batalha real pelo territ\xF3rio est\xE1 prestes a come\xE7ar!",
	["Exterminate the corrupted spirit, born of sadness and melancholy."] = "Extermine o esp\xEDrito corrompido, nascido da tristeza e da melancolia.",
	["Dwarves, Warhogs, and Goblins hate each other with a passion. When the war beacons are lit, they all converge to fight over territory."] = "Warhogs, an\xF5es e goblins se odeiam profundamente. Quando os far\xF3is de guerra s\xE3o acesos, todos convergem para lutar pelo territ\xF3rio.",
	["The shadows of the Tigerlilly Hills hide vicious predators... Packs of chewacabras may appear at any time to ambush travellers."] = "As sombras de Tigerlilly Hills escondem predadores cru\xE9is. Chewacabras podem aparecer a qualquer momento para emboscar viajantes.",
	["Defeat the cultists taking revenge for his fallen brethren!"] = "Derrote os cultistas que buscam vingan\xE7a por seus irm\xE3os ca\xEDdos!",
	["Find a way to light up the sacred lamps and drive the spirits away."] = "Encontre uma maneira de acender as l\xE2mpadas sagradas e afastar os esp\xEDritos.",
	["The cultists summoned demonic creatures for aid. Get rid of them before they thrive!"] = "Os cultistas invocaram criaturas demon\xEDacas para ajudar. Livre-se delas antes que se fortale\xE7am!",
	["When the sacred lamps spread around Castle Thergard are lit again, agonizing souls spawn back into the material world, trying to get rid of the suffering."] = "Quando as l\xE2mpadas sagradas espalhadas ao redor de Castle Thergard s\xE3o acesas novamente, almas em agonia retornam ao mundo material, tentando se livrar do sofrimento.",
	["Skorn cultists aren't a people to mess with... When enough of their own fall dead into the field, they come out of their den seeking for vengeance."] = "Os cultistas Skorn n\xE3o s\xE3o flor que se cheire... Quando muitos deles perdem a vida, eles saem de seus covis buscando vingan\xE7a.",
	["One of Hogula's legends speaks of a giant tortoise, capable of swallowing a grown person in a single bite..."] = "Uma das lendas de Hogula fala de uma tartaruga gigante capaz de engolir um adulto em uma \xFAnica mordida...",
	["Crush the Bandits trying to undo your work"] = "Derrote os bandidos que est\xE3o tentando atrapalhar o seu trabalho.",
	["Far'oro joined the fray! Defeat the master djinn near their war camp."] = "Far'oro se juntou \xE0 batalha! Derrote o mestre djinn perto de seu acampamento de guerra.",
	["Set fire to the Index beacon"] = "Acenda o farol de Index.",
	["Fungi spores have spread throughout Glademire Swamps and now infest the Undermarsh Mines."] = "Os esporos dos fungos se espalharam por Glademire Swamps e agora infestam Undermash Mines.",
	["Wait for Officer Hakon's orders near the Littlefinger beacon."] = "Espere pelas ordens do Officer Hakon perto do farol de Littlefinger.",
	["Tranter's looking for aid in delivering his wares to the citadel of Bastion on the Western edge of Zephyr Vale."] = "Tranter est\xE1 em busca de ajuda para entregar suas mercadorias na cidadela de Bastion, na borda oeste de Zephyr Vale.",
	["Set fire to the Ring beacon"] = "Incendeie o farol de Ring.",
	["Golden furred foxes rally from amidst the woods, hunger for Tranter's cargo."] = "Raposas de pelagem dourada se re\xFAnem no meio da floresta, famintas pela carga de Tranter.",
	["Set fire to the Thumb beacon"] = "Acenda o farol de Thumb.",
	["A party of zorians jumped out of the water and are ready to attack the cargo!"] = "Um grupo de zorians saltou da \xE1gua e est\xE1 pronto para atacar a carga!",
	["Set light to the beacon at Littlefinger"] = "Acenda a tocha no farol de Littlefinger.",
	["Zorians don't tolerate intruders near their nests, they have younglings and hard-earned spoils to protect!"] = "Zorians n\xE3o toleram intrusos perto de seus ninhos. Eles t\xEAm filhotes e despojos conquistados com dificuldade para proteger!",
	["The road between Bastion and Defiance is more dangerous than ever. Join up with the Ravenguard to restore peace and safe travel once more!"] = "A estrada entre Bastion e Defiance est\xE1 mais perigosa do que nunca. Una-se \xE0 Ravenguard para restaurar a paz e a seguran\xE7a nas viagens mais uma vez!",
	["Search the barrels for the mariner's hidden treasure."] = "Procure nos barris pelo tesouro escondido do marinheiro.",
	["Defeat the waves of haunted trunks."] = "Derrote as ofensivas de Haunted Trunks.",
	["Return and talk to the spectre once again."] = "Volte e fale com o espectro mais uma vez.",
	["Sporemine's ground became a rotten swamp for a reason. Some people might be remembered of that today..."] = "O solo de Sporemine se tornou um p\xE2ntano podre por um motivo. Algumas pessoas podem ser lembradas disso hoje...",
	["Kill the freshly landed pirates in Camp Clew and collect their gold rings!"] = "Derrote os piratas rec\xE9m-chegados em Camp Clew e colete seus an\xE9is de ouro!",
	["When travelers extract too much ores from Sporemine Swamp, haunted trunks appear in the area to remind them the reason those grounds are haunted."] = "Quando os viajantes extraem min\xE9rios demais de Sporemine Swamp, Haunted Trunks aparecem na \xE1rea para lembr\xE1-los do motivo pelo qual essas terras s\xE3o assombradas.",
	["Talk to the spectre outside the hut."] = "Fale com o espectro do lado de fora da cabana.",
	["Defeat the terrifying Ravenous Sister!"] = "Derrote a aterrorizante Ravenous Sister!",
	["Investigate the area."] = "Investigue a \xE1rea.",
	["Survive the third wave and collect as many Dire Wolf pelts as possible"] = "Sobreviva \xE0 terceira ofensiva e colete o m\xE1ximo de peles de Dire Wolf que puder.",
	["Everyone on Harbor Isle avoids the hut on Blood Mountain... as the urban legend goes, the place is haunted by an angry ghost."] = "Todos em Harbor Island evitam a cabana em Blood Mountain... conforme a lenda urbana, o lugar \xE9 assombrado por um fantasma enfurecido.",
	["Return to Declan with the furs."] = "Entregue as peles a Declan.",
	["Eliminate the final wave of warhogs and Ghaz!"] = "Elimine a \xFAltima ofensiva de Warhogs e Ghaz!",
	["Survive the second wave and collect as many Dire Wolf pelts as possible"] = "Sobreviva \xE0 segunda ofensiva e colete o m\xE1ximo de peles de Dire Wolf que puder.",
	["Eliminate the second wave of warhogs and Ghaz!"] = "Elimine a segunda ofensiva de Warhogs e Ghaz!",
	["Survive the first wave and collect as many Dire Wolf pelts as possible"] = "Sobreviva \xE0 primeira ofensiva e colete o m\xE1ximo de peles de Dire Wolf que puder.",
	["Join the fray. Eliminate all warhogs and Ghaz!"] = "Junte-se ao combate. Elimine todos os Warhogs e Ghaz!",
	["Talk to Declan"] = "Fale com Declan.",
	["Ghaz Raiders and Warhogs are about to clash in C\xE2nion Ventoso!"] = "Warhogs e Ghaz Raiders est\xE3o prestes a entrar em conflito em Windy Canyon.",
	["A fur trader passing through Margrove wants help obtaining more pelts to sell in Ravencrest."] = "Um comerciante de peles de animais que est\xE1 passando por Margrove quer ajuda para obter mais peles para vender em Ravencrest.",
	["Tensions are high in Windy Canyon - Ghaz Raiders and Warhogs are about to clash over territory!"] = "As tens\xF5es est\xE3o altas em Windy Canyon: Warhogs e Ghaz Raiders est\xE3o prestes a entrar em conflito por territ\xF3rio!",
	["Defeat the terrifying Sightless Sister!"] = "Derrote a aterrorizante Sightless Sister!",
	["Survive the attack of the Bloodthirsty Minotaurs!"] = "Sobreviva ao ataque dos minotauros sedentos por sangue!",
	["Defend yourself against the abominations!"] = "Defenda-se contra as abomina\xE7\xF5es!",
	["Minotaurs are one of the most aggressive creatures found in the land, some specimens are so hostile, they have to be isolated from the rest of their kind."] = "Os minotauros est\xE3o entre as criaturas mais agressivas encontradas na terra. Alguns s\xE3o t\xE3o hostis que precisam ser isolados dos demais de sua esp\xE9cie.",
	["Discover what lies beyond the once sealed passageway."] = "Descubra o que se esconde al\xE9m da passagem uma vez selada.",
	["Kill the Hookmask Bandits attacking Jensen's house."] = "Derrote os bandidos Hookmask que est\xE3o atacando a casa de Jensen.",
	["The most insidious among the Crowmaidens are under the direct influence of the Voice of the Bog. They are protected like the vital organs of a body, and they can't be reached without sacrifice."] = "As mais insidiosas entre as Crowmaidens est\xE3o sob a influ\xEAncia direta da Voz do P\xE2ntano. Elas s\xE3o protegidas como os \xF3rg\xE3os vitais de um corpo, e n\xE3o podem ser alcan\xE7adas sem sacrif\xEDcio.",
	["Defend Jensen's house from the attacking Hookmask Bandits."] = "Defenda a casa de Jensen dos bandidos Hookmask.",
	["Destroy the Ghost Ships drifting near the Dead Man's Shore."] = "Destrua os navios fantasmas que est\xE3o \xE0 deriva perto de Dead Man's Shore.",
	["Everyone wants a taste of Jensen's wine, especially bandits... they'd just rather not pay for it."] = "Todos querem provar o vinho de Jensen, especialmente os bandidos Hookmask... eles s\xF3 prefeririam n\xE3o pagar por ele.",
	["Some shipwrecks along Sajecho's coastline seem to be emerging from the waters again."] = "Alguns destro\xE7os de navios ao longo da costa de Sajecho parecem estar emergindo das \xE1guas novamente.",
	["For no apparent reason, bands of hookmasks bandits came to protect their lair"] = "Por raz\xE3o ainda desconhecida, grupos de bandidos Hookmasks vieram proteger seu esconderijo.",
	["On spooky nights, the wreckages of Dead Man's Shore emerge from the waters as revenant ships, searching for new souls to join their undead crew."] = "Nas noites assustadoras, destro\xE7os em Dead Man's Shore emergem das \xE1guas como navios assombrados, em busca de novas almas para se juntarem \xE0 sua tripula\xE7\xE3o morta-viva.",
	["After unlocking an intricate mechanism in the deepest parts of the Hookmask's hideout countless bandits appeared to protect their lair."] = "Ap\xF3s destravar um mecanismo intrincado nas partes mais profundas do esconderijo dos Hookmasks, in\xFAmeros bandidos apareceram para proteg\xEA-lo.",
	["A school of surgeons rose to the surface!"] = "Um cardume de esturj\xF5es emergiu!",
	["Defeat as many Shadowfoxes as possible before time runs out!"] = "Derrote o maior n\xFAmero poss\xEDvel de Shadowfoxes antes que o tempo se esgote!",
	["A school of rare sturgeons are lured to the surface by the bait. Prepare your rod and get ready to fish!"] = "Um cardume de raros esturj\xF5es foi atra\xEDdo para a superf\xEDcie! Pegue sua vara e prepare-se para pescar!",
	["The foxes' howl north of Ravencrest sounds deeper today..."] = "O uivo das raposas ao norte de Ravencrest parece mais profundo hoje...",
	["Gather coconuts from nearby palm trees, but don't let Koo Koo get eaten by tortoises!"] = "Colete cocos das palmeiras mais pr\xF3ximas, mas n\xE3o deixe Koo Koo ser devorada pelas tartarugas!",
	["When too much fox blood has been shed upon their lair, the full moon draws in with ghastly howls of Shadowfoxes."] = "Quando muito sangue de raposa foi derramado sobre sua toca, a lua cheia se aproxima com uivos fantasmag\xF3ricos de Shadowfoxes.",
	["Return to Koo Koo."] = "Volte at\xE9 Koo Koo.",
	["Take out the leader aspirants and all the warring orcs remaining inside Orkwatch!"] = "Elimine os aspirantes a l\xEDder e todos os orcs que ainda est\xE3o dentro de Orkwatch!",
	["This chicken really loves coconuts, but they're too high up... not to mention the tortoises trying to make a snack out of him."] = "Esta galinha realmente adora cocos, mas eles est\xE3o muito altos... sem falar nas tartarugas tentando tirar a barriga da mis\xE9ria.",
	["Orc Veterans joined the clash! Keep culling their numbers!"] = "Orcs veteranos se juntaram ao conflito! Continue reduzindo seus n\xFAmeros!",
	["Put down the Uncontrolled Mecha before it collapses!"] = "Elimine o Uncontrolled Mecha antes que ele entre em colapso!",
	["Join the fray! Keep fighting until all the Warring Orcs are eliminated!"] = "Junte-se \xE0 batalha! Continue lutando at\xE9 que todos orcs em conflito sejam eliminados!",
	["Goblins never worry about consequences. An uncontrolled, destructive machine on the loose in the workshop? A usual day like any other..."] = "Goblins nunca se preocupam com as consequ\xEAncias. Uma m\xE1quina descontrolada e destrutiva solta na oficina? \xC9 s\xF3 um dia como qualquer outro...",
	["Clear the cliffs of attacking Thornasaurs before time runs out!"] = "Elimine todos os Thornasaurs atacando o penhasco antes que o tempo acabe!",
	["Prepare to defend Woodhaven against the assault."] = "Prepare-se para defender Woodhaven contra o ataque.",
	["In the middle of the Crystal Fountains grows a red tree that blossoms with fruit during certain times of day, beckoning to those nearby..."] = "Em meio \xE0s fontes de cristal, cresce uma \xE1rvore vermelha que floresce com frutos em determinadas horas do dia, chamando aqueles que est\xE3o por perto...",
	["Defeat the Crowmaidens and send them back to their decrepit hollows!"] = "Derrote as Crowmaidens e as envie de volta \xE0s suas tocas decr\xE9pitas!",
	["Deal with the source of demonic power before it's too late"] = "Lide com a fonte de poder demon\xEDaco antes que seja tarde demais.",
	["Fend off the enemy hordes born from the corrupted bog!"] = "Afaste as hordas inimigas nascidas do p\xE2ntano corrompido!",
	["Find the source of power within the dungeon"] = "Encontre a fonte de poder dentro da masmorra.",
	["When a Crow Matriarch falls, the corrupted ecosystem of Crowhollow Bog reacts just like a body trying to exterminate an alien organism, sending its thralls to retaliate upon the people of Woodhaven."] = "Quando uma Crow Matriarch cai, o ecossistema corrompido de Crowhollow Bog reage como se tentasse exterminar um corpo estranho, enviando seus servos para retaliar contra o povo de Woodhaven.",
	["Find a way through the magic wall"] = "Encontre um caminho pela parede m\xE1gica.",
	["Defeat the second wave of drunken pirates!"] = "Derrote a segunda ofensiva de piratas b\xEAbados!",
	["Hidden underneath the shadows of the Fields of Despair, an enchanted rune is attracting demons and twisted cultists to its magic core."] = "Escondida sob as sombras de Fields of Despair, uma runa encantada est\xE1 atraindo dem\xF4nios e cultistas para seu n\xFAcleo m\xE1gico.",
	["Destroy 25 Pirate Ships and the Dreadknought."] = "Destrua 25 Navios Piratas e o Dreadknought.",
	["The frolls have reached the eastern gate of Deadbane Halls. Assist the dwarves before the frolls can break in!"] = "Os frolls chegaram ao port\xE3o oeste de Deadbane Halls. Ajude os an\xF5es antes que os frolls consigam entrar!",
	["Destroy 15 Pirate Ships."] = "Destrua 15 Navios Piratas.",
	["Raiding frolls aren't easily defeated. They are now pressing forward in the direction of  Deadbane Halls."] = "Frolls incursores n\xE3o s\xE3o facilmente derrotados. Agora eles est\xE3o avan\xE7ando na dire\xE7\xE3o de Deadbane Halls.",
	["Destroy 10 Pirate Ships."] = "Destrua 10 Navios Piratas.",
	["Frolls are banding together at the bottom of Peerstone Mountain, preparing an imminent raid."] = "Frolls est\xE3o se unindo no sop\xE9 de Peerstone Mountain, preparando um ataque iminente.",
	["League of Splinters fleets are gathering to take back control of Sajecho Sea!"] = "Frotas da Liga dos Estilha\xE7os est\xE3o se reunindo para retomar o controle do mar de Sajecho!",
	["Amidst the white veil of a storm, a dwarven sentinel spots the shapes of frolls preparing a raiding party at the entrance of their lair..."] = "No meio do v\xE9u branco de uma tempestade, um an\xE3o sentinela avista sombras de frolls preparando uma investida na entrada de sua toca...",
	["Too many of their ships have been sunk beneath the waves, and a League of Splinters fleet has appeared to regain control of the sea."] = "Muitas embarca\xE7\xF5es da Liga dos Estilha\xE7os foram naufragadas, e uma frota deles apareceu para retomar o controle do mar.",
	["Send the Shrouded Puppeteer back to the grave!"] = "Envie o Shrouded Puppeteer de volta \xE0 sepultura!",
	["Kill 20 Hobgoblins and 30 Goblin Gliders."] = "Elimine 20 Hobgoblins e 30 Goblin Gliders.",
	["Fend off the restless spirits deep beneath the Silent Catacombs!"] = "Afaste os esp\xEDritos inquietos nas profundezas de Silent Catacombs!",
	["Kill 40 Hobgoblins."] = "Elimine 40 Hobgoblins.",
	["Investigate what's happening deep below the old catacombs."] = "Investigue o que est\xE1 acontecendo nas profundezas das antigas catacumbas.",
	["Prepare for the goblin invasion."] = "Prepare-se para a invas\xE3o dos goblins.",
	["A strong demonic presence is coming from the magic seal. Deal with it before it's too late!"] = "Uma forte presen\xE7a demon\xEDaca est\xE1 vindo do selo m\xE1gico. Resolva isso antes que seja tarde demais!",
	["One too many goblins has been killed! Mounted raiding parties are forming in Dalgon Canyon to take revenge on nearby settlements."] = "Um n\xFAmero excessivo de goblins foi eliminado! Grupos de incurs\xE3o montados est\xE3o se formando em Dalgon Canyon para se vingar.",
	["Find the sources of demonic power within the dungeon and deal with them before it's too late."] = "Encontre as fontes de poder demon\xEDaco dentro da masmorra e lide com elas antes que seja tarde demais.",
	["Defeat 30 Brotherhood bandits."] = "Elimine 30 bandidos da Brotherhood.",
	["Deep into the bowels of the land, a scorching magic rune corrupts the land, turning rivers into lava and the ground into ashes."] = "Nas entranhas da terra, uma runa m\xE1gica abrasadora corrompe o territ\xF3rio, transformando rios em lava e o solo em cinzas.",
	["You've accidentally stumbled too close to a Rohna Brotherhood hideout!"] = "Voc\xEA acidentalmente se aproximou demais de um esconderijo da Rohna Brotherhood!",
	["Slay the corrupted spawn before they vanish."] = "Elimine a prole corrompida antes que desapare\xE7a.",
	["Strangers have gotten too close to a Rohna Brotherhood hideout, and bandits have appeared to ensure their location stays secret."] = "Estranhos se aproximaram demais de um esconderijo da Rohna Brotherhood, e bandidos surgiram para garantir que sua localiza\xE7\xE3o permane\xE7a em segredo.",
	["Slay the chainwraiths before they vanish."] = "Elimine os espectros acorrentados antes que desapare\xE7am.",
	["The Queen Spider is running away with the shard! Don't lose sight of it!"] = "A Queen Spider est\xE1 fugindo com o fragmento! N\xE3o a perca de vista!",
	["The air thickens as disturbed souls awaken from their final rest."] = "O ar fica pesado \xE0 medida que as almas perturbadas despertam de seu repouso final.",
	["A monstrosity crawled out of the shadows and now roams the hallways. Defeat the Lady of the Snow!"] = "Uma monstruosidade saiu das sombras e agora perambula pelos corredores. Derrote a Lady of the Snow!",
	["When the living insist on disturbing the dead, their resentment can grow to a point where it overtakes their whole being."] = "Quando os vivos insistem em perturbar os mortos, seu ressentimento pode crescer a ponto de dominar todo o seu ser.",
	["Defend yourself from the waves of Snow Spiders!"] = "Defenda-se das ofensivas de Snow Spiders!",
	["In the cave, a snow abomination has appeared unexpectedly... slay it!"] = "Dentro da caverna, uma abomina\xE7\xE3o da neve apareceu inesperadamente. Elimine-a!",
	["Prepare yourself! An imminent swarm of spiders is getting closer and closer..."] = "Prepare-se! Uma ninhada de aranhas est\xE1 se aproximando cada vez mais...",
	["Snow Beasts are getting slaughtered by the Yetis! Act quickly before the Yetis take the cave for themselves."] = "Bestas da neve est\xE3o sendo massacradas pelos Yetis! Aja rapidamente antes que os Yetis tomem a caverna para si.",
	["Deep in the heart of Glaceforde, a maiden as old as the White Blight still lives in despair, collecting Igneous Ice shards in an attempt to protect herself from her inevitable death."] = "No cora\xE7\xE3o de Glaceforde, uma donzela t\xE3o antiga quanto a Praga Branca ainda vive em desespero, coletando fragmentos de gelo \xEDgneo numa tentativa de se proteger de sua morte inevit\xE1vel.",
	["Repeated use of Pheromonic Essence has driven more of the yetis to invade snow beast territory, throwing the cave into chaos."] = "O uso repetido da ess\xEAncia ferom\xF4nica levou mais yetis a invadir o territ\xF3rio das bestas da neve, lan\xE7ando a caverna no caos.",
	["Survive the second wave of Goblin Gliders."] = "Sobreviva \xE0 segunda ofensiva de Goblin Gliders.",
	["Take care of the Gazers before their gaze reduces you to mere dust!"] = "Cuide dos Gazers antes que o olhar deles te reduza \xE0 poeira!",
	["Survive the first wave of Goblin Gliders."] = "Sobreviva \xE0 primeira ofensiva de Goblin Gliders.",
	["The scorching hot sands of the desert are not the only force actively drying the live of the region."] = "As areias escaldantes do deserto n\xE3o s\xE3o a \xFAnica coisa secando a vida da regi\xE3o.",
	["Goblin Gliders are gathering to attack Sajecho Village!"] = "Goblin Gliders est\xE3o se reunindo para atacar Sajecho Village!",
	["Defeat the Troll Titan."] = "Derrote o Troll Titan.",
	["The settlers of Sajecho Village don't have much, but that doesn't stop Goblin Gliders from trying to steal everything they own."] = "Os colonos de Sajecho Village n\xE3o t\xEAm muito, mas isso n\xE3o impede que os Goblin Gliders tentem roubar tudo o que eles t\xEAm.",
	["Open the Titan's treasures."] = "Abra os tesouros do tit\xE3.",
	["Kill 10 Forest Spiderlings, 20 Forest Spiders, and 10 Forest Spider Queens."] = "Elimine 10 Forest Spiderlings, 20 Forest Spiders e 10 Forest Spider Queens.",
	["At the highest peak of Tarmire Sands, there lives one of the most ferocious creatures of this whole land, but who'd be brave or fool enough to disturb him?"] = "No pico mais alto de Tarmire Sands vive uma das criaturas mais ferozes desta terra. Quem seria corajoso ou tolo o suficiente para perturb\xE1-la?",
	["Kill 15 Forest Spiders, 10 Forest Spider Queens, and the Blackarmor Queen."] = "Elimine 15 Forest Spiders, 10 Forest Spider Queens e a Blackarmor Queen.",
	["Kill 15 Bees."] = "Elimine 15 abelhas.",
	["Kill 15 Forest Spiderlings and 15 Forest Spiders."] = "Elimine 15 Forest Spiderlings e 15 Forest Spiders.",
	["You've stumbled upon a hive of Honey Wasps gathering pollen!"] = "Voc\xEA se deparou com um enxame de abelhas coletando p\xF3len!",
	["Kill 20 Forest Spiderlings."] = "Elimine 20 Forest Spiderlings.",
	["Interrupted while gathering pollen, a hive of furious Honey Wasps are swarming and attacking nearby threats."] = "Interrompidas enquanto coletavam p\xF3len, um enxame de abelhas furiosas est\xE1 atacando amea\xE7as pr\xF3ximas.",
	["A giant cluster of spiders have emerged from the tunnels!"] = "Aranhas emergiram das tocas!",
	["Survive the final Elderwood wave."] = "Sobreviva \xE0 \xFAltima ofensiva dos Elderwood.",
	["The weather is unusually warm in the Rohna Woods, and a cluster of spiders have emerged from the tunnels to feed."] = "O clima est\xE1 excepcionalmente quente em Rohna Woods, e aranhas emergiram das tocas para se alimentar.",
	["Survive the second Elderwood wave."] = "Sobreviva \xE0 segunda ofensiva dos Elderwood.",
	["Kill 20 Chewacabras."] = "Elimine 20 Chewacabras.",
	["Survive the first Elderwood wave."] = "Sobreviva \xE0 primeira ofensiva dos Elderwood.",
	["Kill 15 Chewacabras."] = "Elimine 15 Chewacabras.",
	["Elderwoods have gathered near the Elderwood Grotto to defend their home!"] = "Elderwoods se reuniram perto de Elderwood Grotto para defender sua casa!",
	["Kill 10 Chewacabras."] = "Elimine 10 Chewacabras.",
	["Speak to the local magnate, Jurgen von Thuring."] = "Fale com o magnata local, Jurgen von Thuring.",
	["Kill Toadstool Shamans in the nearby swamps"] = "Elimine Toadstool Shamans nos p\xE2ntanos pr\xF3ximos.",
	["The conditions are just right for a rare variety of mushrooms to spawn. A local shaman requires help gathering them before the weather turns."] = "As condi\xE7\xF5es est\xE3o perfeitas para uma variedade rara de cogumelos aparecer. Um xam\xE3 local precisa de ajuda para colet\xE1-los antes que o clima mude.",
	["Bring the Angel Wing Seashells back to Sallee."] = "Leve as Angel Wing Seashells de volta para Sallee.",
	["Kill sharks above ground and collect Angel Wing Seashells before time runs out."] = "Elimine os tubar\xF5es acima da superf\xEDcie e colete Angel Wing Seashells antes que o tempo se esgote.",
	["Talk to Salle."] = "Fale com Salle.",
	["The Sharpa Keys have the best seashells... if you can survive the sharks long enough to gather them."] = "Sharpa Keys t\xEAm as melhores conchas do mar, isto \xE9, se voc\xEA conseguir sobreviver aos tubar\xF5es tempo suficiente para colet\xE1-las.",
	["Put an end to the eldritch nightmare!"] = "Ponha um fim ao pesadelo arcano!",
	["Defend yourself against the abominations at the depths of the Blotch!"] = "Defenda-se contra as abomina\xE7\xF5es nas profundezas de The Blotch!",
	["Something stirs at the defiled heart of The Blotch... something insidious and unspeakably grotesque. When gazing into nothingness, one must take care not to be changed forever."] = "Algo se move no cora\xE7\xE3o profanado de The Blotch... algo insidioso e indescritivelmente grotesco. Ao olhar para o vazio, deve-se tomar cuidado para n\xE3o ser mudado para sempre.",
	["Investigate and clear the cavern."] = "Investigue e limpe a caverna.",
	["Discover what lies behind the opened door."] = "Descubra o que est\xE1 por tr\xE1s da porta aberta.",
	["An old Ghaz Raider hideout lies within these ruins... If found, are you capable of handling the secrets it holds inside?"] = "Um antigo esconderijo dos Ghaz Raiders se encontra dentro destas ru\xEDnas... Se encontrado, voc\xEA \xE9 capaz de lidar com os segredos que guarda em seu interior?",
	["Use mining skills to obtain the smelly ore."] = "Use habilidades de minera\xE7\xE3o para obter o min\xE9rio malcheiroso.",
	["When mined, the glowing purple rock in the middle of the room begins to release a strange smell."] = "Ao ser minerada, a rocha roxa brilhante no meio da sala come\xE7a a liberar um cheiro estranho.",
	["The Grim Crone is here. Defeat her!"] = "A Grim Crone est\xE1 aqui. Derrote-a!",
	["Hags and wraiths have come to feast on lost souls. Kill them all!"] = "Bruxas e espectros vieram para se alimentar das almas perdidas. Elimine todas elas!",
	["Those are no swamp lights. Defend against the lost souls!"] = "Essas n\xE3o s\xE3o luzes do p\xE2ntano. Defenda-se contra as almas perdidas!",
	["Strange lights can be seen glowing up the swamp during moonless nights. Some say it's the work of wretched hags coming out of their lairs to harvest the souls of those lost within the bog."] = "Luzes estranhas podem ser vistas brilhando no p\xE2ntano durante as noites sem luar. Alguns dizem que \xE9 obra de bruxas perversas saindo de suas tocas para colher as almas daqueles perdidos no p\xE2ntano.",
	["Kill 10 Troll Fledgling, 10 Trolls, and 10 Troll Brawlers. "] = "Elimine 10 Troll Fledgling, 10 Trolls e 10 Troll Brawlers. ",
	["Kill 15 Troll Fledgling and 15 Trolls."] = "Elimine 15 Troll Fledgling e 15 Trolls."
}
