-- chunkname: @/modules/client_locale/locales/pt-BR/npc_template_messages.lua

translation = {
	["As much as I hate saying this, if it wasn't for the Crimson Lotus this town wouldn't exist. At least they keep the peace on the streets. That is, when they're not the one causing it..."] = "Por mais que eu odeie dizer isso, se n\xE3o fosse pela Crimson Lotus, esta cidade n\xE3o existiria. Mesmo com todos os problemas que eles causam, pelo menos eles mant\xEAm a paz nas ruas.",
	["I've got more than I need, but what I want is the thrill of business."] = "Tenho mais do que preciso, mas o que quero \xE9 a emo\xE7\xE3o dos neg\xF3cios.",
	["This city was once a symbol of hope, but now, look around, it's nothing but a symbol of despair."] = "Esta cidade j\xE1 foi um s\xEDmbolo de esperan\xE7a, mas agora, olhe ao redor... n\xE3o passa de um s\xEDmbolo de desespero.",
	["I've got word of a new lair up north, across the lake."] = "Recebi not\xEDcias de um novo covil ao norte, do outro lado do lago.",
	["I don't need words. Got my traps and my fire."] = "N\xE3o preciso de palavras. Tenho minhas armadilhas e meu fogo.",
	["Such fine fabrics..."] = "Tecidos t\xE3o finos...",
	["Focus on the coming hunt, sis."] = "Concentre-se na ca\xE7a que est\xE1 por vir, irm\xE3.",
	["Maw dark, like Galien's heart!"] = "Buraco negro, como o cora\xE7\xE3o de Galien!",
	["Ale and stories, that's all I've left..."] = "Cerveja e hist\xF3rias, \xE9 tudo o que me resta...",
	["Getting turned... will it hurt?"] = "Se transformar... vai doer?",
	["Times like these are perfect for finding little treasures along the shore."] = "\xC9pocas como estas s\xE3o perfeitas para encontrar pequenos tesouros ao longo da costa.",
	["The roar of water against rock... like a thousand whispers on the wind."] = "O rugido da \xE1gua contra a rocha... como mil sussurros ao vento.",
	["Ah, how I long to be casting my line on the shore..."] = "Ah, como eu queria estar pescando na costa...",
	["Earn your place at the anvil, then come talk to me."] = "A bigorna n\xE3o \xE9 pra qualquer um. Conquiste seu lugar e depois venha conversar comigo.",
	["Ol' Gold Vein, where'd you go? Could've sworn it was right 'round this bend..."] = "Velha Veia de Ouro, onde voc\xEA foi? Podia jurar que estava bem naquela esquina...",
	["Cold steel, warmed by the forge, cooled by the winter winds."] = "A\xE7o frio, aquecido pela forja, resfriado pelos ventos de inverno.",
	["You think you can just waltz in here? Not on my watch."] = "Voc\xEA acha que pode simplesmente entrar aqui? N\xE3o \xE9 assim que funciona.",
	["We squander all our riches until our pockets poor!"] = "Esbanjamos todas as nossas riquezas at\xE9 nossos bolsos ficarem murchos!",
	["Damn cold out here... "] = "Que frio dos infernos aqui fora...",
	["Harold! Where's that lazy, good for nothing... Can't find good servants these days."] = "Harold! Onde est\xE1 aquele pregui\xE7oso imprest\xE1vel... N\xE3o \xE9 f\xE1cil encontrar bons servos hoje em dia.",
	["I hope I'll get to see the bard perform tonight. I traveled all the way from Dras Narda to hear them!"] = "Espero que eu consiga ver o bardo se apresentar hoje \xE0 noite. Vim l\xE1 de Dras Narda s\xF3 pra isso!",
	["The vale ain't what it used to be, not with these twisted critters prowling about."] = "O vale n\xE3o \xE9 mais o que costumava ser. N\xE3o com essas criaturas perturbadoras rondando por a\xED.",
	["You should take a boat tour around, the views are breathtaking."] = "Voc\xEA deveria fazer um passeio de barco por aqui, as vistas s\xE3o de tirar o f\xF4lego.",
	["I wonder if there are more journals in his lab?"] = "Ser\xE1 que tem mais di\xE1rios no laborat\xF3rio dele?",
	["If you go to the north, be careful. It's said that the ghosts of sailors still linger there."] = "Se voc\xEA seguir para o norte, tenha cuidado. Dizem que os fantasmas de marinheiros ainda vagam por l\xE1.",
	["Gilead's jungle, full of secrets and one very lost pirate."] = "A selva de Gilead \xE9 cheia de segredos e tem um pirata muito perdido.",
	["I want to see a bard battle!"] = "Quero ver uma batalha de bardos!",
	["Kjartan says I'm headstrong. I say he's afraid of progress."] = "Kjartan diz que sou teimoso, mas eu acho que ele tem medo do progresso.",
	["When the weather looks good, I always plan to set sail at dawn."] = "Quando o tempo est\xE1 bom, costumo zarpar ao amanhecer.",
	["You can run from vampires, or you can stake up and fight."] = "Voc\xEA pode fugir de vampiros ou pode empunhar estacas e lutar.",
	["Sure wish I could find a good Rum Downslider around here... not a lot of folk know how to make that dish."] = "Tomara que eu possa encontrar uma boa refei\xE7\xE3o por aqui... tem alguma sugest\xE3o?",
	["This is no place for your kin."] = "Este n\xE3o \xE9 lugar pra gente da sua laia.",
	["You can clearly distinguish an experienced pirate from a novice one."] = "\xC9 bem f\xE1cil distinguir um pirata experiente de um novato.",
	["Heard Jurgen's paying gold for mutant hides. If I had the guts, I'd be rolling in coin!"] = "Ouvi dizer que Jurgen est\xE1 pagando em ouro por peles de mutantes. Se eu tivesse coragem, estaria nadando em moedas!",
	["One day, I'd like to become a soldier. Their lives must be so exciting. Wearing cool armor and fighting bad guys to protect the town. Sign me up!"] = "Um dia eu quero ser um soldado. Vestir armaduras e lutar para proteger a cidade deve ser uma sensa\xE7\xE3o incr\xEDvel.",
	["Hear, hear! Ancient artifacts showcase at the museum!"] = "Ou\xE7am, ou\xE7am! Exposi\xE7\xE3o de artefatos antigos no museu!",
	["We may not have much, but we're trying to make the best out of what we have."] = "Podemos n\xE3o ter muito, mas tentamos fazer o melhor com o que temos.",
	["Between Bastion and Defiance lies Thurings' Meadow, if we ever lose control over the farmstead, Defiance will be totally isolated."] = "Entre Bastion e Defiance fica o terreno dos Thurings. Se algum dia perdermos o controle sobre a fazenda deles, Defiance ficar\xE1 completamente isolada.",
	["The one thing nobody can complain living out here is how quickly the washing dries! Just imagine being somewhere like Glaceforde, I bet everything freezes before it dries."] = "O bom de viver aqui \xE9 que as roupas secam rapidamente, isso ningu\xE9m pode reclamar. Imagina viver em Glaceforde? Aposto que l\xE1 tudo congela.",
	["Hey, you looking down on me? Huh? You ain't welcome here, fancy pants."] = "Ei, t\xE1 olhando o qu\xEA, hein? Sua presen\xE7a n\xE3o \xE9 bem-vinda aqui.",
	["Worst part of the job is having to guard the pass... Standing in the blistering sun as strangers come and go requires nerves of steel."] = "A pior parte do trabalho \xE9 ter que ficar sob o sol escaldante enquanto estranhos entram e saem. Haja paci\xEAncia.",
	["Lots of folk head for Johan von Thurings' or Bertha von Thurings' farmsteads for work. There's always need for more hands."] = "Muita gente vai para as fazendas de Johan von Thurings ou Bertha von Thurings em busca de trabalho. Sempre h\xE1 necessidade de mais m\xE3o de obra.",
	["Jensen produces the best wine I've ever drank! I wonder what his secret is... he must do something special."] = "O vinho do Jensen \xE9 o melhor que j\xE1 bebi! Qual ser\xE1 o segredo dele? Deve ter algum ingrediente especial...",
	["I feel like the river's drying up. The catch ain't what it used to be and it's not like we suddenly had an explosion of fishermen or anythin'."] = "Sinto que o rio est\xE1 secando. A quantidade de peixes reduziu bastante, e n\xE3o houve uma explos\xE3o de pescadores ou algo do tipo que pudesse justificar.",
	["Just the other day I saw some merchants pay the guards to kick the beggars out of the market."] = "Outro dia, vi alguns comerciantes pagando aos guardas para expulsar os mendigos do mercado.",
	["They say that some rare herbs and plants only grow on Sajecho."] = "Dizem que algumas ervas e plantas raras s\xF3 crescem em Sajecho.",
	["My grandfather used to tell me stories of how good this place once was..."] = "Meu av\xF4 costumava me contar hist\xF3rias sobre como este lugar era bom antigamente...",
	["The orca is said to protect us from harm and bring prosperity to the town."] = "Dizem que a orca nos protege e traz prosperidade para a cidade.",
	["Making your offering to the orca brings good fortune, but only if you truly believe it!"] = "Fazer sua oferenda \xE0 orca traz boa sorte, mas s\xF3 se voc\xEA realmente acreditar nisso!",
	["It's more important to be quick than well protected out here. That's the reason we use less armor than the soldiers in the North."] = "Aqui \xE9 mais importante ser r\xE1pido do que estar bem protegido. \xC9 por isso que usamos menos armadura do que os soldados no norte.",
	["You might hear strange noises coming from the north hill at night."] = "\xC9 poss\xEDvel ouvir barulhos estranhos vindo do monte ao norte \xE0 noite.",
	["Support the local market and fishermen! Our fish is fresh and tasty."] = "Apoie o mercado local e os pescadores! Nosso peixe \xE9 fresco e saboroso.",
	["In spite of everything, the dock is still the heart of the city."] = "Apesar de tudo, a doca ainda \xE9 o cora\xE7\xE3o da cidade.",
	["If you ask me, the streets of this city are getting more dangerous with every day..."] = "As ruas desta cidade est\xE3o ficando mais perigosas a cada dia...",
	["There are way too many stairs in Bastion... Why do we have to scale the whole mountain to reach the tavern!?"] = "H\xE1 muitas escadas em Bastion... Por que precisamos escalar a montanha inteira para chegar \xE0 taverna!?",
	["I heard that adventurers are coming to help us."] = "Ouvi dizer que aventureiros est\xE3o vindo nos ajudar.",
	["Talent is important, but nothing surpasses hard work. Us common soldiers train daily to keep in shape."] = "O talento ajuda, mas nada supera o trabalho duro e a dedica\xE7\xE3o. N\xF3s, soldados comuns, treinamos diariamente para nos mantermos em forma.",
	["Curse those von Thurings! I bet they're working with the bandits. How else could they keep their farmsteads?!"] = "Malditos von Thurings! Aposto que est\xE3o trabalhando com os bandidos. Se n\xE3o, como eles poderiam manter suas fazendas?!",
	["My father always said to me not to trust the sea."] = "Meu pai sempre me disse para n\xE3o confiar no mar.",
	["We're luckier than most settlements in the region, what with a steady supply of water from the river."] = "Somos mais sortudos do que a maioria da regi\xE3o, pois temos a \xE1gua do rio para nos suprir.",
	["You need to make sure to check the tides before setting out to fish."] = "Antes de sair para pescar \xE9 importante saber como est\xE1 a mar\xE9.",
	["Ugh... Humans. Soft. Disgusting."] = "Ugh... Humanos. Fracos. Moles. Nojentos.",
	["The pay in remote towns like this is always awful compared to big cities like Darzuac. Wish I could get relocated..."] = "O pagamento em cidades como esta \xE9 sempre inferior em compara\xE7\xE3o com cidades grandes como Darzuac. Queria poder ser transferido...",
	["My grandfather was a fisherman, too. He taught me everything I know."] = "Meu av\xF4 era pescador tamb\xE9m. Ele me ensinou tudo o que sei.",
	["Huh?"] = "Hein?",
	["There's something almost romantic about living as the Ghaz do, isn't there? Following your own rules and living under the sky like that. I just can't see past the raiding!"] = "Seguir as pr\xF3prias regras, e viver do jeito que vivem... Existe um certo romantismo no estilo de vida dos Ghaz, n\xE3o acha?",
	["The sea can be unpredictable, and that's what makes it so exciting."] = "O mar \xE9 sempre imprevis\xEDvel, e \xE9 isso que o torna incr\xEDvel!",
	["I'm sick of sea fish! As soon as my shift is over, I'm taking my fishing rod to one of Glademire's ponds instead!"] = "Estou cansado de pescar no mar! Assim que eu sair do batente, vou com a minha vara de pesca para um dos lagos de Glademire!",
	["Sometimes we deal with the Morningstars in Firslight. Fine folk, once you get past the zealotry."] = "De vez em quando lidamos com os Morningstars em Firslight. Apesar do fanatismo, at\xE9 que s\xE3o gente boa.",
	["Weakling."] = "Fracote.",
	["Be careful."] = "Tenha cuidado.",
	["The locals here are friendly as long as you don't disturb their way of life."] = "Desde que voc\xEA n\xE3o perturbe a vida de ningu\xE9m aqui, as pessoas s\xE3o amig\xE1veis. ",
	["I wish I was brave enough to explore all this place has to offer..."] = "Gostaria de ter coragem o suficiente para explorar tudo o que este lugar tem a oferecer...",
	["Those Morningstars are no joke. Those Paladins are just as well trained as we are..."] = "Aqueles Morningstars n\xE3o est\xE3o para brincadeira. Aqueles paladinos s\xE3o t\xE3o bem treinados quanto n\xF3s...",
	["Like most of my neighbors I travel to Bastion to sell the produce my folks grow on their farm."] = "Assim como a maioria dos meus vizinhos, viajo at\xE9 Bastion para vender os produtos que meus pais cultivam em sua fazenda.",
	["I joined the garrison to avoid begging on the streets. It doesn't pay much, but it's honest work."] = "Eu me juntei \xE0 guarni\xE7\xE3o para evitar mendigar nas ruas. N\xE3o paga muito, mas \xE9 um trabalho honesto.",
	["You're on your own here... The guards won't lift a finger to help."] = "Voc\xEA vai ter que se virar sozinho por aqui... Os guardas n\xE3o v\xE3o mover uma palha para ajudar.",
	["Good day for fishing! As any other!"] = "\xC9 um \xF3timo dia para pescar como qualquer outro!",
	["Keeping the peace doesn't seem to be the goal of the guards."] = "Manter a paz parece n\xE3o ser o objetivo dos guardas.",
	["Be careful if venture to the west sea. Pirates control a great part of the waters towards Harbor Island."] = "Tenha cuidado ao se aventurar no mar a oeste. Piratas controlam grande parte das \xE1guas em dire\xE7\xE3o a Harbor Island.",
	["I've been fishing these waters for years... The sea is my life."] = "Pesco nestas \xE1guas h\xE1 anos... O mar \xE9 minha vida.",
	["Apparently a group of bards are supposed to perform here in the tavern... dunno when, but I can't wait!"] = "Parece que um grupo de bardos vai se apresentar aqui na taverna... n\xE3o sei quando, mas mal posso esperar!",
	["Did you know there was a great shipping company at Seabreeze decades ago? If I'm not mistaken, pirates had killed the owners and claimed their headquarters for themselves."] = "Voc\xEA sabia que havia uma grande transportadora em Seabreeze h\xE1 d\xE9cadas? Se n\xE3o me engano, piratas mataram os propriet\xE1rios e tomaram a sede deles para si.",
	["Those Hookmasks brought out some sort of monster to fight us the other day. I think they called it 'Unleashed', it sends shivers down my spine."] = "Aqueles Hookmasks trouxeram algum tipo de monstro para nos enfrentar outro dia. Acho que o chamaram de \"Unleashed\". S\xF3 de lembrar me d\xE1 calafrios.",
	["I need to make a delivery to Glademire today, so I'm getting nice and tossed. I just can't stand those stinking swamps..."] = "Preciso fazer uma entrega em Glademire hoje, mas n\xE3o suporto o cheiro de brejo do caminho... Em todo caso, tenho que parecer empolgado. ",
	["In the city, the guards make sure everyone is on their best behavior... but out here I do whatever I want!"] = "Na cidade, os guardas garantem que todos se comportem bem... mas aqui fora, fa\xE7o o que quiser!",
	["Most of the folk around here simply work to support the Ravenguard. If we don't, the town will be lost."] = "A maioria das pessoas por aqui trabalha para ajudar a Ravenguard. Se n\xE3o fizermos isso, a cidade estar\xE1 perdida.",
	["There's always something new to discover in the world of fishing."] = "N\xE3o importa o quanto voc\xEA pesque, sempre haver\xE1 algo novo para descobrir.",
	["I heard a lot of nasty rumors about how soldiers and people act in Darzuac, but it can't all be true, can it?"] = "Ouvi muitos rumores desagrad\xE1veis sobre como os soldados e as pessoas se comportam em Darzuac, mas tudo isso n\xE3o deve ser verdade, certo?",
	["The soldiers arrested my neighbors claiming they're criminals, but all they did was report that their son was taken by the Crimson Lotus, it's outrageous!"] = "Os soldados prenderam meus vizinhos, alegando que s\xE3o criminosos. Tudo o que fizeram foi relatar que o filho deles foi levado pela Crimson Lotus! \xC9 um absurdo!",
	["They say that if you cross the Crimson Lotus, they'll take you deep below Darzuac and you'll never be seen again. Best to stay out of trouble, I say."] = "Dizem que quem cruza com um Crimson Lotus \xE9 levado para algum lugar pouco conhecido de Darzuac, e nunca mais \xE9 visto. Te aconselho a ficar longe de encrenca.",
	["This town has grown much since I first arrived here, and I still love it!"] = "Esta cidade cresceu muito desde que cheguei aqui pela primeira vez, e eu continuo gostando dela!",
	["I can't believe I let myself gamble the last of my silver again. What will I do until the next pay day?"] = "N\xE3o acredito que perdi meu dinheiro em apostas de novo. E agora? Como eu vou fazer at\xE9 receber o pr\xF3ximo pagamento?",
	["This city is more of a pirate haven these days than anything else."] = "Esta cidade est\xE1 mais para um ref\xFAgio de piratas nos dias de hoje do que qualquer outra coisa.",
	["Sometimes I go diving into the water from the docks. The guards say it's dangerous, but I enjoy the thrill!"] = "\xC0s vezes, dou um mergulho na \xE1gua. Os guardas dizem que \xE9 perigoso, mas eu gosto da adrenalina!",
	["I may have my disagreements with how he runs things, but Commander Rolstad always looks after the people first and foremost."] = "Posso n\xE3o concordar com a forma como ele comanda as coisas, mas o comandante sempre cuida das pessoas em primeiro lugar.",
	["They say that orcs are twice the size of an adult man... I hope that's not true and if it is then I hope it's true they don't know how to build ladders..."] = "Dizem que os orcs t\xEAm o dobro do tamanho de um homem adulto... Espero que n\xE3o seja verdade, e se for, tomara que eles n\xE3o saibam construir escadas...",
	["I've heard pirates are once again terrorizing the seas... They are even forming groups now. Dawn help us!"] = "Ouvi dizer que os piratas est\xE3o aterrorizando os mares novamente... Eles est\xE3o at\xE9 formando grupos agora. Que a Alvorada nos ajude!",
	["If you head northeast, you'll see a tower, but I don't know what lies within."] = "Se voc\xEA seguir para o nordeste, ver\xE1 uma torre, mas n\xE3o sei dizer o que h\xE1 l\xE1 dentro.",
	["Ravendawn is a marvelous land, filled with wonder! It seems like we discover something new every day."] = "Ravendawn \xE9 uma terra maravilhosa e cheia de surpresas! Parece que descobrimos algo novo todos os dias.",
	["My father always told me there are hidden secrets around waiting to be discovered."] = "Meu pai sempre me dizia que h\xE1 segredos ocultos esperando para serem descobertos.",
	["Ever heard of the Alchemist Guild? Supposedly, they only accept members through invitation..."] = "J\xE1 ouviu falar da Guilda dos Alquimistas? Parece que s\xF3 aceitam como membro quem recebe convite...",
	["I've seen all kinds of creatures out on the water."] = "J\xE1 vi todo tipo de criaturas que voc\xEA pode imaginar na \xE1gua.",
	["Wonder who dies today."] = "Quem ser\xE1 que vai morrer hoje?",
	["There's a sense of camaraderie among the fishermen in this town."] = "Existe um senso de camaradagem entre os pescadores desta cidade.",
	["The catch was good today. We'll have fresh fish at the market tomorrow."] = "A pesca foi boa hoje. Teremos peixes frescos no mercado amanh\xE3.",
	["The world outside these walls is dangerous, but I feel safe here. The Ravenguard are elite soldiers!"] = "O mundo l\xE1 fora \xE9 perigoso, mas me sinto seguro aqui. A Ravenguard \xE9 extremamente competente!",
	["Ever met Lady Shopan? Crazy broad. Lives all alone in that big ol' castle atop the mountain."] = "J\xE1 conheceu a Lady Shopan? A mulher \xE9 maluca. Vive sozinha naquele grande castelo no topo da montanha.",
	["I heard stories of fishermen that found priceless pearls in the harbor and became rich beyond belief. One day that'll be me, mark my words!"] = "Ouvi hist\xF3rias de pescadores que encontraram p\xE9rolas preciosas no porto e ficaram podres de ricos. Um dia isso vai acontecer comigo, escute o que estou dizendo!",
	["Me? Lost? Never! I'm just... explorin' new horizons, savvy..."] = "Eu? Perdido? Nunca! Estou apenas... explorando novos horizontes.",
	["When it's hot out, nothing beats a beer. When it's cold, I prefer rum instead... warms the belly nice like."] = "Quando est\xE1 calor, nada supera uma cervejinha gelada, mas no frio, prefiro rum para aquecer o corpo.",
	["The Frostlass name means trust in trade. I aim to keep it that way."] = "O nome Frostlass significa confian\xE7a no com\xE9rcio. Pretendo mant\xEA-lo assim.",
	["Good day to spill blood."] = "Hoje \xE9 um bom dia para derramar sangue.",
	["Bastion, I've should have known better than to settle here..."] = "Bastion, eu deveria ter pensado melhor antes de me estabelecer aqui...",
	["Fishing is not just a job, it's a way of life!"] = "Pescar n\xE3o \xE9 apenas um trabalho, \xE9 um estilo de vida!",
	["Do you like this city, Mircella?"] = "Voc\xEA gosta desta cidade, Mircella?",
	["The Raven's Throne is the best tavern in town, and there ain't nobody that can tell me otherwise! "] = "A taverna Raven's Throne \xE9 a melhor da cidade, e ponto final!",
	["Good day to you."] = "Tenha um bom dia.",
	["This kitchen's not for the unproven. Out with you!"] = "Esta cozinha \xE9 s\xF3 pra quem provar que merece pisar aqui! Cai fora!",
	["There's nothing like a warm meal after a long day on the water."] = "N\xE3o h\xE1 nada como uma refei\xE7\xE3o quentinha ap\xF3s um longo dia no mar.",
	["Saint Alsek's light guides all in this town."] = "A luz de Saint Alsek guia a todos nesta cidade.",
	["I'm just heading out to catch some fish. It's one of my favorite pastimes."] = "Estou indo pescar um pouco. \xC9 uma das coisas que eu mais gosto de fazer.",
	["Harbor Isle, where ships go to rest and pirates to wander."] = "Harbor Island, onde os navios descansam e os piratas vagueiam.",
	["We're always short on supplies out there. Thank the light for Helga and the farmers of Bastion."] = "Sempre estamos com falta de suprimentos por l\xE1. Agrade\xE7o \xE0 Luz por Helga e pelos agricultores de Bastion.",
	["Now... What was I looking for?"] = "O que \xE9 que eu estava procurando mesmo?",
	["I wish we could get Jurgen von Thuring to support us. A veteran soldier allied to us in Seafroth could really turn things around!"] = "Eu gostaria que consegu\xEDssemos o apoio de Jurgen von Thuring. Um soldado veterano aliado a n\xF3s em Seafroth realmente poderia mudar as coisas!",
	["Can't hunt on an empty stomach... or a frozen one."] = "N\xE3o posso ca\xE7ar com o est\xF4mago vazio... ou congelado.",
	["Thanks to the pirates, this is now a ghost town."] = "Gra\xE7as aos piratas, esta cidade agora est\xE1 deserta.",
	["Shush... Don't be scaring all 'em buggers."] = "Shhh... N\xE3o assuste os bichos.",
	["Some families have been fishing and living off the land here for generations."] = "Algumas fam\xEDlias pescam e vivem desta terra h\xE1 gera\xE7\xF5es.",
	["I guess those higher-ups know better than a simple farmer."] = "Acho que esses superiores sabem mais do que um simples fazendeiro.",
	["I don't need an excuse to go out and have a cold one with the fellows! It's the best way of spending free time!"] = "N\xE3o h\xE1 nada como relaxar e passar o tempo livre tomando uma gelada com os amigos sem ter que dar nenhuma desculpa!",
	["I should make up my mind soon... I'm not getting any younger."] = "Eu deveria tomar uma decis\xE3o logo... O tempo n\xE3o para.",
	["There ain't no better place to drink with friends after a long, hard day of work! Come, join us!"] = "N\xE3o h\xE1 lugar melhor para beber com amigos depois de um longo e cansativo dia de trabalho! Chega a\xED, vem beber com a gente!",
	["Remember that last vampire we ran into? He looked... Sad."] = "Se lembra do \xFAltimo vampiro com quem nos encontramos? Ele parecia... triste.",
	["My partner and I settled here to get away from the stench of Darzuac, but I feel like the taint of that city is spreading everywhere more and more."] = "Meu parceiro e eu nos estabelecemos aqui para escapar do mau cheiro de Darzuac, mas sinto que a influ\xEAncia daquela cidade est\xE1 se espalhando cada vez mais por todos os lugares.",
	["Prove yourself worthy first, wanderer."] = "Prove sua dignidade primeiro, viajante.",
	["The seafood market is the heart of the town."] = "O mercado de frutos do mar \xE9 o cora\xE7\xE3o da cidade.",
	["Basements, best place to avoid the King's prying eyes!"] = "Por\xF5es, o melhor lugar para evitar os olhares curiosos do Rei!",
	["Believe it or not, some pirates keep a small library in their abodes."] = "Acredite ou n\xE3o, alguns piratas t\xEAm uma pequena biblioteca em suas casas.",
	["Never trust the Hookmasks."] = "Nunca confie nos Hookmasks.",
	["Knock it off..."] = "Pare com isso...",
	["Welcome to Gilead's Museum, esteemed guest."] = "Bem-vindo ao Museu de Gilead, ilustre visitante.",
	["A lot of fish in the reef it seems!"] = "Parece que h\xE1 muitos peixes nos recifes!",
	["The blessings of Zephyr fall upon those who deserve them."] = "As b\xEAn\xE7\xE3os de Zephyr recaem sobre aqueles que as merecem.",
	["There should really be some sort of reward from bringing in proof of having slain orcs or the Ghaz."] = "Acho que deveria haver alguma recompensa para quem se livrar de orcs ou de algum Ghaz, se \xE9 que me entende.",
	["In Wolfhold, even our fabrics need to be tough."] = "Em Wolfhold, at\xE9 nossos tecidos precisam ser resistentes.",
	["The powerful and benevolent spirit of the orca is all around."] = "O esp\xEDrito poderoso e benevolente da orca est\xE1 por toda parte.",
	["This pipe's older than half me crew!"] = "Este cachimbo \xE9 mais velho que metade da minha tripula\xE7\xE3o!",
	["At times I just want to pack up and head off to Darzuac, so I could drink wine as I protect some fat merchant in their manor rather than deal with this."] = "Em vez de ter que lidar com isso, \xE0s vezes me d\xE1 vontade de fazer as malas e ir para Darzuac para poder beber vinho enquanto protejo algum rico comerciante em sua mans\xE3o.",
	["Now, where did I put that stash? This jungle all looks the same!"] = "Onde foi que eu coloquei? \xC9 tudo igual nessa selva!",
	["Sajecho is intertwined with tales of pirates and adventures on the high seas."] = "Hist\xF3rias de piratas e aventuras nos mares \xE9 o que n\xE3o falta em Sajecho!",
	["Spare a coin for this poor fella?"] = "Uma moedinha pra esse pobre coitado?",
	["Be careful when you're near the north hill; it's haunted."] = "Tenha cuidado quando estiver perto da colina ao norte; ela \xE9 assombrada.",
	["Lots of activity on the roads lately, frolls must be sleeping..."] = "As estradas est\xE3o bem movimentadas ultimamente. Os trolls devem estar dormindo...",
	["It takes much longer to return to the capital by heading out this way, but it's also safer..."] = "Leva muito mais tempo para retornar \xE0 capital seguindo este caminho, mas pelo menos \xE9 mais seguro...",
	["Cold, snow and furs, that's all we got up here!"] = "Frio, neve e peles, isso \xE9 tudo o que temos por aqui!",
	["We get all sorts here: settlers, bandits, exiles, just be sure not to cause trouble and you'll find a home."] = "Recebemos todo tipo de pessoa aqui: colonos, exilados, e at\xE9 bandidos. Contanto que voc\xEA n\xE3o cause problemas, est\xE1 tudo certo.",
	["I wanna go outside!"] = "Eu quero ir l\xE1 fora!",
	["Farewell."] = "Se cuida.",
	["Art is my heart's whisper, given color and form."] = "A arte \xE9 o sussurro do meu cora\xE7\xE3o, dada cor e forma.",
	["Us Ravenguard fear nothing! Maybe a dragon, but you'll never see one in Ravencrest. I hope not, anyways..."] = "N\xF3s da Ravenguard n\xE3o temos medo de nada! Talvez de um drag\xE3o, mas voc\xEA nunca ver\xE1 um em Ravencrest. Pelo menos eu espero que n\xE3o...",
	["Surard's shop sign? Oh, I think it got \"lost\" again..."] = "A placa da loja de Surard? Ah, acho que \"se perdeu\" de novo...",
	["I'm just getting ready to head out to sea. Wish me luck!"] = "Estou me preparando para partir mar adentro. Me deseje sorte!",
	["The sacred forge is not for the unproven."] = "A forja sagrada n\xE3o \xE9 para os que ainda n\xE3o se provaram dignos.",
	["If anyone starts to cause trouble, I'd be more worried about the farmers than the guards coming after you, ha!"] = "Se algu\xE9m come\xE7ar a causar problemas, eu ficaria mais preocupado com os fazendeiros indo atr\xE1s da pessoa do que com os guardas, ha ha!",
	["Wonder if I could make tea with these..."] = "Ser\xE1 que eu poderia fazer ch\xE1 com isso aqui...",
	["I don't mind giving up a portion of my produce to feed the soldiers that patrol the roads. I doubt I'd even have a farm if not for their protection!"] = "N\xE3o me importo em dar uma parte do que produzo para alimentar os soldados que patrulham as estradas. Se n\xE3o fosse pela prote\xE7\xE3o deles talvez eu nem tivesse uma fazenda hoje!",
	["This short rest was anything but short!"] = "Este descanso r\xE1pido foi tudo menos r\xE1pido!",
	["Commander Rolstad has held off all attacks and I know as long as he is here, Defiance will not fall."] = "O comandante tem resistido a todos os ataques, e eu sei que, enquanto ele estiver aqui, Defiance n\xE3o cair\xE1.",
	["A humble workshop today, tomorrow, a beacon of joy for all ages. Just wait and see."] = "Uma oficina humilde hoje, amanh\xE3 um farol de alegria para todas as idades. Espera s\xF3 pra ver.",
	["Say what you will about this place, but at least the officers don't nag us for indulging in a few vices the city has to offer. We work hard and deserve it!"] = "Diga o que quiser sobre este lugar, mas pelo menos os oficiais n\xE3o nos incomodam por nos entregarmos a alguns v\xEDcios que a cidade oferece. Trabalhamos duro e merecemos!",
	["Kalas knows the truth, they can't pull the wool over his eyes."] = "Kalas sabe a verdade, eles n\xE3o podem engan\xE1-lo.",
	["It's said that Lucien Ravencrest and his crew spent more than years sailing the seas... I can hardly imagine it."] = "Dizem que Lucien Ravencrest e sua tripula\xE7\xE3o passaram mais de anos navegando pelos mares... Mal consigo imaginar isso.",
	["These weaving stations are for those who know the craft, not novices."] = "Essas esta\xE7\xF5es de tecelagem s\xE3o para quem conhece o of\xEDcio, n\xE3o para iniciantes.",
	["Always remember to bring two skins, one for the water and the other for wine!"] = "Lembre-se sempre de trazer dois odres: um para a \xE1gua e outro para o vinho!",
	["Don't speak much. Words are for folk with warmth to spare."] = "N\xE3o fale muito. Palavras s\xE3o para pessoas com calor de sobra.",
	["I think the upper brass are wantin' to send us into the hills to clear out some trouble spots, but that sounds like suicide. Let the adventurers handle it, I say!"] = "Acho que os superiores est\xE3o pensando em nos enviar para as colinas para resolver alguns problemas, mas isso parece suic\xEDdio. \xC9 melhor deixar os aventureiros lidarem com isso.",
	["Hey, hands off those tools! You ain't earned the right yet."] = "Ei, tire as m\xE3os dessas ferramentas! Voc\xEA ainda n\xE3o ganhou o direito.",
	["I know merchants who prefer the sea routes... but I'm terrified of sharks and sea monsters."] = "Conhe\xE7o comerciantes que preferem as rotas mar\xEDtimas, mas tenho medo de tubar\xF5es e monstros marinhos.",
	["You haven't seen a hidden stash around here, have you?"] = "Voc\xEA n\xE3o viu um esconderijo por aqui, viu?",
	["We're a small and peaceful town, but what with all the rumors coming out of the desert, I fear that our peace will not last..."] = "Somos uma cidade pequena e pac\xEDfica, mas com todos os rumores que chegam do deserto, temo que nossa paz n\xE3o dure muito...",
	["Please, refrain from touching the exhibitions."] = "Por favor, evite tocar nas exposi\xE7\xF5es.",
	["I'm surprised at the defections we had among the alchemists in favor of the Hookmasks."] = "Estou surpreso com as deser\xE7\xF5es que tivemos entre os alquimistas a favor dos Hookmasks.",
	["They call me Old Caelum. Still beats being called \"Late Caelum\"!"] = "Eles me chamam de Velho Caelum. Antes isso do que \"velhote\" ou \"velharia\"...",
	["I always make sure to say a prayer to the orca before I set out on a journey."] = "Nunca me esque\xE7o de fazer uma ora\xE7\xE3o \xE0 orca antes de embarcar em uma jornada.",
	["No room at Orca Bay today. Best of luck finding another port, child."] = "N\xE3o h\xE1 vagas em Orca Bay hoje. Boa sorte em encontrar outro porto, jovem.",
	["It takes time and patience to build a good fishing rod."] = "Leva tempo e paci\xEAncia para fazer uma boa vara de pesca.",
	["To live eternally, young and pretty..."] = "Para viver eternamente, jovem e bonita...",
	["I wish I could spend more time at the top of the towers, you can see so far out from up there!"] = "A vista do alto das torres \xE9 incr\xEDvel! Eu adoraria passar mais tempo l\xE1.",
	["Can't believe I gotta jump through these hoops for shelter..."] = "N\xE3o acredito que tenho que passar por esses obst\xE1culos para ter abrigo...",
	["There's always something new to discover out on the water, that's why I love living by the sea."] = "As \xE1guas s\xE3o sempre cheias de mist\xE9rios, e \xE9 por isso que amo viver \xE0 beira-mar.",
	["Carrying a song in your heart, you'll never feel alone."] = "Carregando uma m\xFAsica em seu cora\xE7\xE3o, voc\xEA nunca sentir\xE1 a solid\xE3o.",
	["You've not had a real night out until you drank here in Darzuac! You've also not had a real hangover 'till then neither, ha!"] = "S\xF3 quem bebe aqui em Darzuac sabe o que \xE9 uma noite e uma ressaca de verdade!",
	["There's not enough stone in these walls to keep those creatures out."] = "N\xE3o h\xE1 pedra suficiente nessas paredes para manter essas criaturas longe.",
	["I'm thinking of moving to Ravencrest one day. It might be a dull life, but at least it's safer."] = "Estou pensando em me mudar para Ravencrest um dia. Pode ser uma vida mon\xF3tona, mas pelo menos \xE9 mais segura.",
	["If only I could get my hands on some quality ore..."] = "Se ao menos eu pudesse colocar as m\xE3os em um min\xE9rio de qualidade...",
	["How soothing is the sound of the waves crashing against the shore..."] = "\xC9 t\xE3o reconfortante ouvir o som das ondas quebrando na praia...",
	["Don't drink more than you can handle. Some funny fella may try to rob ya."] = "N\xE3o beba mais do que consegue aguentar. Algum engra\xE7adinho pode tentar te roubar.",
	["The best defence we have is this mountain. Most of the soldiers here are barely equipped militia and we're not important enough to be sent real Ravenguards."] = "A nossa melhor defesa \xE9 esta montanha. A maioria dos soldados aqui \xE9 uma mil\xEDcia mal equipada e sem import\xE2ncia suficiente para atrair a aten\xE7\xE3o dos verdadeiros Ravenguards.",
	["Ravendawn's cuisine is amazing! The variety of spices and ingredients... the possibilities are endless!"] = "A culin\xE1ria de Ravendawn \xE9 incr\xEDvel! Tem muita variedade de especiarias e ingredientes... as possibilidades s\xE3o infinitas!",
	["We're the last line of defense keeping those bandit scum inside Zephyr Vale..."] = "N\xF3s somos a \xFAltima linha de defesa mantendo aqueles bandidos malditos dentro de Zephyr Vale...",
	["When it comes to sailors, you never know what's true or not. Giant squids? Ha! That's gotta be a myth... right?"] = "Quando se trata de marinheiros, nunca se sabe o que \xE9 verdade ou n\xE3o. Lulas gigantes? Ha! Isso deve ser uma lenda... certo?",
	["Those brewers from the mainland ain't got nothin' on our Sajecho rum. It's the best around, just ask anyone!"] = "Nosso rum de Sajecho \xE9 incompar\xE1vel, pode perguntar pra qualquer um! N\xE3o h\xE1 bebida melhor por a\xED.",
	["See you later."] = "At\xE9 logo.",
	["Most of us have lost our livelihoods to this war with the Hookmasks. We just want it to end."] = "A maioria de n\xF3s perdeu nossos meios de subsist\xEAncia para essa guerra com os Hookmasks. S\xF3 queremos que isso termine.",
	["Another day, more bad news. I wonder when will it stop."] = "A cada dia que passa mais m\xE1s not\xEDcias chegam. Eu me pergunto quando isso vai acabar.",
	["The guards here are open to negotiation, if you know what I mean."] = "Os guardas aqui est\xE3o abertos a negocia\xE7\xF5es, se \xE9 que voc\xEA me entende.",
	["We value order here in Ravencrest, so no funny business alright? Keep your hands to yourself."] = "Valorizamos a ordem aqui em Ravencrest, ent\xE3o n\xE3o tente nenhuma gracinha, certo?",
	["There's plenty of ways to make some coin around Darzuac, just need to know the right people."] = "H\xE1 muitas maneiras de ganhar silver aqui em Darzuac, basta conhecer as pessoas certas.",
	["Truce has got to be the safest place in Zephyr Vale to conduct business, with the exception of Firslight."] = "Truce deve ser o lugar mais seguro em Zephyr Vale para fazer neg\xF3cios, com exce\xE7\xE3o de Firslight.",
	["I hear that the farmers around Defiance have mostly been driven out of the fields due to the clashes with the Morningstars and Bandits..."] = "Ouvi dizer que os agricultores ao redor de Defiance foram em grande parte expulsos dos campos devido aos conflitos com os Morningstars e bandidos Hookmask...",
	["The sea is part of who we are."] = "O mar faz parte de quem somos.",
	["I'm sure there are mysteries hidden deep down these waters."] = "Tenho certeza de que h\xE1 mist\xE9rios escondidos nas profundezas dessas \xE1guas.",
	["They say that those Hookmask bandits will allow any theory to be tested, but surely that can't be true..."] = "Dizem que aqueles bandidos Hookmask permitem que qualquer teoria seja testada, mas certamente isso n\xE3o pode ser verdade...",
	["I hope the missing children are found safe and sound..."] = "Espero que as crian\xE7as desaparecidas sejam encontradas em seguran\xE7a...",
	["Ugh... It's hard keeping clothes clean with all this mud."] = "Ugh... \xC9 dif\xEDcil manter as roupas limpas com toda essa lama.",
	["I hope my kids know not to go into the swamp."] = "Espero que meus filhos n\xE3o se percam e n\xE3o entrem no p\xE2ntano por engano.",
	["I can't believe that our Southern wall is still damaged. Those alchemy bombs the bandits launched at us are no joke..."] = "N\xE3o posso acreditar que nosso muro do sul ainda est\xE1 danificado. As bombas de alquimia que os bandidos lan\xE7aram realmente causaram estragos...",
	["Truce isn't as well protected as Bastion or Defiance, but everyone knows that this is neutral ground."] = "Truce n\xE3o \xE9 t\xE3o bem protegida quanto Bastion ou Defiance, mas todos sabem que este \xE9 um territ\xF3rio neutro.",
	["I try to stay out of trouble whenever I come here, but trouble usually find me, hehe..."] = "Tento evitar problemas sempre que venho aqui, mas os problemas geralmente me encontram, hehe...",
	["The sound of rushing water from our waterfall always puts me at ease, I just wish I could hear it while I sleep too!"] = "O som da nossa cachoeira \xE9 muito relaxante. Eu gostaria de poder ouvi-lo enquanto durmo tamb\xE9m!",
	["Don't tell anyone, but some of the folk here are retired bandits with all sorts of secrets to share over a drink!"] = "N\xE3o conte para ningu\xE9m, mas alguns aqui s\xE3o bandidos aposentados cheios de segredos para contar em uma mesa de taverna!",
	["I always try to bring back a few extra fish to share with my neighbors."] = "Sempre tento trazer alguns peixes a mais para poder compartilhar com meus vizinhos.",
	["If you're not a spice merchant or a soldier, your only hope is to become a servant, else it's a life of poverty or crime that awaits you."] = "Se voc\xEA n\xE3o \xE9 um comerciante de especiarias ou soldado, resta se tornar um servo para escapar da pobreza ou entrar para o submundo do crime.",
	["Sometimes I try to bargain at the town's market. I have my tricks..."] = "\xC0s vezes tento pechinchar no mercado da cidade. Tenho meus truques...",
	["Yetis are good for only one thing, making fine coats."] = "Yetis s\xF3 s\xE3o bons pra uma coisa: casacos.",
	["There are more and more reports these days about travelers going missing on the roads. Be careful out there."] = "H\xE1 cada vez mais relatos nos dias de hoje sobre viajantes desaparecidos nas estradas. Tenha cuidado por a\xED.",
	["These nightmares must be dealt with."] = "Esses pesadelos devem ser tratados.",
	["I been looking for a good mug of sand beer, but it's awfully hard to find up here..."] = "Tenho procurado por uma boa e velha caneca de cerveja, mas \xE9 dif\xEDcil de encontrar por aqui...",
	["Sometimes I wish my studies were a little less... controversial. Like studying paint drying. Or rocks."] = "\xC0s vezes, gostaria que meus estudos fossem um pouco menos... controversos. Como estudar tinta secando. Ou rochas.",
	["So many new settlers coming through the docks lately... Ravencrest is packed, but there's other lands to settle!"] = "Ultimamente t\xEAm chegado muitos novos moradores pela doca... Ravencrest est\xE1 lotada, mas h\xE1 outras terras para explorar!",
	["Mushroom stew: Good for the soul, terrible for diagnostics!"] = "Guisado de cogumelos: bom para a alma, terr\xEDvel para diagn\xF3sticos!",
	["There are two kinds of people. Those who prefer quantity, and those who prefer quality. Me? I'm both."] = "Algumas pessoas preferem quantidade e outras qualidade. Eu prefiro as duas coisas.",
	["A schism in belief can be more powerful than the mightiest storm. I've come to understand the Morningstars' tempest."] = "Um cisma na cren\xE7a pode ser mais poderoso do que a tempestade mais forte. Eu vim para entender a tempestade dos Morningstars.",
	["Good day to kill."] = "Hoje \xE9 um bom dia para derrotar nossos inimigos.",
	["I think I'm worrying too much..."] = "Acho que estou me preocupando demais...",
	["I carry the town's health in my hands, and hope in my heart."] = "Eu carrego a sa\xFAde da cidade nas m\xE3os e a esperan\xE7a no cora\xE7\xE3o.",
	["Even with my eyes open, I can still see 'em... those twisted things"] = "Mesmo com meus olhos abertos, ainda posso v\xEA-los... aquelas coisas distorcidas...",
	["Is there a storm coming? I hope all the boats make it back to port safely."] = "Uma tempestade est\xE1 vindo? Espero que todos os barcos cheguem em seguran\xE7a ao porto.",
	["Beautiful flowers can grow in the harshest of places"] = "Belas flores podem crescer nos lugares mais hostis.",
	["Seeing my son and his wife cradle their newborn, it makes all the past conflicts feel insignificant."] = "Ver meu filho e sua esposa cuidando do beb\xEA deles faz todos os conflitos passados parecerem insignificantes.",
	["There's a beauty to chemical reactions. You see chaos, I see a dance of elements."] = "H\xE1 uma beleza nas rea\xE7\xF5es qu\xEDmicas. Voc\xEA v\xEA caos, eu vejo uma dan\xE7a de elementos.",
	["Every time we round up all the scum off the streets, more just take their place. It's like an infestation. "] = "Toda vez que tiramos a esc\xF3ria das ruas, eles se proliferam. S\xE3o como pragas.",
	["I've paid my dues in blood and sweat. Time for others to carry the torch."] = "Eu paguei minhas d\xEDvidas com sangue e suor. T\xE1 na hora de passar o bast\xE3o pra outro.",
	["Ever seen a holy mackerel? I'm still looking."] = "J\xE1 viu algum peixe sagrado? Estou procurando por um.",
	["These Hookmasks... why can't the Crown just send more men to handle 'em?"] = "Esses Hookmasks... por que a Coroa n\xE3o pode simplesmente enviar mais homens para acabar com eles?",
	["Ever tried Jensen's wine? People keep telling me that it's the best around..."] = "J\xE1 experimentou o vinho do Jensen? As pessoas sempre me dizem que \xE9 o melhor por aqui...",
	["Hmm... That looks like a weak spot..."] = "Hmm... Isso parece um ponto fraco...",
	["How can that vixen act like she's bigger and better than me?!"] = "Aquela megera acha que \xE9 maior e melhor do que eu?!",
	["I'll have a few things to report back..."] = "Vou ter algumas coisas para reportar...",
	["They didn't lie about the fertile ground in Zephyr Vale, but they didn't tell me the bandit problems were this bad..."] = "Eles n\xE3o mentiram sobre a terra f\xE9rtil em Zephyr Vale, mas n\xE3o me disseram que os problemas com bandidos eram t\xE3o s\xE9rios...",
	["Mind your own business, nothing to see here."] = "Cuida da sua pr\xF3pria vida. N\xE3o tem nada pra ver aqui.",
	["All hands have to earn the right to craft here."] = "Todos precisam conquistar o direito de usar o espa\xE7o aqui.",
	["Sombershade has suffered enough already..."] = "Sombershade j\xE1 sofreu o suficiente...",
	["Mother Wylla is doing everything in her power to protect us. I trust her."] = "Mother Wylla est\xE1 fazendo tudo ao seu alcance para nos proteger. Confio nela.",
	["So we drink! We drink! And then we drink some more!"] = "Ent\xE3o bebemos, bebemos, e bebemos mais um pouco!",
	["Visit Gilead's Museum!"] = "Visite o Museu de Gilead!",
	["My forge, his loom. Both essential... I guess."] = "Minha forja, seu tear. Ambos essenciais... eu acho.",
	["Greed's a sickness; faith is the cure."] = "A gan\xE2ncia \xE9 uma doen\xE7a; a f\xE9 \xE9 a cura.",
	["The wind howls as if it didn't want me posted here."] = "O vento uiva como se n\xE3o me quisesse parado aqui.",
	["Hear the breeze? It speaks loudly today..."] = "Est\xE1 ouvindo a brisa? Ela est\xE1 falando alto hoje...",
	["Swapping a watchtower in Sajecho for one in Wolfhold... What a cool exchange."] = "Trocar uma torre de vigia em Sajecho por uma em Wolfhold... Que troca legal.",
	["Ever seen a yeti? They're in on it, too! Trust Kalas!"] = "J\xE1 viu um yeti? Eles tamb\xE9m est\xE3o envolvidos! Confie em Kalas!",
	["The town's hymns replace the echo of pickaxes."] = "Os hinos da cidade substituem o eco das picaretas.",
	["Each fish, a blessin'. Each catch, a prayer."] = "Cada peixe, uma b\xEAn\xE7\xE3o. Cada pesca, uma ora\xE7\xE3o.",
	["Why did I ever think of this formula that way!? So efficient..."] = "Por que eu pensei nessa f\xF3rmula dessa maneira!? T\xE3o eficiente...",
	["May Saint Zephyr protect our freedom now and forever."] = "Que Vossa Santidade Zephyr proteja nossa liberdade agora e sempre.",
	["I had a bad dream..."] = "Eu tive um pesadelo...",
	["Sled delivery, it's the future of Glaceforde!"] = "Entrega de tren\xF3 \xE9 o futuro de Glaceforde!",
	["Go find somewhere else to hide, I don't want company."] = "V\xE1 encontrar outro lugar para se esconder, n\xE3o quero companhia.",
	["The crossbow... An elegant weapon, for a more civilized hunt."] = "A besta \xE9 uma arma elegante para uma ca\xE7a mais civilizada.",
	["Ah, the muse whispers in these harsh winds."] = "Ah, a musa sussurra nesses ventos fortes.",
	["Sniff... Sniff..."] = "Sniff... Sniff...",
	["Ours is the first light, and it shall be the last light, too."] = "A nossa \xE9 a primeira luz e ser\xE1 a \xFAltima luz tamb\xE9m.",
	["Treat yourself to Chad's bargains!"] = "Aproveite as pechinchas de Chad!",
	["I hope one day you shall be returned to us..."] = "Espero que um dia voc\xEA nos seja devolvido...",
	["May the wind's strength carry you far..."] = "Que o vento te leve pra longe...",
	["Hold, not everyone can step into the light."] = "Calma a\xED, nem todos podem entrar na luz.",
	["Mrrh, Shark-folk stench!"] = "Mrrh, fedor de tubar\xE3o!",
	["This wine's so bland, I'll ironically drink it all."] = "Este vinho \xE9 t\xE3o sem gra\xE7a que eu ironicamente vou beber tudo.",
	["I tried heating my armor by the fire once. Sat down and almost roasted my backside!"] = "J\xE1 tentei aquecer minha armadura na fogueira uma vez. Quase queimei minhas costas!",
	["We'd never make it back home if not for you!"] = "Nunca ter\xEDamos voltado para casa se n\xE3o fosse por voc\xEA!",
	["We should visit the old man's grave after this hunt, Jean."] = "Devemos visitar o t\xFAmulo do velho ap\xF3s esta ca\xE7ada, Jean.",
	["Warm drink, cold walls. Just another day in Far Naddod."] = "Bebida quente, paredes frias. Apenas mais um dia em Far Naddod.",
	["I've memorized every name of those who fell so that we never forget."] = "Memorizei o nome de cada um dos ca\xEDdos para que a gente nunca se esque\xE7a.",
	["Thanks, hero!"] = "Obrigada, muito obrigada!",
	["Each painting a mess? No, it's called artistic chaos!"] = "Cada pintura uma bagun\xE7a? N\xE3o, isso se chama caos art\xEDstico!",
	["Thanks for rescuing us!"] = "Obrigado por nos resgatar!",
	["I smell warm blood..."] = "Sinto cheiro de sangue quente...",
	["When I grow up, I wanna be a hero like you!"] = "Quando eu crescer, quero ser como voc\xEA!",
	["Vikkar Bay's chill has a way of hardening a sailor."] = "O frio de Vikkar Bay tem o poder de endurecer um marinheiro.",
	["Just warming up and then back to work!"] = "S\xF3 estou me aquecendo e volto pro trabalho!",
	["I l-like to read by the g-glow of the aurora."] = "G-Gosto de ler sob o brilho da aurora.",
	["All are welcome to stay, as long as they show respect."] = "Todos s\xE3o bem-vindos para ficar, desde que mostrem respeito.",
	["Just make sure you've packed everything, we're about to leave."] = "Veja se embalou tudo. Estamos prestes a partir.",
	["Yeti numbers are on the rise, I can't do everything myself..."] = "O n\xFAmero de yetis est\xE1 aumentando, n\xE3o posso fazer tudo sozinho...",
	["Standing guard is even more boring than it looks..."] = "Ficar de guarda \xE9 ainda mais chato do que parece...",
	["My son, married to a Ravenguard's daughter, and they have given me a grandchild. I marvel at the wonders of life."] = "Meu filho \xE9 casado com a filha de um Ravenguard, e eles me deram um neto. A vida \xE9 mesmo uma surpresa!",
	["Return when the Morningstars trust you, not before."] = "Volte quando os Morningstars confiarem em voc\xEA, n\xE3o antes.",
	["Just one glimpse and Kjartan will see my formulas are perfect."] = "Basta um olhar e Kjartan ver\xE1 que minhas f\xF3rmulas s\xE3o perfeitas.",
	["Don't be a fool, you're not ready for what lies beneath these icy waves."] = "Deixe de tolice! Voc\xEA n\xE3o tem o que \xE9 preciso para ver o que est\xE1 sob essas ondas congeladas.",
	["You want to use my forges? Show me you're worthy first."] = "Quer usar minhas forjas? Precisa mostrar que \xE9 digno primeiro!",
	["Feel that chill? That's the wind of change, my friend."] = "Sentiu o calafrio? S\xE3o os ventos trazendo mudan\xE7a, camarada.",
	["Wonder how I'll get there..."] = "Me pergunto como vou chegar l\xE1...",
	["Only the deserving may pass."] = "Somente merecedores podem passar.",
	["Cough! Cough! These old bones sure are aching today..."] = "Cof! Cof! Esses ossos velhos est\xE3o doendo hoje...",
	["It's easy to get lost in the desert, tracks don't last for long and mirages abound."] = "\xC9 f\xE1cil se perder no deserto. Os rastros n\xE3o duram muito e tem miragens demais.",
	["The workshop is closed to strangers until they prove themselves."] = "A oficina est\xE1 fechada para estranhos at\xE9 que se provem dignos.",
	["Welcome to Ahirom's Emporium!"] = "Sejam bem-vindos ao Emp\xF3rio Ahirom!",
	["Blasted rocks, hide their gold better than a rooster hides its chicks! Where'd my granpappy's pappy leave that mine?"] = "Maldi\xE7\xE3o, escondeu o ouro melhor do que um galo esconde seus filhotes! Onde ser\xE1 que o pai do meu av\xF4 deixou aquela mina?",
	["Hopefully this place is outta the way enough..."] = "Espero que este lugar seja fora do caminho o bastante...",
	["Water deep, secrets deeper!"] = "\xC1guas profundas, segredos mais profundos!",
	["Went from stirring potions to spoiling grandchildren. Wouldn't change a thing."] = "Passei de mexer po\xE7\xF5es a mimar netos. N\xE3o mudaria nada.",
	["Ina's Potions & Lotions, cures what ails ya!"] = "Po\xE7\xF5es e Lo\xE7\xF5es de Ina, cura o que te aflige!",
	["If my knees creak louder than Bastion's gates, is it time to retire?"] = "Se meus joelhos rangerem mais alto do que os port\xF5es de Bastion, significa que \xE9 hora de me aposentar?",
	["Cassius Nix, all flash, no subtlety. But he gets the job done."] = "Cassius Nix tem um jeito de ser bem peculiar, mas ele faz o que tem que ser feito.",
	["Be careful if you're heading north, only monsters up there."] = "Tenha cuidado se estiver indo para o norte. S\xF3 h\xE1 monstros por l\xE1.",
	["Not many people are sturdy enuff to keep up wif me in a drinkin' competition... *hick*"] = "T\xE1 pra nascer algu\xE9m que consegue beber mais do que eu...",
	["Keep your wits about you. And a spare handkerchief."] = "Fique em alerta. E com um len\xE7o reserva.",
	["Wanna fight?"] = "Vai encarar?",
	["Nothing beats the solitude of an icy evening with a warm brew."] = "Nada supera a solid\xE3o de uma noite gelada com uma bebida quente.",
	["Know that winemaker, Jensen? He sure is short. Wonder if his daddy was a dwarf..."] = "Sabe aquele produtor de vinho, Jensen? Ele \xE9 bem baixinho. Ser\xE1 que o pai dele era um an\xE3o?",
	["I wish the biggest problem I had was rats in the granary, not mutant abominations."] = "Queria que meu maior problema fosse ratos no celeiro, n\xE3o abomina\xE7\xF5es mutantes.",
	["I wish I knew what was in Twilight... It's so good and if I had the secret I'd be rich beyond belief and always happy. It's like being inside a dream!"] = "Eu gostaria de saber o que tem na Twilight... \xE9 t\xE3o boa! Se eu soubesse o segredo, eu teria riqueza e felicidade. \xC9 como estar dentro de um sonho!",
	["The world's got enough soldiers and blacksmiths. What it needs is more toymakers."] = "O mundo tem soldados e ferreiros suficientes. Precisamos \xE9 de mais fabricantes de brinquedos.",
	["It was worth to travel all this way from Ravencrest just to have a chance to witness all the innovative recipes in action!"] = "Valeu a pena viajar de Ravencrest at\xE9 aqui s\xF3 para ter a chance de testemunhar todas as receitas inovadoras em a\xE7\xE3o!",
	["It's such a pity we lost your mind..."] = "\xC9 uma pena termos perdido sua mente...",
	["If you look at the sky on a clear night, you can see some of the most famous constellations. Keep an eye out for the Bronze Swan and the Unfated Lovers, they're my favorite!"] = "Se voc\xEA olhar para o c\xE9u em uma noite clara, poder\xE1 ver algumas das constela\xE7\xF5es mais famosas. A Cisne de Bronze e as Amantes Sem Destino s\xE3o as minhas favoritas!",
	["Docks are closed to outsiders. Move along and try your luck elsewhere."] = "As docas est\xE3o fechadas para forasteiros. Tente outro lugar.",
	["If you want some Twilight, you better show respect to the Crimson Lotus, even the merchants can't ignore them."] = "Se voc\xEA quiser um pouco de Twilight, \xE9 melhor respeitar os Crimson Lotus. At\xE9 os comerciantes fazem isso.",
	["I saw a three-headed monkey one time, I swear!"] = "Vi um macaco de tr\xEAs cabe\xE7as uma vez. Eu juro!",
	["I've always wanted to visit the wilds of Sajecho, but after hearing all those piracy tales I just wouldn't feel safe."] = "Sempre quis visitar as terras selvagens de Sajecho, mas agora estou sem coragem depois de ouvir todas aquelas hist\xF3rias de piratas.",
	["Can't go out at night, can't go out during day, too..."] = "N\xE3o posso sair \xE0 noite, n\xE3o posso sair durante o dia tamb\xE9m...",
	["There's nothing like the sound of the waves and the smell of the sea."] = "N\xE3o h\xE1 nada como o som das ondas e o cheiro do mar.",
	["Remember to get your tickets here!"] = "Lembre-se de pegar seus ingressos aqui!",
	["Got nothing to say to a human."] = "N\xE3o tenho nada a dizer a um humano.",
	["Cold wind, coming from the north. Storm's on the way."] = "H\xE1 um vento frio vindo do norte. Uma tempestade deve estar se aproximando.",
	["The same crappy work, with the same crappy ale and the same crappy home. I wish something different would happen around here!"] = "\xC9 sempre o mesmo trabalho ruim, com a mesma cerveja ruim e a mesma casa ruim. Queria que algo diferente acontecesse por aqui!",
	["They say I'm a fool, wasting my talent. But, there's no waste in creating happiness."] = "Dizem que sou um idiota, desperdi\xE7ando meu talento. Mas criar felicidade n\xE3o \xE9 desperd\xEDcio!",
	["Seems like a new group of bandits pops up every other day. Every one is another headache to deal with!"] = "Parece que um novo grupo de bandidos aparece a cada dois dias. Isso s\xF3 me tr\xE1s mais dor de cabe\xE7a!",
	["Welcome to Chad's Treats!"] = "Bem-vindo \xE0s Iguarias do Chad!",
	["Shh... if my spouse asks, you never saw me here..."] = "Shh... Se minha fam\xEDlia perguntar, voc\xEA nunca me viu aqui...",
	["Ain't nothing like the smell of garlic bread in the mornin'. Good for the soul, and keeps the vampires away!"] = "N\xE3o h\xE1 nada como o cheiro de p\xE3o de alho pela manh\xE3. Faz bem pra alma e afasta os vampiros!",
	["We don't only protect Ravencrest. The Ravenguard takes care of threats everywhere to keep our citizens safe!"] = "N\xE3o protegemos apenas Ravencrest. A Ravenguard cuida das amea\xE7as em todos os lugares para manter nossos cidad\xE3os seguros!",
	["Night falls, the hunt begins."] = "A noite cai, a ca\xE7a come\xE7a.",
	["Tropical Rum is like liquid gold, drives pirates crazy. Trust me, it just gets better and better with every sip."] = "O rum tropical \xE9 como ouro l\xEDquido; enlouquece os piratas. Acredite em mim, ele fica melhor a cada gole.",
	["Turn around, sailor! These docks are full. Find another harbor."] = "D\xEA meia volta! Essas docas est\xE3o lotadas. Encontre outro porto.",
	["I get that Defiance is under constant threat of attack, but do we really have to give up our harvests to support them? "] = "Entendo que Defiance est\xE1 sob constante amea\xE7a de ataque, mas ser\xE1 que realmente precisamos abrir m\xE3o de nossas colheitas para apoi\xE1-los?",
	["Banana farming is quite a-peeling for many, ha ha ha!"] = "\xC9 preciso ter paci\xEAncia para plantar frutas, porque voc\xEA vai enfrentar muitas \"fruta\xE7\xF5es\", ha ha ha!",
	["Zephyr Vale is full of opportunity, but the best spots are in the hands of the von Thurings. I don't think it's fair for one family to own so much."] = "Zephyr Vale est\xE1 cheia de oportunidades, mas as melhores est\xE3o nas m\xE3os dos von Thurings. N\xE3o acho justo uma \xFAnica fam\xEDlia possuir tanto.",
	["Sajecho, land of surprises and gold. Mostly surprises, I'd say..."] = "Sajecho, terra de surpresas e ouro. Principalmente surpresas, eu diria...",
	["I know it isn't the prettiest place in Ravendawn, but the slums is all we've got and we're proud of it!"] = "Eu sei que n\xE3o \xE9 o lugar mais bonito em Ravendawn, mas \xE9 tudo o que temos e estamos orgulhosos disso!",
	["Hehehe, not for your eyes, not for your hands! Secret workshop, secret things!"] = "Hehehe, n\xE3o para seus olhos, n\xE3o para suas m\xE3os! Oficina secreta, coisas secretas!",
	["I'm off-duty right now, go bother someone else."] = "Estou de folga agora, v\xE1 incomodar outra pessoa.",
	["Ah, my knees are hurting today..."] = "Ah, meus joelhos est\xE3o doendo hoje...",
	["You ever seen one of those Hookmask Ragers in the field? They fight as though possessed by demons!"] = "Voc\xEA j\xE1 viu um dos Hookmask Ragers por a\xED? Eles lutam como se estivessem possu\xEDdos por dem\xF4nios!",
	["Could've sworn it was around here somewhere. Or was it there?"] = "Poderia jurar que estava por aqui em algum lugar. Ou ser\xE1 que era l\xE1?",
	["Funny that we get more trouble from the swamp up North than the bandits right next door."] = "Engra\xE7ado que temos mais problemas com o p\xE2ntano ao norte do que com os bandidos bem ao lado.",
	["Don't speak to me."] = "N\xE3o fale comigo.",
	["I can sense the smell of a good fish being cooked from afar."] = "Posso sentir de longe o cheiro de um bom peixe sendo cozido.",
	["It used to be you could travel from one end of Zephyr Vale to the other with your silver on display and not a man or woman would trouble you, but now..."] = "Antes era poss\xEDvel viajar de um lado a outro de Zephyr Vale com seus pertences \xE0 mostra sem correr riscos, mas agora...",
	["Someday I'm going to earn a buncha silver and buy myself a nice tenement inside the city walls..."] = "Um dia, vou ganhar um monte de silver e comprar um bom sobrado dentro das muralhas da cidade...",
	["I hate that my bedroom window is facing the guard courtyard. Captain Haroldsson has quite the set of lungs on him when he starts yelling. It disturbs my potions!"] = "Odeio ter a janela do quarto de frente para o p\xE1tio dos guardas. Captain Haroldsson tem muita for\xE7a nos pulm\xF5es. Quando ele come\xE7a a gritar, at\xE9 as minhas po\xE7\xF5es come\xE7am a mexer!",
	["Have you heard of the cult that worships demons? Do you believe the rumors? Who would be THAT crazy?"] = "Voc\xEA j\xE1 ouviu falar do culto que faz adora\xE7\xE3o a dem\xF4nios? Voc\xEA acredita nos rumores? Quem seria louco o suficiente?",
	["Rumors say that the guards here are easily swayed by a little bit of silver. Just rumors, of course..."] = "Dizem que um pouco de silver j\xE1 \xE9 suficiente para os guardas aqui, se \xE9 que me entende... Mas s\xE3o apenas boatos, \xE9 claro...",
	["You'll hear complaints about how much the von Thurings own, but they pay well and always have work to offer..."] = "Voc\xEA vai ouvir gente reclamando dos von Thurings, mas eles pagam bem e sempre t\xEAm trabalho para oferecer...",
	["Silverleaf for headaches, bitterroot for stomach pains."] = "Folha prateada para dores de cabe\xE7a, raiz amarga para dores de est\xF4mago.",
	["A good fisherman gives his boat a good once-over before setting sails."] = "Um bom pescador d\xE1 uma boa olhada em seu barco antes de zarpar.",
	["Sit and rest for a while, share a story or two..."] = "Sente-se e descanse um pouco, se quiser pode at\xE9 contar algumas hist\xF3rias...",
	["My friend swears that there is a treasure hidden under the rocks in the south. I think he has gone deranged."] = "Meu amigo jura que h\xE1 um tesouro escondido sob as rochas no sul. Eu acho que ele enlouqueceu.",
	["These forges are for skilled hands only, not beginners."] = "Estas forjas s\xE3o apenas para m\xE3os habilidosas, n\xE3o para iniciantes.",
	["Someday I hope to get out there and travel around Ravendawn just like all those adventurers. I certainly don't want to live my whole life in one place. "] = "Um dia, espero sair por a\xED e viajar por Ravendawn, assim como todos esses aventureiros. Certamente n\xE3o quero passar toda a minha vida no mesmo lugar.",
	["So inspired right now..."] = "Estou inspirado agora...",
	["I don't know what's going on with the weather lately. It's been so unpredictable."] = "N\xE3o sei o que est\xE1 acontecendo com o clima ultimamente. Est\xE1 t\xE3o imprevis\xEDvel...",
	["Zephyr's light isn't for the untested."] = "A luz de Zephyr n\xE3o \xE9 para os n\xE3o testados.",
	["Bastion is the gateway into the Zephyr Vale, which means I get first pick at the highest quality regents that are destined for the rest of the continent!"] = "Bastion \xE9 a entrada para Zephyr Vale, ent\xE3o tenho prefer\xEAncia na escolha dos materiais de melhor qualidade que ser\xE3o enviados para o restante do continente!",
	["The Ebony Marble, a ship unlike any other! Lost her, I did."] = "O M\xE1rmore de \xC9bano \xE9 um navio diferente de qualquer outro! Eu o perdi, sim, perdi...",
	["I used to beg in the city... I got a few silver pieces, but it wasn't worth the bruises the guards gave me."] = "Eu costumava mendigar na cidade... Consegui algumas moedas, mas n\xE3o valeu a pena trocar moedas por hematomas. Os guardas n\xE3o aliviam.",
	["You know what's tougher than blacksmithing? Hemming trousers."] = "Voc\xEA sabe o que \xE9 mais dif\xEDcil do que forjar armas? Fazer bainha de cal\xE7as.",
	["Although the local Ghaz may cause some problems, we have high walls and a mountain to defend ourselves. Let them come, I say!"] = "Embora os Ghaz da regi\xE3o possam causar alguns problemas, temos muralhas altas e uma montanha para nos defender. Que venham tentar a sorte!",
	["I'm tired of them all, the vamps, the slayers, they're all the same..."] = "Estou cansado de todos eles. Os vampiros e os ca\xE7adores s\xE3o todos iguais...",
	["I practice every single day, even when its my day off. Never know when an attack may come."] = "Eu me preparo todos os dias, mesmo em dias de folga. Nunca se sabe quando um ataque pode acontecer.",
	["If you're looking for information on the tower in the northeast, I'm afraid I can't help you much."] = "Se voc\xEA est\xE1 procurando informa\xE7\xF5es sobre a torre no nordeste, receio n\xE3o poder ajudar muito.",
	["Some villagers bury bottles of liquor in the riverbank to keep 'em cool. Smart, but what if someone else finds them?"] = "Algumas pessoas enterram garrafas de bebida nas margens do rio para mant\xEA-las frescas. \xC9 uma boa ideia, mas e se algu\xE9m encontr\xE1-las?",
	["I work on the farms, but I'm just a lowly farmhand... I don't earn enough to live within the city walls."] = "Trabalho nas fazendas, mas sou apenas um simples ajudante... N\xE3o ganho o suficiente para viver dentro da cidade.",
	["My kids love to see the boats setting sails."] = "Meus filhos adoram ver os barcos zarpando.",
	["I say we torch the whole swamp! Can't go wrong by burning it all!"] = "Acho que devemos incendiar o p\xE2ntano inteiro! N\xE3o tem como dar errado se a gente colocar fogo em tudo!",
	["I've been having those weird dreams again, it's exhausting..."] = "Aqueles sonhos estranhos que eu tenho tido est\xE3o me deixando exausto.",
	["Love is an oasis amidst the desert..."] = "O amor \xE9 um o\xE1sis no deserto...",
	["If you want to stay safe, you best keep inside the walls and don't listen to malicious rumors about the city guard."] = "Para sua seguran\xE7a, \xE9 melhor ficar dentro das muralhas e n\xE3o dar ouvidos a boatos maliciosos sobre a guarda da cidade.",
	["It's hard to plant vegetables when the rats keep digging it up as soon as something starts growing."] = "\xC9 dif\xEDcil plantar aqui, os ratos destroem tudo que come\xE7a a crescer...",
	["It feels like every time we subdue an attack on one front, there's another somewhere else. We need more soldiers."] = "Parece que toda vez que enfrentamos um ataque em uma frente, h\xE1 outro em algum outro lugar. Precisamos de mais soldados.",
	["Ah, I should retire from the merchant's life, I'm growing old."] = "Ah, eu deveria me aposentar da vida de comerciante. Estou ficando velho.",
	["Go away."] = "V\xE1 embora.",
	["Welcome to Gilead's Museum!"] = "Bem-vindo ao Museu de Gilead!",
	["The seas shall give us a good show today!"] = "Os mares nos dar\xE3o um bom espet\xE1culo hoje!",
	["This is not quite the academic setting I'm used to, but I won't refuse a good drink..."] = "Este n\xE3o \xE9 exatamente o ambiente acad\xEAmico com o qual estou acostumado, mas n\xE3o vou recusar uma boa bebida...",
	["Unlike most places we don't mind people carrying arms, what with the Ghaz around and all."] = "Com todos esses Ghaz por a\xED e tudo mais, aqui n\xE3o nos importamos em ver pessoas carregando armas, ao contr\xE1rio da maioria dos lugares.",
	["Those high brass might as well rob me themselves with these price regulations!"] = "Aqueles altos escal\xF5es tamb\xE9m podem me roubar com esses regulamentos de pre\xE7os!",
	["What's your problem?"] = "Qual \xE9 o seu problema?",
	["That last shipment was worth a small fortune!"] = "Esse \xFAltimo envio valia uma pequena fortuna!",
	["Look at them. I bet these merry drunks don't even know about my collection of moa feathers."] = "Olhe para eles. Aposto que esses b\xEAbados alegres nem sabem que eu tenho uma cole\xE7\xE3o de penas de Moa.",
	["Dead or alive? I say it depends on how much they squirm!"] = "Morto ou vivo? Eu digo que depende de quanto eles se contorcem!",
	["The weather here is sometimes unpredictable..."] = "O clima aqui \xE0s vezes \xE9 imprevis\xEDvel...",
	["Ah, the wonders of Kabbar..."] = "Ah, as maravilhas de Kabbar...",
	["A lot of traders are coming to Ravencrest these days. I keep my eyes wide open for any suspicious activity."] = "Muitos comerciantes est\xE3o vindo para Ravencrest hoje em dia. Fico de olho em qualquer atividade que me pare\xE7a suspeita.",
	["Ever heard a pirate snore? It's a sea shanty of its own!"] = "J\xE1 ouviu um pirata roncar? \xC9 uma can\xE7\xE3o de marinheiro \xFAnica!",
	["When the water levels are low, it's much harder to catch anything."] = "Quando o n\xEDvel da \xE1gua est\xE1 baixo, fica muito mais dif\xEDcil pescar alguma coisa.",
	["The howls from the north... they chill me more than any wind."] = "Os uivos do norte... eles me arrepiam mais do que qualquer vento.",
	["These filthy pirates took everything from us."] = "Esses malditos piratas levaram tudo que a gente tinha.",
	["Sunsets here paint the sky in hues even I can't match."] = "O p\xF4r-do-sol aqui pinta o c\xE9u em tons que nem eu consigo igualar.",
	["I heard the Ravenguard captured one of the Adepts from the Hookmasks. Oh, the secrets that his mind must possess! I wish I could get my hands on them."] = "Ouvi dizer que a Ravenguard capturou um dos adeptos dos Hookmasks. Imagina os podres que ele deve saber! Estou curioso.",
	["Those crypts... More than ice lurks there."] = "Essas criptas... Mais do que gelo espreita l\xE1.",
	["We've been seeing use of Twilight appear in Dras Narda recently. It's quite worrying to imagine criminal gangs showing up here too."] = "Recentemente, temos visto o uso de Twilight aparecer em Dras Narda. \xC9 bastante preocupante imaginar gangues criminosas aparecendo aqui tamb\xE9m.",
	["Hmm... My ink seems to have frozen."] = "Hmm... Minha tinta parece ter congelado.",
	["Call me simple, but I hate sweet sugary drinks. Nothing tops a cold ale for me!"] = "Pode me chamar do que quiser, mas eu odeio bebidas doces e a\xE7ucaradas. N\xE3o h\xE1 nada como uma cerveja bem gelada!",
	["They call it the Deadlands for a reason. Wish I'd knew better..."] = "Eles chamam de Deadlands por uma raz\xE3o. Gostaria de saber mais...",
	["I hate patrolling at night, but the one upside is how beautiful the sky looks this deep and high up in the desert."] = "Eu odeio patrulhar \xE0 noite, mas n\xE3o posso negar que ver as estrelas no c\xE9u daqui do deserto \xE9 uma sensa\xE7\xE3o \xFAnica.",
	["No room here, friend! You'll have to anchor elsewhere."] = "N\xE3o h\xE1 espa\xE7o aqui, colega! Vai ter que ancorar em outro lugar.",
	["Safe travels!"] = "Boa viagem!",
	["We brawl! We fight! We're calling it a war!"] = "N\xF3s brigamos! N\xF3s lutamos! Estamos chamando de guerra!",
	["Others underestimate the threat we are facing here. The Ghaz, the orcs, and dangerous animals. Don't even get me started on water..."] = "Algumas pessoas subestimam as amea\xE7as que enfrentamos aqui: Os Ghaz, os orcs e animais perigosos. E a \xE1gua ent\xE3o nem se fala...",
	["First time I tested a sled, I ended up in a snowdrift!"] = "A primeira vez que testei um tren\xF3 acabei num monte de neve!",
	["I don't like complaining, but being assigned to caravan duty is the most worst... I heard we lost another group to an orc attack last week."] = "N\xE3o gosto de reclamar, mas ser designado para acompanhar a caravana \xE9 o fim da picada... Ouvi dizer que perdemos outro grupo para um ataque de orcs na semana passada.",
	["My looms are not for the untrained. Come back when you've proven yourself."] = "Meus teares n\xE3o s\xE3o para inexperientes. Volte quando tiver provado seu valor.",
	["Some merchants do the work for profit, but me? I just wanted to see the world. The silver is just a bonus."] = "Alguns comerciantes pensam em lucro, mas eu s\xF3 queria viajar pelo mundo. Silver \xE9 apenas consequ\xEAncia.",
	["These abominations ain't natural. They're getting bolder, too, coming closer to town."] = "Essas abomina\xE7\xF5es n\xE3o s\xE3o naturais. Est\xE3o ficando mais ousadas tamb\xE9m, chegando cada vez mais perto da cidade.",
	["There's something roting deep under this swamp... I can feel it in my bones."] = "H\xE1 algo apodrecendo sob este p\xE2ntano... Eu consigo sentir nos meus ossos.",
	["Sorry, dear, the docks are full. You'll have to seek harbor elsewhere."] = "Desculpe, meu bem, as docas est\xE3o cheias. Voc\xEA ter\xE1 que procurar o porto em outro lugar.",
	["I bet we'll see a mermaid one of these days"] = "Aposto que vamos ver uma sereia qualquer dia desses.",
	["What's a hunter without proper clothes? A popsicle."] = "Como se chama um ca\xE7ador sem roupas adequadas? Picol\xE9.",
	["See you around."] = "A gente se v\xEA por a\xED!",
	["You wouldn't happen to have a compass, would you? Mine's a bit... off."] = "Voc\xEA n\xE3o teria uma b\xFAssola, teria? A minha t\xE1 meio... doida.",
	["Docks are reserved, sailor. Try again another time."] = "As docas est\xE3o reservadas. Tente outra hora.",
	["Go away! Don't say hello!"] = "Nada de \"oi\"! Cai fora! ",
	["The Inn's not just a building, it's Seabreeze's heart."] = "A pousada n\xE3o \xE9 apenas um edif\xEDcio, \xE9 o cora\xE7\xE3o de Seabreeze.",
	["Fate... It's winds blowing us all into place."] = "Destino... S\xE3o os ventos nos soprando...",
	["Need to get that last shipment out before it goes bad."] = "Preciso enviar aquela \xFAltima remessa antes que estrague.",
	["Nails, hinges and locks. Not much more to forge today."] = "Pregos, dobradi\xE7as e fechaduras. Nada mais que isso pra forjar hoje.",
	["From my window, I watch for the town's well-being."] = "Da minha janela, vejo como a cidade est\xE1 bem.",
	["Working the fields is already hard enough without having to worry about bandits."] = "Trabalhar no campo j\xE1 \xE9 dif\xEDcil sem ter que se preocupar com bandidos.",
	["Nothing warms you up like a shot of Frostfire Whisky!"] = "Nada esquenta mais que uma dose de Frostfire Whisky!",
	["They think they're immortal, until they meet me."] = "Eles pensam que s\xE3o imortais, at\xE9 me encontrarem...",
	["This ain't a playground. Move along, friend."] = "Aqui n\xE3o \xE9 um parquinho. Continue andando, chapa.",
	["These anvils are for those who have walked our path."] = "Estas bigornas s\xE3o para aqueles que seguiram nosso caminho.",
	["I may not venture out, but I know more than those who do."] = "Posso n\xE3o me aventurar, mas sei mais do que quem se aventura.",
	["Only those touched by Zephyr may use these tools."] = "Somente aqueles tocados por Zephyr podem usar essas ferramentas.",
	["The mold in that wall is quite annoying, don't listen to it!"] = "O mofo naquela parede incomoda muito. N\xE3o d\xEA ouvidos a ele!",
	["I just need to lie low for a while..."] = "S\xF3 preciso ficar na surdina por um tempo...",
	["Lots of noise coming from the tavern, guess they're having a good time."] = "Que barulh\xE3o vindo da taverna. Acho que est\xE3o se divertindo.",
	["Interesting..."] = "Interessante...",
	["You not have what it takes! No peeking, no sneaking. Shoo, shoo!"] = "Voc\xEA n\xE3o tem o que \xE9 preciso! Sem espiar ou se esgueirar. X\xF4! X\xF4!",
	["Prove yourself first, then we'll talk about using the workstations."] = "Primeiro, prove seu valor. Depois a gente conversa sobre usar as esta\xE7\xF5es de trabalho.",
	["Half the tales I tell are true. Guess which."] = "Metade das hist\xF3rias que conto s\xE3o verdadeiras. Adivinha quais.",
	["Wreckage? Plunder? I'm merely searching for my lost love!"] = "Naufr\xE1gio? Pilhagem? S\xF3 queria encontrar o meu amor perdido!",
	["My sources are of the highest quality, only the best ingredients!"] = "Minhas fontes s\xE3o da mais alta qualidade, apenas os melhores ingredientes!",
	["These prices are a steal!"] = "Esses pre\xE7os s\xE3o uma pechincha!",
	["Shh! They'll hear us, savvy. Or is that just me they hear?"] = "Shh! Eles v\xE3o nos ouvir, \xF4 esperteza. Ou ser\xE1 que sou s\xF3 eu que eles ouvem?",
	["The wind whispers of your inexperience."] = "O vento sussurra sobre sua falta de experi\xEAncia.",
	["My lamp, a humble mirror of the Saint's own light."] = "Minha lamparina \xE9 um humilde espelho da pr\xF3pria luz do Santo.",
	["Thank you so much for saving us!"] = "Agrade\xE7o muito por nos salvar!",
	["Heave ho! A beggar's life's for me!"] = "A vida de mendigo \xE9 pra mim!",
	["Can't let the cold get to you. Warm hearts keep this place alive."] = "N\xE3o deixe o frio te pegar. Cora\xE7\xF5es quentes mant\xEAm este lugar vivo.",
	["If I had a coin for every ship I've lost... I'd have quite a few coins."] = "Se eu ganhasse uma moeda para cada navio que perdi, eu teria moeda pra caramba...",
	["Smell that? Sea-salt and ale, smells like home."] = "Est\xE1 sentindo? Sal marinho e cerveja. Cheirinho de casa.",
	["This rocking chair has seen more than any pirate."] = "Esta cadeira de balan\xE7o j\xE1 viu mais do que qualquer pirata.",
	["I was so afraid back in that cage!"] = "Eu estava com tanto medo naquela jaula!",
	["With these awful margins, I'd make more money selling snow in Glaceforde!"] = "Com essas margens rid\xEDculas, eu ganharia mais dinheiro vendendo neve em Glaceforde!",
	["Oh, how I wish I could pick your brain myself, Krieger..."] = "Ah, n\xE3o faz ideia do quanto eu quero pegar os seus miolos, Krieger...",
	["The heat is unbearable today..."] = "Que calor insuport\xE1vel hoje...",
	["This icy spray, it's a cold comfort against fear."] = "Esse ar frio \xE9 um consolo contra o medo.",
	["I've seen 'em, frolls dancing under the full moon. Quite a sight..."] = "Eu j\xE1 vi os frolls dan\xE7ando sob a lua cheia. Que vis\xE3o...",
	["Come share a pint or two, you youngsters could learn something."] = "Venha tomar uma cerveja ou duas. Voc\xEAs, jovens, poderiam aprender alguma coisa.",
	["One problem at a time, Tammi."] = "Um problema de cada vez, Tammi.",
	["Bandits, battles... Bah! All I care about is the sound of coins clinking."] = "Bandidos, batalhas... Bleh! O que me interessa \xE9 o som das moedas.",
	["Pawns, pawns in their game..."] = "Pe\xF5es, pe\xF5es em seu jogo...",
	["Saw a new breed of bloodsucker last night, Jean. Looked stronger."] = "Vi uma nova ra\xE7a de sugadores de sangue na noite passada, Jean. Pareciam mais fortes.",
	["You can order something or get a sip o' the swamp."] = "Voc\xEA pode pedir algo ou tomar um gole do p\xE2ntano.",
	["E-Even if I stutter, my magic won't!"] = "M-Mesmo se eu gaguejar, m-minha magia n\xE3o vai f-fracassar!",
	["Far Naddod may be rustic, but I'm rustic too, so I don't care!"] = "Far Naddod pode ser r\xFAstica, mas eu tamb\xE9m sou r\xFAstico, ent\xE3o n\xE3o me importo!",
	["Mortals... always so eager to make their lives even shorter."] = "Mortais... sempre t\xE3o ansiosos para tornar suas vidas ainda mais curtas.",
	["The sea doesn't reward bravery, it rewards wisdom."] = "O mar n\xE3o recompensa a bravura, recompensa a sabedoria.",
	["Can't let you on these docks. Not unless I'm sure you can handle yourself."] = "N\xE3o posso te deixar nessas docas. N\xE3o at\xE9 que eu seja convencido de que voc\xEA sabe se virar.",
	["No matter how many hags we slay, their stench still lingers in the swamp."] = "N\xE3o importa quantas bruxas matamos, o p\xE2ntando ainda fede com o cheiro delas.",
	["No talking with the prisoners!"] = "Nada de conversa com os prisioneiros!",
	["Marvelous show, Master Leagros! Marvelous!"] = "Um show maravilhoso, Mestre Leagros! Maravilhoso!",
	["I could've been born a farmer..."] = "Eu poderia ter nascido em uma fazenda...",
	["Quit moaning! Finish your duties and I'll buy each of you a drink!"] = "Chega de choramingar! Terminem seus deveres e eu pago uma bebida para cada um!",
	["Don't look at me."] = "N\xE3o olhe para mim.",
	["I'd duel anyone for a drink right now..."] = "Sairia no soco com qualquer um por uma bebida agora...",
	["No matter if it rains or snows, the CCC will deliver your stuff!"] = "Fa\xE7a chuva ou fa\xE7a sol, n\xF3s entregaremos suas coisas!",
	["Grr... Cowardly Djinns will pay."] = "Grr... Os Djinns covardes v\xE3o pagar por isso.",
	["Look at me!"] = "Olhe para mim!",
	["Shhh! Need to keep quiet..."] = "Shhh! Precisamos ficar em sil\xEAncio...",
	["If only I could venture in the depths myself..."] = "Se ao menos eu pudesse me aventurar em pessoa nas profundezas...",
	["Stay classy, Darzuac! He he he!"] = "Continue classuda, Darzuac! He he he!",
	["When we dream do our minds travel to different planes?"] = "Quando sonhamos, nossas mentes viajam para planos diferentes?",
	["Hic! Down goes another one!"] = "(Hic!) L\xE1 se vai mais uma!",
	["The ancient days of bloodshed are long past. Time to look to the future."] = "Os velhos tempos de derramamento de sangue ficaram para tr\xE1s. Hora de olhar para o futuro.",
	["I wonder if there's any possibility of enchanting a household object to fly..."] = "Ser\xE1 que existe um jeito de encantar um objeto dom\xE9stico para voar...",
	["Monsters and mysteries, this world is full of both."] = "Monstros e mist\xE9rios, este mundo est\xE1 cheio de ambos.",
	["Cheers - hic! May we live another day!"] = "Sa\xFAde! Que possamos viver mais um dia!",
	["Those djinn ruins hold more secrets than the sands reveal..."] = "Aquelas ru\xEDnas de Djinn guardam mais segredos do que as areias revelam...",
	["Get your bait, line and tackle here!"] = "Compre sua isca, linha e equipamento aqui!",
	["I've never once back away from a challenge!"] = "Eu nunca recuso um desafio!",
	["The winds carry tales of strife. The Jackals wage war, the desert weeps."] = "Os ventos carregam hist\xF3rias de sofrimento. Os chacais travam uma guerra, o deserto chora.",
	["I'm all sold out, folks. I'll be back with more little lost treasures soon!"] = "Acabou todo meu estoque, pessoal. Voltarei com mais tesouros perdidos em breve!",
	["Can't do the time if you don't do the crime...."] = "Se ningu\xE9m viu, eu n\xE3o fiz nada...",
	["Stay away from my traps. I've no use for your fur, ha!"] = "Fique longe das minhas armadilhas, n\xE3o tenho nada para fazer com sua pele, ha!",
	["They laughed at me, but when I return we'll see who'll be laughing..."] = "Riram de mim, mas quando eu voltar, vamos ver quem ri por \xFAltimo...",
	["Just a bit of drink gets anyone in the mood for gossip!"] = "S\xF3 um pouquinho de bebida j\xE1 deixa qualquer um no clima para uma fofoca!",
	["Don't forget we'll have drills at dawn every week!"] = "N\xE3o se esque\xE7a de que teremos treinamentos ao amanhecer toda semana!",
	["Blessings to all who are faithful!"] = "B\xEAn\xE7\xE3os sobre todos os fi\xE9is!",
	["Plans, I've got plans!"] = "Planos, eu tenho planos!",
	["I can't stand delays nor undelivered letters!"] = "N\xE3o suporto atrasos nem cartas n\xE3o entregues!",
	["Listen to me..."] = "Escuta...",
	["Talk to me, I have an offer!"] = "Chega mais, tenho uma oferta imperd\xEDvel!",
	["I get another wingin', whinin' slayer younglin' I'm gonna walk."] = "Se aparecer mais um ca\xE7ador reclamando e choramingando, t\xF4 fora.",
	["Now, where did I place that lamp..."] = "Agora, onde eu coloquei aquela l\xE2mpada...",
	["I'll need more ancient-looking stuff If I'm going to Gilead next..."] = "Vou precisar de coisas com apar\xEAncia mais antiga se eu for para Gilead depois...",
	["Be careful to avoid frogs and especially frogs bearing poison tipped weapons."] = "Cuidado, evite sapos e especialmente sapos com armas envenenadas.",
	["I will never allow anyone else to get the better of me again!"] = "Nunca mais permitirei que algu\xE9m se aproveite de mim!",
	["Interesting, the fumes of this mixture are making me blind..."] = "Interessante, os vapores dessa mistura est\xE3o me deixando cego...",
	["My life changed completely after I reached the rank of Seeker."] = "Minha vida mudou completamente depois que atingi a patente de Seeker.",
	["Can't talk! Work work work!"] = "N\xE3o posso falar! Trabalho, trabalho, trabalho!",
	["I might go out for one last scouting mission..."] = "Talvez eu v\xE1 em uma \xFAltima miss\xE3o de reconhecimento...",
	["When I was your age, I walked the entire continent... Now I can hardly get up from bed."] = "Quando eu tinha sua idade, atravessei o continente a p\xE9... Agora mal consigo levantar da cama.",
	["Damn cold out here..."] = "T\xE1 um frio maldito aqui fora...",
	["We train hard every single day to repel the draconic threat!"] = "Treinamos bastante todos os dias para repelir a amea\xE7a drac\xF4nica!",
	["I'm sure these gems will drive those hogs crazy."] = "Tenho certeza de que essas gemas v\xE3o deixar aqueles hogs malucos.",
	["Nothing to see here..."] = "Nada para ver aqui...",
	["So many humans these days... times sure are changing."] = "Tantos humanos nos dias de hoje... os tempos certamente est\xE3o mudando.",
	["The forest rewards those who defend it..."] = "A floresta recompensa aqueles que a defendem...",
	["We must ensure to preserve the natural balance."] = "Devemos garantir a preserva\xE7\xE3o do equil\xEDbrio natural.",
	["Gotta get my pot ready..."] = "Tenho que deixar minha panela pronta...",
	["What is a beast? What is a man? That is the age old question..."] = "O que \xE9 uma besta? O que \xE9 um homem? Eis a quest\xE3o mais antiga...",
	["A fisherman afraid of the water, can you imagine that?"] = "Um pescador com medo da \xE1gua, d\xE1 pra imaginar?",
	["I dun sell to no guards!"] = "N\xE3o vendo para nenhum guarda!",
	["The Ghaz are liars and cheats!"] = "Os Ghaz s\xE3o mentirosos e trapaceiros!",
	["The ravens circle Firslight!"] = "Os corvos rodeiam Firslight!",
	["Before long I will become the best wizard there ever was!"] = "Logo serei o melhor mago que j\xE1 existiu!",
	["Garamond... What am I to do with you..."] = "Garamond... O que eu vou fazer com voc\xEA...",
	["What's that? Got an injury or bad rash?"] = "O que foi? Est\xE1 com uma les\xE3o ou uma rea\xE7\xE3o al\xE9rgica?",
	["I dream of the day our children will be safe outside the home."] = "Sonho com o dia em que nossos filhos estar\xE3o seguros fora de casa.",
	["I better not be late to Mistress Tauras' lecture again..."] = "\xC9 melhor eu n\xE3o me atrasar para o serm\xE3o da mestra Taura novamente...",
	["Forging is as much an art as a technique, don't let Kaden tell you otherwise..."] = "Forjar \xE9 tanto uma arte quanto uma t\xE9cnica, n\xE3o deixe que Kaden te diga o contr\xE1rio...",
	["Who would've thought that a simple spell would be this hard?"] = "Quem diria que um feiti\xE7o simples assim seria t\xE3o dif\xEDcil?",
	["I feel sorry for any young Scholars under Lorekeeper Syvias, so grumpy!"] = "Sinto pena de qualquer jovem estudioso sob a supervis\xE3o do Lorekeeper Syvias, ele \xE9 t\xE3o rabugento!",
	["How come they always have a better hand than me?"] = "Como eles sempre t\xEAm uma m\xE3o melhor do que a minha?",
	["Every time we round up all the scum off the streets, more just take their place. It's like an infestation."] = "\xC9 s\xF3 tirar a esc\xF3ria das ruas que ela aparece ainda mais. Parecem pragas.",
	["Don't worry, I won't ask where ye got yer coins."] = "N\xE3o se preocupe, n\xE3o vou perguntar de onde voc\xEA tirou suas moedas.",
	["You have to tell me if you're a guard..."] = "Voc\xEA tem que me dizer se \xE9 um guarda...",
	["A straight is higher than a royal flush? No, no..."] = "Um straight \xE9 melhor do que um royal flush? N\xE3o, nem pensar...",
	["Too many outsiders these days..."] = "H\xE1 muitos forasteiros nos dias de hoje...",
	["Was it three drops or thirty?"] = "Foram tr\xEAs gotas ou trinta?",
	["Sometimes I'd like to have my own workshop, but that'd be too boring..."] = "\xC0s vezes gostaria de ter minha pr\xF3pria oficina, mas isso seria muito entediante...",
	["You can't see me, I'm too sneaky."] = "Voc\xEA n\xE3o est\xE1 me vendo, sou muito sorrateiro.",
	["Chop! Dice! Sear! It's not dwarven engineering, it's cooking!"] = "Corte! Pique! Sele! N\xE3o \xE9 engenharia an\xE3, \xE9 culin\xE1ria!",
	["Can one have an open mind without an open heart?"] = "\xC9 poss\xEDvel ter uma mente aberta sem um cora\xE7\xE3o aberto?",
	["There's a gazillion sand grains in the desert!"] = "Tem um gazilh\xE3o de gr\xE3os de areia no deserto!",
	["Now, where did I place that bottle..."] = "Onde ser\xE1 que eu coloquei aquela garrafa...",
	["Steady winds today. Perfect for keeping an eye on the horizon."] = "Ventos constantes hoje. Perfeito para ficar de olho no horizonte.",
	["Nobody ever wants to help those of us in the slums..."] = "Ningu\xE9m quer ajudar quem mora nos bairros pobres...",
	["Nobody will ever understand the humiliation I suffered."] = "Ningu\xE9m jamais entender\xE1 a humilha\xE7\xE3o que sofri.",
	["The Slayers remain ignorant of the real threat..."] = "Ca\xE7adores continuam ignorantes da verdadeira amea\xE7a...",
	["Where's my assistant! Oh, right..."] = "Onde est\xE1 meu assistente! Ah, \xE9 verdade...",
	["Komuram should not be locked up in here."] = "Komuram n\xE3o deveria estar trancado aqui.",
	["How does one know who they are?"] = "Como algu\xE9m sabe quem \xE9?",
	["I see you, demons. You'll soon fall to my blade."] = "Eu vejo voc\xEAs, dem\xF4nios. Logo cair\xE3o diante da minha l\xE2mina.",
	["Never forget the betrayal..."] = "Nunca esque\xE7a a trai\xE7\xE3o...",
	["I wonder if I could convince one of the Slayers to join us here..."] = "Ser\xE1 que eu consigo convencer um dos ca\xE7adores a vir com a gente...",
	["Peace of mind is what I need now..."] = "Paz de esp\xEDrito \xE9 o que eu preciso agora...",
	["I hope they don't find me..."] = "Espero que n\xE3o me encontrem...",
	["Perhaps one day I'll test lethal magical enchantments."] = "Talvez um dia eu teste encantamentos m\xE1gicos letais.",
	["Welcome to Whale of a Catch! What are you drinking today?"] = "Boas-vindas! O que vai beber hoje?",
	["I'm surrounded by short sighted fools and brainless glory hounds..."] = "Estou cercado de tolos m\xEDopes e idiotas que s\xF3 querem saber de gl\xF3ria...",
	["I'm glad professor Marius took me in..."] = "Que bom que o professor Marius me acolheu...",
	["They promised us a better world..."] = "Eles prometeram um mundo melhor para n\xF3s...",
	["To think a man of my noble pedigree must compete with that... peasant!"] = "Pensar que um homem do meu quilate vai precisar competir com aquele... plebeu!",
	["Every thread has a place in the grand tapestry, Ananda sees to it with care."] = "Cada fio tem seu lugar na grande tape\xE7aria, Ananda cuida para que seja assim.",
	["Darzuac might be the Jewel of the South, but here I've found a treasure like no other... freedom."] = "Darzuac pode ser a Joia do Sul, mas aqui eu encontrei um tesouro como nenhum outro... liberdade.",
	["These treasures fell off the back of a ship... a very, uh, generous ship!"] = "Esses tesouros ca\xEDram de um navio... um navio muito, digamos, generoso!",
	["Where's Sir Cuddles?"] = "Cad\xEA o Sir Cuddles?",
	["Hrm."] = "Hrm.",
	["C'mere, little fishies!"] = "Venham aqui, peixinhos!",
	["No need for questions, just buy it and thank me later."] = "N\xE3o precisa fazer perguntas, apenas compre e me agrade\xE7a depois.",
	["Perhaps I should experiment with lightning..."] = "Talvez eu devesse fazer experimentos com raios...",
	["Why do these outsiders come to me for directions? Do I look like a guide?!"] = "Por que esses forasteiros v\xEAm me pedir dire\xE7\xF5es? Por acaso eu pare\xE7o um guia?!",
	["One day I'll cook for the king, mark my words."] = "Um dia eu vou cozinhar para o rei, pode escrever.",
	["The fire of youth needs to be nurtured and appreciated!"] = "O fogo da juventude precisa ser alimentado e apreciado!",
	["All kinds of folks are welcome here, unless you're a filthy pirate, of course."] = "Todos os tipos de pessoas s\xE3o bem-vindos aqui, a menos que seja um pirata imundo, \xE9 claro.",
	["Everyone comes to me looking to enchant objects for them, but it's only for appearances."] = "Todos v\xEAm falar comigo para encantar objetos para eles, mas \xE9 s\xF3 pelas apar\xEAncias.",
	["Those mutts will suffer for their insolence."] = "Aqueles vira-latas v\xE3o pagar pela insol\xEAncia.",
	["Nugs, not drugs."] = "Onde \xE9 que eu estava mesmo? Ah, lembrei...",
	["The world is vast and curious."] = "O mundo \xE9 vasto e curioso.",
	["I'm just passing through."] = "Estamos todos s\xF3 de passagem.",
	["A snip here, a snip there, and you'll be the talk of the town!"] = "Uma aparadinha aqui, um cortezinho ali, e voc\xEA ser\xE1 o assunto da cidade!",
	["A great haircut isn't just about style, it tells a story all on itself!"] = "Um corte de cabelo incr\xEDvel n\xE3o \xE9 apenas sobre estilo, \xE9 sobre contar toda uma hist\xF3ria!",
	["Ah, hair is like life. Full of twists, turns, and unexpected tangles."] = "Ah, cabelo \xE9 igual \xE0 vida. Cheio de reviravoltas, curvas e emaranhados inesperados.",
	["This year's parade will be a disaster If I can't get this seam right!"] = "O desfile deste ano vai ser um desastre se eu n\xE3o acertar esta costura!",
	["Firslight will be the final light to hold!"] = "Firslight ser\xE1 a \xFAltima luz acesa!",
	["Hmm... yes... wind speed here is steady and stable, too."] = "Hmm... sim... a velocidade do vento aqui est\xE1 constante e est\xE1vel tamb\xE9m.",
	["Alright, just need to find another measuring spot..."] = "Tudo bem, s\xF3 preciso encontrar outro ponto de medi\xE7\xE3o...",
	["This is intriguing! I wonder if all of the vale is the same."] = "Isso \xE9 intrigante! Eu me pergunto se todo o vale \xE9 assim.",
	["Sombershade should be renamed to Ananda's Folly, if you ask me."] = "Sombershade deveria ser renomeada para Loucura de Ananda, se quer saber minha opini\xE3o.",
	["Style and looks are good, but you won't kill anything with just that..."] = "Estilo e apar\xEAncia s\xE3o legais, mas s\xF3 isso n\xE3o basta...",
	["We are always in need of more knights!"] = "Estamos sempre precisando de mais cavaleiros!",
	["Here's a health to the company!"] = "\xC0 sa\xFAde da companhia!",
	["The Raven's Throne is the best tavern in town, and there ain't nobody that can tell me otherwise!"] = "A Raven's Throne \xE9 a melhor taverna da cidade, e n\xE3o tem ningu\xE9m que possa me convencer do contr\xE1rio!",
	["Knighthood is open to anyone who can prove themselves!"] = "Qualquer um que prove sua habilidade pode se tornar cavaleiro!",
	["Trust Old Willy, get as far from the League of Splinters as ye can!"] = "Confie no Velho Willy, fique o mais longe poss\xEDvel da Liga dos Estilha\xE7os!",
	["Make sure you store the gunpowder away from the sun this time!"] = "Certifique-se de guardar a p\xF3lvora longe do sol desta vez!",
	["It's dreary enough already around here without stopping our feasts and celebrations!"] = "N\xE3o devemos parar com os banquetes e celebra\xE7\xF5es! A vida j\xE1 est\xE1 sombria demais por aqui.",
	["Don't eavesdrop!"] = "N\xE3o fique espiando!",
	["All are welcome here!"] = "Todos s\xE3o bem-vindos aqui!",
	["Don't talk to me."] = "N\xE3o fale comigo.",
	["I am so close to the discovery of the century, I can almost taste it..."] = "Estou muito perto da descoberta do s\xE9culo, quase d\xE1 para sentir o gosto...",
	["Ahlan, weary traveler. Pull up a seat and let the tales weave around you."] = "Ahlan, viajante. Puxe uma cadeira e deixe as hist\xF3rias envolverem voc\xEA.",
	["Hmm... yes, this one is really valuable..."] = "Hmm... sim, esta aqui \xE9 bem valiosa...",
	["Despite the tales, they really don't prepare you for the cold..."] = "Apesar de todos os contos, eles realmente n\xE3o te preparam para o frio...",
	["Elf longevity... transferrable to humans? Further study needed."] = "Longevidade \xE9lfica... transfer\xEDvel para humanos? \xC9 necess\xE1rio estudo adicional.",
	["There is nothing better than seeing a young drake slayer take their first kill."] = "N\xE3o h\xE1 nada melhor do que ver um jovem ca\xE7ador de drakes completando sua primeira ca\xE7ada.",
	["Ah, my joints hurt again..."] = "Ah, minhas juntas est\xE3o doendo de novo...",
	["I wonder what it is like to have so little time to contemplate life..."] = "Eu me pergunto como \xE9 ter t\xE3o pouco tempo para contemplar a vida...",
	["Working together is the only way for us to prosper."] = "Trabalhar juntos \xE9 a \xFAnica maneira de prosperarmos.",
	["Your path in life is a weaving, and every stitch tells a story."] = "Seu caminho na vida \xE9 como tecer. Cada ponto conta uma hist\xF3ria...",
	["Storm's a-brewin'! Old Willy can feel it in them old bones!"] = "Tempestade se aproximando! O Velho Willy pode sentir isso nos ossos!",
	["We would rather lose a hand than give our loot to another man!"] = "Preferimos perder uma m\xE3o do que dar nosso saque a outro homem!",
	["Perhaps one day I will find other survivors among my kin..."] = "Talvez um dia eu encontre outros sobreviventes entre os meus...",
	["May you never lack warmth in these frozen lands."] = "Que voc\xEA nunca perca o calor nestas terras congeladas.",
	["Locking up long-legs must be the funniest thing I ever did!"] = "Trancar pernas longas deve ser a coisa mais engra\xE7ada que j\xE1 fiz!",
	["Winterborn blood, Saint Alsek's heart."] = "Sangue de Winterborn, cora\xE7\xE3o de Saint Alsek.",
	["I'm not - hic! drunk... I'm right in a hunting mood."] = "N\xE3o estou (hic!) b\xEAbado... Estou morrendo de vontade de ca\xE7ar.",
	["You're not ready for the tower's glow."] = "Voc\xEA n\xE3o tem o preparo necess\xE1rio para o brilho da torre.",
	["Treasure hunting is dangerous business, but it's worth it."] = "Ca\xE7ar tesouros \xE9 um neg\xF3cio perigoso, mas vale a pena.",
	["Back off, friend. These docks are closed for your safety."] = "Para tr\xE1s, camarada. Essas docas est\xE3o fechadas por quest\xE3o de seguran\xE7a.",
	["I hope this jungle fellow isn't just leading me on..."] = "Espero que este colega da selva n\xE3o esteja apenas me enganando...",
	["Seabreeze, a nest of scoundrels. Good for business."] = "Seabreeze \xE9 um ninho de patifes, mas \xE9 um bom local para neg\xF3cios.",
	["Here to fix another cartwheel? Sigh..."] = "Veio para consertar mais um carrinho de m\xE3o? (Suspiro...)",
	["No entry, no entry! Secret stuff inside. Go away, big one!"] = "N\xE3o pode entrar! N\xE3o pode entrar! Tem coisa secreta aqui dentro. Cai fora, grande!",
	["I wonder what happened to Timulus the Enchanter..."] = "O que ser\xE1 que aconteceu com o Timulus...",
	["Skill and dedication first, then you may use my looms."] = "Primeiro, habilidade e dedica\xE7\xE3o. Depois voc\xEA pode usar meus teares.",
	["I just need my big break..."] = "S\xF3 preciso de uma chance...",
	["This section could use a little dusting..."] = "Uma limpadinha nessa se\xE7\xE3o cairia bem...",
	["They told me Zephyr Vale was the new real state hit... I'm not amused."] = "Ouvi falar que Zephyr Vale era o novo sucesso imobili\xE1rio... Isso n\xE3o me surpreende.",
	["L-Lewett says I can't cast, but I'll p-prove him wrong."] = "L-Lewett diz que n\xE3o posso conjurar, mas vou p-provar que ele est\xE1 errado.",
	["Come, come! You will find what you always wanted!"] = "Venham, venham! Aqui temos o que voc\xEA procura!",
	["Come back when you've got something to show for your skills."] = "Volte quando tiver alguma coisa que prove suas habilidades!",
	["Captain Haroldsson has been sending out patrols searching for the Hookmask hideout, but no luck so far."] = "Captain Haroldsson tem enviado patrulhas em busca do esconderijo dos Hookmasks, mas at\xE9 agora sem sucesso.",
	["The black market here is thriving, but you have to know where to look."] = "O mercado ilegal aqui est\xE1 crescendo como nunca, mas voc\xEA precisa saber onde procurar.",
	["More and more folk indulge in Twilight, even the guards partake. I like that Darzuac have such an exquisite specialty, but what if everyone just turn into a bunch of layabouts?"] = "Cada vez mais pessoas se rendem \xE0 Twilight, at\xE9 mesmo os guardas. O problema \xE9 se todo mundo aqui em Darzuac se transformar em um bando de desocupados.",
	["Who paid for the bait this time?"] = "Quem pagou pela isca desta vez?",
	["The rich merchants and the armed guards might make it look like the city is under control, but those Crimson Lotus criminals run the place."] = "Os ricos comerciantes e os guardas armados podem dar a impress\xE3o de que a cidade est\xE1 sob controle, mas \xE9 a Crimson Lotus que comanda o local.",
	["The rat problem only seems to be getting worse! It's like somebody wants to keep this place infested..."] = "O problema com os ratos parece s\xF3 piorar! Parece at\xE9 que algu\xE9m quer manter este lugar infestado desses vermes...",
	["Ever had one of Marcella's rat kebabs? They're delicious!"] = "J\xE1 experimentou um dos kebabs de rato da Marcella? S\xE3o deliciosos!",
	["I've tried to get into the dyeing business, but venturing into the desert is just too dangerous now-a-days."] = "Tentei entrar no neg\xF3cio de tingimento, mas se aventurar no deserto est\xE1 cada vez mais perigoso nos dias de hoje.",
	["Nothing better than good fishing!"] = "Nada melhor do que uma boa pescaria!",
	["Couldn't get a wink o' sleep last night. I kept hearing scratching under my bed. Maybe I've got rats or somethin'."] = "Ouvi arranh\xF5es embaixo da minha cama a noite toda. N\xE3o consegui pregar os olhos. Devem ser ratos ou algo do tipo.",
	["If I have to hear about the 'deep one' one more time from that mad beggar while on patrol, I'm going to lose it!"] = "Se durante a patrulha eu tiver que ouvir daquele mendigo louco sobre o \"Grande Abissal\" mais uma vez, n\xE3o vou responder por mim!",
	["Despite some complaining that Captain Haroldsson drills us too hard, unlike other officers, he joins us in our training every day."] = "Verdade seja dita, apesar de algumas reclama\xE7\xF5es sobre o treinamento rigoroso do Captain Haroldsson, ele se junta a n\xF3s todos os dias.",
	["I love the sea view. It makes me relax and unwind."] = "Amo a vista para o mar, \xE9 t\xE3o relaxante!",
	["I signed up to the guard after my parents farm was raided by the Hookmask bandits. I'll make them pay!"] = "Eu decidi ser guarda depois que a fazenda dos meus pais foi saqueada pelos bandidos Hookmask. Eles v\xE3o pagar por isso!",
	["When I reach the top of the lookout tower, everyone looks like ants below and I can see every mountain in the region!"] = "Toda vez que eu subo ao topo da torre de observa\xE7\xE3o, todo mundo fica parecendo formiguinha l\xE1 embaixo e consigo ver todas as montanhas da regi\xE3o!",
	["Hm."] = "Hm.",
	["Hey, there's a huge balcony upstairs... Dare you to jump off it!"] = "Ei, tem uma sacada enorme l\xE1 em cima... Duvido voc\xEA pular dela!",
	["If ya hear any singin' in the swamp, know that it's probably a hag."] = "Se voc\xEA ouvir alguma cantoria no p\xE2ntano, provavelmente \xE9 uma bruxa.",
	["Have you ever tried Joy? I hear it's incredible, but I'm a little worried about wanting more afterwards..."] = "J\xE1 experimentou a Joy? Ouvi dizer que \xE9 incr\xEDvel, o problema \xE9 se eu quiser mais depois...",
	["Why does it have to be so dark and deep?"] = "Por que precisa ser t\xE3o sombrio e profundo?",
	["The music is much more lively in the South than the capital, I love it!"] = "Nossa, a m\xFAsica \xE9 muito mais animada no sul do que na capital!",
	["Soon enough, I'll be leavin' this place behind."] = "Em breve, vou deixar este lugar para tr\xE1s.",
	["Did you know you can actually see Gilead from here when the sky is clear?"] = "Realmente d\xE1 pra ver Gilead daqui quando o c\xE9u est\xE1 limpo. Voc\xEA sabia disso?",
	["I don't sugarcoat stories, I lay them bare for all to see."] = "Quando eu conto hist\xF3rias n\xE3o tem enrola\xE7\xE3o, eu digo a verdade nua e crua.",
	["See any monsters wandering the city streets? No? Didn't think so. You can thank us guards for that!"] = "Viu alguma criatura perambulando pelas ruas da cidade? N\xE3o? Pois \xE9, voc\xEA deveria agradecer a n\xF3s, guardas, por isso!",
	["Hic! What's that... spiders?"] = "Hic! O que \xE9 isso... aranhas?",
	["Hookmask bandits on every front and Morningstar fanatics in the North. This keep is called Defiance for a reason!"] = "Bandidos Hookmask em todos os lugares e fan\xE1ticos Morningstar ao norte. N\xE3o \xE9 \xE0 toa que este forte se chama Defiance!",
	["Why did the priest cover his eyes? Because the Dawn blinded him!"] = "Por que o padre cobriu os olhos? Porque a Alvorada o cegou!",
	["I heard rumors that Helga von Thuring has been dealing with more assaults from the bandits, if I was any good at fighting I bet she would pay a lot."] = "Ouvi rumores de que Helga von Thuring tem lidado com mais ataques dos bandidos. Se eu fosse bom de briga, aposto que ela pagaria muito.",
	["I'll need some kind of banner..."] = "Precisarei de algum tipo de estandarte...",
	["Leave, before I kill you."] = "Saia, antes que eu acabe com voc\xEA.",
	["Ask not what the people can do for you, ask what you can do for the people!"] = "N\xE3o pergunte o que o povo pode fazer por voc\xEA, pergunte o que voc\xEA pode fazer pelo povo!",
	["Y-you think yo-hic! Think you're better than meh!?"] = "V-voc\xEA acha que \xE9 mel-(hic!)-melhor do que eu!?",
	["Don't try to argue with Kaden about finer details... It's pointless."] = "Nem adianta tentar discutir com o Kaden... \xC9 como dar murro em ponta de faca.",
	["I'm meeting someone... er, I'm supposed to anyway... it's been four hours... they're still coming, I think..."] = "Estou aqui esperando... bom, pelo menos eu acho, mas j\xE1 se passaram quatro horas... eles ainda est\xE3o vindo, espero...",
	["Dawn's blessings to you, child. May Ananda's teachings light your path."] = "Que as b\xEAn\xE7\xE3os da Alvorada caiam sobre voc\xEA, e que os ensinamentos de Ananda iluminem seu caminho.",
	["Stop staring or I'll take your eyes."] = "T\xE1 olhando o qu\xEA? Quer que eu arranque seus olhos?",
	["Great hunt coming, Djinns make mighty prey."] = "Uma \xF3tima ca\xE7ada se aproxima, Djinns s\xE3o presas formid\xE1veis.",
	["Huh? This is only my tenth round... don't judge me! I only drink to calm my nerves!"] = "Hein? Essa \xE9 apenas a d\xE9cima caneca... n\xE3o me julgue! S\xF3 bebo para acalmar os nervos!",
	["Trolls, they're everywhere."] = "Trolls, eles est\xE3o por toda parte.",
	["My axe is thirsty."] = "Meu machado est\xE1 sedento.",
	["The only way to conquer death is to embrace it!"] = "A \xFAnica maneira de vencer a morte \xE9 aceit\xE1-la de bom grado!",
	["Joining the Ravenguard isn't easy. Every day, we go through intense training so we are prepared for every situation."] = "Entrar para a Ravenguard n\xE3o \xE9 f\xE1cil. Todos os dias passamos por um treinamento intenso para estarmos preparados para qualquer situa\xE7\xE3o.",
	["Gotta keep quiet..."] = "Tenho que me manter em sil\xEAncio...",
	["Human. Chicken. Same thing."] = "Humano. Galinha. \xC9 tudo a mesma coisa.",
	["Plenty profitable opportunities around here, traveler!"] = "Tem muitas oportunidades lucrativas por essas bandas, viajante!",
	["It's too dangerous for most of us to settle land around Defiance, but there's always work at Seafroth."] = "Para a maioria de n\xF3s, \xE9 muito perigoso pensar em viver nas terras ao redor de Defiance, mas sempre h\xE1 trabalho em Seafroth.",
	["When facing danger, it is better to stand tall than to cower."] = "Ao enfrentar o perigo, \xE9 melhor encarar de frente do que se encolher.",
	["I don't love living here, but from what I hear it's way better than Glademire..."] = "Confesso que n\xE3o amo morar aqui, mas pelo que ou\xE7o, \xE9 muito melhor do que Glademire...",
	["Nightmares... nightmares and reality merged together..."] = "Pesadelos... pesadelos e realidade se tornaram uma coisa s\xF3...",
	["I don't want strangers messing around. The Bog hates outsiders."] = "N\xE3o quero estranhos mexendo por aqui. Est\xE1 avisado. ",
	["The quicker this is done, the quicker we can play cards at the inn!"] = "Quanto mais r\xE1pido isso for feito, mais r\xE1pido poderemos jogar cartas na estalagem!",
	["If only there was some way to make my home cool at the end of the day and warm in the night while I sleep at will. Is there a spell for that maybe?"] = "Ser\xE1 que existe alguma magia para manter a casa fresca durante o dia e aquecida durante a noite? \xC9 meu sonho. ",
	["I could - hic! Shoot a spider between its eight eyes!"] = "Eu poderia (hic!) atirar entre os oito olhos de uma aranha!",
	["It don't matter where I hide my food, the rats always find it! Filthy rodents..."] = "\xC9 impressionante, os ratos sempre encontram minha comida n\xE3o importa onde eu esconda ela! Que roedores nojentos...",
	["I should check up on Corbin soon, I don't like that cough he's been having..."] = "Preciso dar uma olhada no Corbin daqui a pouco, ele anda tossindo muito...",
	["..."] = "...",
	["I strive to find warmth even in the coldest hearts."] = "Eu me esfor\xE7o para encontrar calor at\xE9 nos cora\xE7\xF5es mais frios.",
	["Alright."] = "Certo.",
	["They think I'm crazy..."] = "Eles acham que sou louco...",
	["The Igotawa clan will rise again!"] = "O cl\xE3 Igotawa se levantar\xE1 novamente!",
	["Our ancient wisdom and power can't be compared to the crude weapons those wild dogs fling at us."] = "Nossa antiga sabedoria e poder n\xE3o podem ser comparados \xE0s armas grosseiras que aqueles c\xE3es selvagens lan\xE7am contra n\xF3s.",
	["The way of the shadow is not for the faint of heart."] = "O caminho da sombra n\xE3o \xE9 para os fracos de cora\xE7\xE3o.",
	["Eons of time, grains of sand... each story unique in the endless dunes."] = "\xC9ons, gr\xE3os de areia... toda hist\xF3ria \xE9 \xFAnica nas dunas intermin\xE1veis.",
	["Bloody Ghaz... nothing but a bunch of lousy, thieving, raiders!"] = "Malditos Ghaz... nada mais que um bando de ladr\xF5es, saqueadores miser\xE1veis!",
	["Dragons, drakes... worthless wyrms the lot of them."] = "Drag\xF5es, drakes... todos vermes in\xFAteis.",
	["The only good demon is a dead demon."] = "Dem\xF4nio bom \xE9 dem\xF4nio morto.",
	["Please rest and pray to the Dawn for good fortune."] = "Por favor, descanse e pe\xE7a boa sorte \xE0 Alvorada.",
	["Funny that we often use the tools of our enemies against them."] = "\xC9 curioso que muitas vezes usamos as ferramentas dos nossos inimigos contra eles.",
	["I can't wait to blow up a monster with a fireball!"] = "Mal posso esperar para explodir um monstro com uma bola de fogo!",
	["From hero to zero... how could it happen to me?"] = "Do luxo ao lixo... como isso p\xF4de acontecer comigo?",
	["It's wonderful to finally have a little fun... the Red Council is terribly dull."] = "\xC9 maravilhoso finalmente ter um pouco de entretenimento... o Conselho Vermelho \xE9 muito entediante.",
	["Why does the priest hate vampires? Because they're too dark!"] = "Por que o padre odeia vampiros? Porque eles n\xE3o gostam da luz divina!",
	["At least I'm not outside, patrolling Rohna anymore..."] = "Pelo menos n\xE3o estou mais l\xE1 fora, patrulhando Rohna...",
	["The next one to complain gets latrine duty!"] = "O pr\xF3ximo que reclamar vai lavar os banheiros!",
	["I wonder why Master Leagros wants all those things..."] = "Por que ser\xE1 que o Mestre Leagros quer todas essas coisas...",
	["Uh... Spare a coin?"] = "Uh... Tem um trocado?",
	["If trolls come again, there's a sweet hiding spot I found."] = "Se os trolls voltarem, encontrei um esconderijo supimpa.",
	["Fishin's been great this season!"] = "Essa temporada de pesca est\xE1 \xF3tima!",
	["Somehow, I can never get bored of crafting arrows, I wonder why..."] = "De alguma forma, nunca fico entediado de fazer flechas, me pergunto por qu\xEA...",
	["I wonder if we could recruit humans into our ranks..."] = "Me pergunto se poder\xEDamos recrutar humanos...",
	["Someday, my work will be famous all over Ravendawn!"] = "Um dia, meu trabalho ser\xE1 famoso por toda Ravendawn!",
	["Dark wings bring darker deeds!"] = "Asas sombrias trazem feitos mais sombrios ainda!",
	["Why did the holy knight refuse to joust? Because the lance was \"light\"-weight!"] = "Por que o cavaleiro recusou a justa? Porque ele sentiu que a situa\xE7\xE3o estava ficando apertada.",
	["Hmm."] = "Hmm.",
	["No good recruits anywhere."] = "N\xE3o h\xE1 bons recrutas em lugar nenhum.",
	["The night's lady won't claim my mind!"] = "A dama da noite n\xE3o tomar\xE1 minha mente!",
	["Have you met  Bryce already? Ask him about the time I burned his hair, ha!"] = "Voc\xEA j\xE1 conheceu o Bryce? Pergunte a ele sobre a vez em que eu queimei o cabelo dele, ha!",
	["Forging is in our blood, molten steel runs through our veins!"] = "Forjar est\xE1 no nosso sangue, a\xE7o derretido corre em nossas veias!",
	["Plunder a ship then gamble it all away... That's life for me!"] = "Saquear um navio e depois perder tudo jogando... Isso que \xE9 vida para mim!",
	["Just a man minding his own business..."] = "Apenas um homem cuidando da pr\xF3pria vida...",
	["They never send us enough bodies to fight the good fight."] = "Nunca nos enviam homens o suficiente para lutar.",
	["Any and all donations are welcome to help the needy."] = "Toda e qualquer doa\xE7\xE3o \xE9 bem-vinda para ajudar os necessitados.",
	["Sniff, sniff.. Storm's coming, be wary of the shifting sands."] = "Estou sentindo cheiro de tempestade se aproximando. Cuidado com as areias movedi\xE7as.",
	["When a peasant crossed me, I turned him into a newt! He got better."] = "Quando um plebeu me desagradou, o transformei em uma salamandra! Tudo melhorou.",
	["What's that smell? Oh, it's me. Ha ha ha!"] = "Que cheiro \xE9 esse? Ah, sou eu. Ha ha ha!",
	["Witchcraft is closer to science than to magic, you know..."] = "Feiti\xE7aria est\xE1 mais pr\xF3xima da ci\xEAncia do que da magia, sabe...",
	["How would I even buy that many weapons?"] = "Como \xE9 que eu compraria tantas armas?",
	["If you know of any good spot, let me know!"] = "Se voc\xEA souber de algum lugar bom, me avise!",
	["The Ravenguard took me in, they're my clan now."] = "Os Ravenguard me acolheram, eles s\xE3o meu cl\xE3 agora.",
	["I will become a Lorekeeper, but not until I sate my thirst for adventure!"] = "Me tornarei um Lorekeeper, mas n\xE3o at\xE9 saciar minha sede de aventura!",
	["Ah, this will make a fine addition to my collection..."] = "Ah, isso ser\xE1 uma \xF3tima adi\xE7\xE3o \xE0 minha cole\xE7\xE3o...",
	["Vampires are nothing compared to demons..."] = "Vampiros n\xE3o s\xE3o nada comparados a dem\xF4nios...",
	["Rat spice! Get yer rat spice here!"] = "Tempero para rato! Na minha m\xE3o \xE9 mais barato!",
	["Who knew that I would enjoy my unlife far more than my living state?"] = "Quem diria que eu gostaria muito mais da minha n\xE3o-vida do que de viver?",
	["Perhaps I should start hiding cards up my sleeve...."] = "Talvez eu deva come\xE7ar a esconder cartas na manga...",
	["Just 1 silver! Get 6 rat spice for the price of 5!"] = "Tempero para rato na promo\xE7\xE3o! Compre 5 e leve 6 por apenas 1 silver!",
	["You'll find the residents of Orca Bay are very kind when they open up."] = "Voc\xEA vai descobrir que os moradores de Orca Bay s\xE3o muito gentis quando se abrem.",
	["A guard's duty is never idle. Troubles can come at any moment."] = "O dever de um guarda nunca \xE9 ocioso. Problemas podem surgir a qualquer momento.",
	["Ugh... can't stand this heat. My ice cubes are meltin'..."] = "Ah... n\xE3o aguento esse calor. Meus cubos de gelo est\xE3o derretendo...",
	["These trees are funny!"] = "Essas \xE1rvores s\xE3o engra\xE7adas!",
	["I swear, if I see another sand-grain in my pot..."] = "Juro, se eu vir outro gr\xE3o de areia na minha panela...",
	["I should invite more mortals..."] = "Eu deveria convidar mais mortais...",
	["Welcome to Corvo's Courier Company. What would you like dispatched?"] = "Boas-vindas \xE0 Companhia de Correios Corvo. O que voc\xEA gostaria de despachar?",
	["I wonder what opportunities await beyond the seas and sands..."] = "Me pergunto que oportunidades esperam al\xE9m dos mares e areias...",
	["See a lot of newcomers lately... won't last long."] = "Tenho visto muitos novatos ultimamente... n\xE3o v\xE3o durar muito.",
	["Why did the priest climb the ladder? To get to a higher power!"] = "Por que o padre subiu na escada? Para alcan\xE7ar um poder mais alto!",
	["What treasures does the desert whisper to you, wanderer?"] = "Quais tesouros o deserto sussurra em seus ouvidos, viajante?",
	["Guard! Komuram demands to be released!"] = "Guarda! Komuram exige ser libertado!",
	["Uhuh."] = "Uhuh.",
	["Help an old beggar eat?"] = "Ajude um velho mendigo a comer?",
	["Someday I hope to get out there and travel around Ravendawn just like all those adventurers. I certainly don't want to live my whole life in one place."] = "Um dia espero sair por a\xED e viajar por Ravendawn como esses aventureiros. N\xE3o quero viver minha vida inteira em um s\xF3 lugar.",
	["Alright... should I mix this with... that?"] = "Tudo bem... devo misturar isso com... aquilo?",
	["Hmm... Toss a silver to your Ranger..."] = "Hmm... passa um trocado a\xED...",
	["It is always a pleasure to meet other Lyderians."] = "\xC9 sempre um prazer conhecer outros lyderianos.",
	["I don't want to believe the rumors of missing children... I can't..."] = "N\xE3o quero acreditar nos rumores sobre crian\xE7as desaparecidas... N\xE3o pode ser verdade...",
	["Although we have few professional soldiers, every farmer trains weekly to protect Truce."] = "Embora tenhamos poucos soldados profissionais, todo fazendeiro treina semanalmente para proteger Truce.",
	["It's funny that we mostly do our patrols at dusk and dawn. Most folk, especially those up to no good prefer to stay indoors when the sun is out in full force."] = "\xC9 engra\xE7ado que fazemos nossas patrulhas principalmente ao anoitecer e ao amanhecer. A maioria das pessoas, especialmente as que n\xE3o t\xEAm boas inten\xE7\xF5es, prefere ficar dentro de casa quando o sol est\xE1 a pino.",
	["Aye, I've tried leaving Sajecho... but the winds always bring me back here to this very pub!"] = "Por mais que eu tente sair de Sajecho, os ventos sempre me trazem de volta!",
	["With all sorts of spices and exotic flavors, the food here is amazing!"] = "Com todas as especiarias e sabores ex\xF3ticos da regi\xE3o, a comida aqui \xE9 incr\xEDvel!",
	["Be careful out there, especially after havin' a drink! You never know what might be watching you from the leaves."] = "Tenha cuidado por a\xED, especialmente depois de beber! Voc\xEA nunca sabe quem pode estar te observando.",
	["Have a good one."] = "Tenha um bom dia.",
	["Although the Ghaz are fearsome fighters, they are no match for the discipline of Ravenguards."] = "Embora os Ghaz possam intimidar, eles n\xE3o s\xE3o p\xE1reo para a disciplina dos Ravenguards.",
	["The fish seem to be getting smarter... They're not biting like they used to."] = "Os peixes parecem estar ficando mais espertos... Eles n\xE3o mordem a isca como antes.",
	["The guards are doing all the hard work right now. Me? I'm holing up in here until the bad stuff goes away."] = "Os guardas est\xE3o l\xE1 fazendo todo o trabalho sujo. J\xE1 eu, estou me escondendo aqui at\xE9 o pior passar.",
	["Aye, the goblins attack us every now and then... but they always get sent back to their hills, shrieking in pain!"] = "Sim, os goblins nos atacam de vez em quando, mas eles sempre s\xE3o mandados de volta para suas colinas, gritando de dor!",
	["If I had a silver piece for every rat I've killed, I'd be as rich as the king himself!"] = "Se eu ganhasse uma moeda por cada rato que j\xE1 dei fim, eu j\xE1 estaria t\xE3o rico quanto o rei!",
	["I get that Defiance is under constant threat of attack, but do we really have to give up our harvests to support them?"] = "Entendo que Defiance est\xE1 sob constante amea\xE7a de ataque, mas ser\xE1 que realmente precisamos abrir m\xE3o de nossas colheitas para apoi\xE1-los?",
	["What's skill good for when you don't have a coin to your name, anyways?"] = "De que serve a habilidade quando voc\xEA n\xE3o tem um tost\xE3o no bolso?",
	["Nobody cares about us common folk this far South. I wish I could see the capital one day and see what real guards look like..."] = "N\xF3s aqui no sul muitas vezes passamos despercebidos. Ningu\xE9m se importa com a gente. Um dia eu gostaria de poder visitar a capital e ver como s\xE3o os verdadeiros guardas...",
	["This is nothin' compared to trapping frost wolves. Child's play!"] = "Isso n\xE3o \xE9 nada comparado a capturar lobos na neve. Mel na chupeta!",
	["The stuff I have now is getting pretty worn out, I might have to buy new things."] = "As coisas que tenho agora est\xE3o ficando bem gastas. Talvez eu tenha que comprar coisas novas.",
	["If only I could fix myself up into fighting form..."] = "Se ao menos eu pudesse entrar em forma...",
	["I hope to one day become a personal guard to the doge, those fellas live like kings!"] = "Espero um dia me tornar guarda pessoal do doge. Esses caras vivem como reis!",
	["We all wear masks to hide ourselves from the world."] = "Todos usamos m\xE1scaras para nos esconder do mundo.",
	["I doubt you'll ever see a place as beautiful as our beloved Sajecho!"] = "N\xE3o h\xE1 lugar mais bonito do que Sajecho!",
	["Rat spice! Unquestionable origins rat spice!"] = "Tempero de rato! Tempero de rato de proced\xEAncia inquestion\xE1vel!",
	["I can't believe he caught a fish that big! I have to see it to believe it."] = "\xC9 s\xE9rio que ele pegou um peixe t\xE3o grande? S\xF3 acredito vendo.",
	["I seem to have misplaced my dignity. If you find it anywhere, please let me know."] = "Acho que perdi minha dignidade. Me avise se encontr\xE1-la.",
	["I found a home in Truce after losing my fingers in battle. Only able bodied can fight in the war down South."] = "Depois que perdi meus dedos durante uma batalha, Truce virou minha casa. S\xF3 pode lutar na guerra do sul quem estiver apto.",
	["Most of the families here have ties to the military as well as the outlaws. Be careful who you cross."] = "A maioria das fam\xEDlias aqui tem la\xE7os com o ex\xE9rcito e com os bandidos. Cuidado com o que voc\xEA fala.",
	["I only joined so I could earn enough pay to open my own shop one day."] = "S\xF3 fa\xE7o parte porque quero juntar dinheiro para abrir minha pr\xF3pria loja um dia.",
	["They call the people of Defiance tough, but they don't know what tough means until they come to Truce!"] = "Dizem que em Defiance o pessoal \xE9 corajoso, mas \xE9 aqui em Truce que o bicho pega!",
	["Fishing is one of the main sources of food in Ravendawn. There are so many kinds of fish in the ocean..."] = "A variedade de peixes no oceano \xE9 enorme! N\xE3o \xE9 \xE0 toa que a pesca \xE9 uma das principais fontes de alimento em Ravendawn.",
	["The seas hold every type of treasure... wealth, power, knowledge. All up for grabs out there on the vast ocean!"] = "Os mares guardam todos os tipos de tesouros: riqueza, poder e conhecimento.",
	["Better watch your pockets. Plenty of rats around to nick what's yours."] = "\xC9 melhor ficar de olho nas suas coisas. Os ratos n\xE3o perdem a oportunidade de furtar algo.",
	["Going on a long journey? Don't forget to take a variety of dried food. Diversity is important so you don't get seasick!"] = "Vai fazer uma viagem? N\xE3o se esque\xE7a de levar alimentos diferentes. \xC9 importante variar para n\xE3o enjoar!",
	["If you see the League of Splinters, best to throw your valuables in the water and sail away as fast as you can."] = "Se voc\xEA encontrar a Liga dos Estilha\xE7os, \xE9 melhor jogar seus objetos de valor na \xE1gua e zarpar o mais r\xE1pido que puder.",
	["The orca is said to protect us from harm and bring prosperity to the town. "] = "Dizem que a orca nos protege e traz prosperidade \xE0 cidade.",
	["Hey, watch your back, there's danger around every corner here."] = "Ei, cuidado por onde anda. H\xE1 perigo por toda a parte aqui.",
	["Hic! Another round! Tomorrow we might find ourselves knee-deep in mud!"] = "Desce mais uma! Amanh\xE3 vamos estar cambaleando por a\xED.",
	["We're still picking up the pieces of what this place once was."] = "Ainda estamos juntando os cacos... Aqui j\xE1 foi um lugar diferente.",
	["The Rohna Brotherhood would never dare attack the city. They just hide out in the forest like the cowards they are!"] = "A Brotherhood nunca ousaria atacar a cidade. Eles apenas se escondem nos bosques como os covardes que s\xE3o!",
	["The sudden thunderstorms and heatwaves make the weather here unpredictable."] = "Com as tempestades de trov\xE3o repentinas e as ondas de calor, o clima aqui fica imprevis\xEDvel.",
	["I joined the Ravenguard because of the weapons. Our swords are crafted from only the best ore and blacksmiths!"] = "S\xF3 me juntei \xE0 Ravenguard por causa das armas. Nossas espadas s\xE3o feitas pelos melhores ferreiros e com o melhor min\xE9rio!",
	["I'm trying to drown these nightmares with ale... It's working! Hic!"] = "S\xF3 a cerveja para me fazer esquecer os pesadelos...",
	["I've seen strange lights rising from the swamp at night... scary."] = "J\xE1 vi luzes estranhas subindo do p\xE2ntano \xE0 noite... \xE9 assustador.",
	["When outside the city's walls, doubt even your own shadow. Especially if you're a trader! Thieves are everywhere!"] = "Fora da cidade, duvide at\xE9 de sua pr\xF3pria sombra, principalmente se voc\xEA for comerciante! Tem ladr\xE3o em todo lugar. ",
	["I bet you to spend a night in the middle of the bog!"] = "Aposto que voc\xEA n\xE3o dura uma noite no meio do brejo!",
	["The best thing about Darzuac are the smells and drinks you can find! Ha ha!"] = "A melhor coisa sobre Darzuac s\xE3o os cheiros e bebidas que voc\xEA pode encontrar! Ha ha!",
	["Mother Wylla knows more than what she tells us! There's very dark magics going on here."] = "Mother Wylla guarda alguns segredos! H\xE1 magias muito sombrias acontecendo aqui.",
	["Wanna play a game?"] = "Quer jogar um jogo?",
	["Phew... Just a few more haystacks and I'll be done."] = "Ufa... S\xF3 mais alguns montes de feno e termino.",
	["I always carry this orca charm with me as a symbol of strength and courage."] = "Sempre carrego este amuleto de orca comigo como s\xEDmbolo de for\xE7a e coragem.",
	["Almost got kidnapped by bandits on my way here... I need to hire a bodyguard. Or two."] = "Quase fui sequestrado por bandidos a caminho daqui... Preciso contratar um guarda-costas. Ou dois.",
	["Did I remember to feed the chickens today?"] = "Ser\xE1 que me lembrei de alimentar as galinhas hoje?",
	["Once a bustling town, now here is just a shadow of its glorious past."] = "Aqui j\xE1 foi uma cidade movimentada, agora \xE9 apenas uma sombra de seu glorioso passado.",
	["I'm glad Darzuac isn't exposed like other towns in the region. No orcs are getting over our walls!"] = "Ainda bem que Darzuac n\xE3o est\xE1 exposta como outras cidades na regi\xE3o. Nenhum orc passa por nossas muralhas!"
}
